                           @HighDate smalldatetime,   
                           @Subject     varchar(100) 
               
  Returns:		      n/a
   ==================================================================
   Created:         09/24/2009
   By:    	            GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE PROCEDURE [dbo].[admin_GetSavedEmails] 
                                                   @LowDate  smalldatetime, 
                                                   @HighDate smalldatetime,   
                                                   @Subject     varchar(100) 
AS

SET NOCOUNT ON

DECLARE	@SPName   varchar(255)                     

BEGIN TRY
   SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID));

   IF (@Subject IS NULL OR LEN(RTRIM(@Subject)) = 0)  
      SELECT   [ID], 
                     [EntryDate],  
                     [Recipient], 
                     [CC], 
                     [Sender], 
                     [Subject], 
                     [Body], 
                     [Importance] 
         FROM [Saved_Emails] 
         WHERE [EntryDate] BETWEEN @LowDate AND @HighDate                
   ELSE 
      SELECT   [ID], 
                     [EntryDate],  
                     [Recipient], 
                     [CC], 
                     [Sender], 
                     [Subject], 
                     [Body], 
                     [Importance] 
         FROM [Saved_Emails] 
         WHERE [EntryDate] BETWEEN @LowDate AND @HighDate      
               AND [Subject] LIKE (RTRIM(@Subject) + '%')
		                              
END TRY
BEGIN CATCH 
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

   EXEC [spLog]  @ErrorMessage, @SPName	  
   RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH




