				   occurs in both the from member id and the to member id.

  Resources: 	User_Payee_Change_History
               User_Payee
               User_Type
               Payee
               wc_NickNames_Change_History
  ------------------------------------------------------------------
  Accepts:		@ID varchar(32) 

  Returns:		First Recordset:
                  DISTINCT ch.ModifyDate as LastUpdatedOn, 
                           ch.ModifiedBy as LastUpdatedBy, 
                           SUBSTRING(ch.Reason, 1, (CHARINDEX(';', ch.Reason)) - 1) as Reason, 
                           ut.Description as UserDesc, 
                           CASE ch.ModifyType 
                              WHEN 'U' THEN 'Updated'
                              WHEN 'D' THEN 'Deleted'
                              WHEN 'C' THEN 'Copied'
                              ELSE ''
                           END as ModifyType, 
                           ch.MemberID  
               
               Second Recordset:
                  ch.ModifyDate as LastUpdatedOn, 
                  ch.ModifiedBy as LastUpdatedBy, 
                  SUBSTRING(ch.Reason, 1, (CHARINDEX(';', ch.Reason)) - 1) as Reason, 
                  ut.Description as UserDesc, 
                  CASE ch.ModifyType 
                     WHEN 'U' THEN 'Updated'
                     WHEN 'D' THEN 'Deleted'
                     WHEN 'C' THEN 'Copied'
                     ELSE ''
                  END as ModifyType, 
                  ch.CopiedFromMemberID as FromPayeeListID, 
                  ch.MemberID as ToPayeeListID, 
                  ch.PayeeID, 
                  p.Name as PayeeName, 
                  n.NickName as NickName, 
                  ch.UsersAccountAtPayee, 
                  ch.NameOnAccount, 
                  ch.PaymentMethod, 
                  ch.Active   
   ==================================================================
   Created:  	07/15/2008
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE    PROCEDURE [dbo].[admin_GetUserPayeeCopyHistory]
					            @ID varchar(32) 
AS

SET NOCOUNT ON

SELECT DISTINCT   ch.ModifyDate as LastUpdatedOn, 
                  ch.ModifiedBy as LastUpdatedBy, 
                  SUBSTRING(ch.Reason, 1, (CHARINDEX(';', ch.Reason)) - 1) as Reason, 
                  ut.Description as UserDesc, 
                  CASE ch.ModifyType 
                     WHEN 'U' THEN 'Updated'
                     WHEN 'D' THEN 'Deleted'
                     WHEN 'C' THEN 'Copied'
                     ELSE ''
                  END as ModifyType, 
                  ch.MemberID  
   FROM User_Payee_Change_History ch WITH (NOLOCK) 
      INNER JOIN User_Payee u WITH (NOLOCK) ON ch.PayeeListID = u.PayeeListID 
      INNER JOIN User_Type ut WITH (NOLOCK) ON ch.User_Type = ut.UserType 
      LEFT JOIN Payee p WITH (NOLOCK) ON ch.PayeeID = p.PayeeID 
      LEFT JOIN wc_NickNames_Change_History n WITH (NOLOCK) ON ch.PayeeListID = n.PayeeListID AND ch.MemberID = n.MemberID 
   WHERE u.Active = 'Y' 
      AND ch.ModifyType = 'C'
      AND (ch.MemberID = @ID OR ch.CopiedFromMemberID = @ID) 
ORDER BY ch.ModifyDate DESC
         
SELECT   ch.ModifyDate as LastUpdatedOn, 
         ch.ModifiedBy as LastUpdatedBy, 
         SUBSTRING(ch.Reason, 1, (CHARINDEX(';', ch.Reason)) - 1) as Reason, 
         ut.Description as UserDesc, 
         CASE ch.ModifyType 
            WHEN 'U' THEN 'Updated'
            WHEN 'D' THEN 'Deleted'
            WHEN 'C' THEN 'Copied'
            ELSE ''
         END as ModifyType, 
         ch.MemberID, 
         ch.CopiedFromPayeeListID as FromPayeeListID, 
         ch.PayeeListID as ToPayeeListID, 
         ch.PayeeID, 
         p.Name as PayeeName, 
         n.NickName as NickName, 
         ch.UsersAccountAtPayee, 
         ch.NameOnAccount, 
         ch.PaymentMethod, 
         ch.Active   
   FROM User_Payee_Change_History ch WITH (NOLOCK) 
      INNER JOIN User_Payee u WITH (NOLOCK) ON ch.PayeeListID = u.PayeeListID 
      INNER JOIN User_Type ut WITH (NOLOCK) ON ch.User_Type = ut.UserType 
      LEFT JOIN Payee p WITH (NOLOCK) ON ch.PayeeID = p.PayeeID 
      LEFT JOIN wc_NickNames_Change_History n WITH (NOLOCK) ON ch.PayeeListID = n.PayeeListID AND ch.MemberID = n.MemberID 
   WHERE u.Active = 'Y' 
      AND ch.ModifyType = 'C'
      AND (ch.MemberID = @ID OR ch.CopiedFromMemberID = @ID) 
ORDER BY ch.ModifyDate DESC

SET NOCOUNT OFF 

