/*
 ==================================================================
  SP Name:     admin_ImportScrubFiles

  Description: Used by the import scrub file process to import 
               user_payee data that was validated by a payee and 
               update the billpay database user_payee table with 
               the new validated account numbers.
                              
  Resources: 	Configuration
               sys.objects
               udfGetDirectoryFiles
               udfGetXmlFromFile
               User_Payee
               User_Payee_Change_History
               Change_History
               udfPathExists
               udfCreateDirectory
               udfMoveFile
               spLog
               msdb.dbo.sp_send_dbmail
  ------------------------------------------------------------------
  Accepts:		n/a

  Returns:		@FileCnt int   - The number of files processed.
  ==================================================================
   Created:  	GNordstrom
   By:    		04/30/2007
  ------------------------------------------------------------------
  Modifications: 
  11/05/2007   GNordstrom  Modified call to udfGetXmlFromFile due to changes 
                                                   in its return parameter definition.
   05/15/2009  GNordstrom  Modified to get correct updated count for email.
=========================================================================
*/
CREATE PROCEDURE [dbo].[admin_ImportScrubFiles] 
AS

DECLARE  @SPName  varchar(255), 
         @Source  varchar(256), 
         @root    varchar(256),  
         @File    varchar(500),  
         @idoc    int,
         @XML     xml, 
         @Total   int, 
         @Err     varchar(512), 
         @From    varchar(500), 
         @To      varchar(500), 
         @Results varchar(max), 
         @FileCnt int, 
         @FuncRes int, 
         @UpdatedCount int, 
         @ModifyDate smalldatetime       
      
SET NOCOUNT ON 
         
SET @Results = '<TABLE border=2><TR><TD>File Name</TD><TD>Total Records</TD><TD>Updated Records</TD></TR>'
SET @FileCnt = 0
SET @UpdatedCount = 0 
SET @ModifyDate = GETDATE()

--Set the stored procedure name. 
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
--Get the path where the files are located.
SELECT @Source = ConfigValue FROM Configuration WHERE UPPER(ConfigName) = UPPER('AccountValidationFiles')
--Get the root name, which is the credit unions name without spaces, for the xml files.
SELECT @root = ('/' + REPLACE(ConfigValue, ' ', '') + '/row') FROM Configuration WHERE UPPER(ConfigName) = UPPER('CreditUnionName')

--Create a cursor of the files waiting to be processed.
DECLARE cuFiles CURSOR 
   FOR SELECT Files
         FROM dbo.udfGetDirectoryFiles(@Source, '*.xml')
         
OPEN cuFiles 
FETCH NEXT FROM cuFiles INTO @File 

--Loop thru the records in the cursor
WHILE @@FETCH_STATUS = 0 
BEGIN
   BEGIN TRY
      --Get the xml from the file 
      SET @XML = CONVERT(xml, dbo.udfGetXmlFromFile(@Source +  '\' + @File))
      
      EXEC sp_xml_preparedocument @idoc OUTPUT, @XML
      
      --Get a count of records in the xml 
      SELECT @Total = COUNT(ListID) 
         FROM OPENXML (@idoc, @root, 2)  
                  WITH (ListID   varchar(32), 
                        OldAcct  varchar(32),
                        NewAcct  varchar(32))       
     
      --If the database is using the new change history process;
      --update the user_payee table with the records that match on listid and oldacct 
      --and insert a record into the user_payee_change_history table for every updated record.
      IF EXISTS (SELECT * FROM sys.objects WHERE object_id = OBJECT_ID(N'[dbo].admin_GetPayeeChgHistory') AND type in (N'P'))
      BEGIN 
         UPDATE User_Payee 
            SET   UsersAccountAtPayee = x.NewAcct, 
	               LastUpdatedOn = @ModifyDate   
         OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, 
                  DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
                  INSERTED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
                  2, 'Payee Validation Import', 'U', @ModifyDate, 'WRGMaintenance'  
            INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
                  LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, 
                  Active, MinDaystoPay, PaymentMethod, 
                  User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
         FROM User_Payee u 
                  INNER JOIN (SELECT  ListID, OldAcct, NewAcct           
                     FROM OPENXML (@idoc, @root, 2)  
                        WITH (ListID   varchar(32), 
                              OldAcct  varchar(32),
                              NewAcct  varchar(32))) x ON u.PayeeListID = x.ListID          
         WHERE u.PayeeListId = x.ListID 
	         AND u.UsersAccountAtPayee = x.OldAcct  
	         
      	   SELECT @UpdatedCount = COUNT(PayeeListID) FROM User_Payee_Change_History WHERE Reason = 'Payee Validation Import' AND ModifiedBy = 'WRGMaintenance'  AND ModifyDate = @ModifyDate 
	   END 
      ELSE
      BEGIN 
      --If the database is using the old change history process;
      --update the user_payee table with the records that match on listid and oldacct 
      --and insert a record into the change_history table for every updated record.      
         UPDATE User_Payee 
            SET   UsersAccountAtPayee = x.NewAcct, 
	               LastUpdatedOn = @ModifyDate 
         OUTPUT '', 2, '', INSERTED.PayeeListID, 
               '',  @ModifyDate, 'WRGMaintenance' , 'Payee Validation Import' 
            INTO Change_History (PaymentID, UserType, PayeeID, PayeeListID, 
                  RecPaymentID, LastUpdatedOn, LastUpdatedBy, Reason) 
         FROM User_Payee u 
                  INNER JOIN (SELECT  ListID, OldAcct, NewAcct           
                     FROM OPENXML (@idoc, @root, 2)  
                        WITH (ListID   varchar(32), 
                              OldAcct  varchar(32),
                              NewAcct  varchar(32))) x ON u.PayeeListID = x.ListID          
         WHERE u.PayeeListId = x.ListID 
	         AND u.UsersAccountAtPayee = x.OldAcct  
	         
      	   SELECT @UpdatedCount = COUNT(PayeeListID) FROM Change_History WHERE Reason = 'Payee Validation Import' AND LastUpdatedBy = 'WRGMaintenance'  AND LastUpdatedOn = @ModifyDate
	   END
	         
      
      --Insert the counts for the xml into the results variable.
      SET @Results = @Results + '<TR><TD>' + @File + '</TD><TD align=right>' + CONVERT(varchar, @Total) + '</TD><TD align=right>' + CONVERT(varchar, @UpdatedCount) + '</TD></TR>'
      SET @FileCnt = @FileCnt + 1 

      EXEC sp_xml_removedocument @idoc
   
      SET @To = @Source + '\ArchiveScrubFiles\' 
      --If the archive directory does not exists; create it
      IF (SELECT dbo.udfPathExists(@To)) = 0
         SELECT @FuncRes = dbo.udfCreateDirectory(@To)

      --Archive the file that was processed.
      SET @From = @Source + '\' + @File 
      SET @To = @Source + '\ArchiveScrubFiles\' + @File 
      SELECT @FuncRes = dbo.udfMoveFile(@From, @To)
   
      --Get the next file to be processed.
      FETCH NEXT FROM cuFiles INTO @File  
   END TRY
   BEGIN CATCH
      --If an error occurs, log it.
      SET @Err = 'Import Process Err for ' + @File + '; Err Number=' + CONVERT(varchar, ERROR_NUMBER())
      EXEC spLog @Err, @SPName
      
      --Get the next file to be processed.
      FETCH NEXT FROM cuFiles INTO @File  
   END CATCH
   
END
  
CLOSE cuFiles
DEALLOCATE cuFiles

IF @FileCnt > 0 
BEGIN
   --Email the results of the import process.
   DECLARE  @EMailProfile  nvarchar(256), 
            @EmailTo       nvarchar(256), 
            @SQL           nvarchar(max), 
            @EmailSubject  nvarchar(255)
            
   SELECT @EMailProfile = ConfigValue
      FROM Configuration WITH (NOLOCK) 
      WHERE UPPER(ConfigName) = UPPER('EmailProfile')
      
   SELECT @EmailTo = ConfigValue 
      FROM Configuration WITH (NOLOCK) 
      WHERE UPPER(ConfigName) = UPPER('EmailReportsAddress')
      
   SET @Results = @Results + '</TABLE>'
                  
   SET @EmailSubject = N'Import Payee Accounts Results- ' + CONVERT(varchar, GETDATE(), 101) + ' ' + CONVERT(varchar, GETDATE(), 108)

   EXEC msdb.dbo.sp_send_dbmail
                @profile_name = @EmailProfile,
                @recipients = @EmailTo,
                @subject = @EmailSubject,
                @body = @Results, 
                @body_format = 'HTML'
END

SET NOCOUNT OFF 

SELECT @FileCnt AS FileCount 



GO
