--Check to see if this payee exists already
SELECT @Id = PayeeID
FROM Payee
WHERE dbo.PackStrLG(Name + Address1 + Address2 + City + State + PostalCode + Phone) = @PackedPayee

print @packedpayee
print 'id = ' + @ID
--If the payee does not exist, then add it to the payee table
IF @ID IS NULL 
   BEGIN 
      EXEC @rc = GetID @id_type='PayeeID', @id=@ID OUTPUT	--KKS 17Sep1999 - ID Dispenser replacement

      if @rc < 0 GOTO on_error
	      insert into Payee (	PayeeId,
						            PayeeAliasId,
						            ProviderId,
						            Name,
						            Address1,
						            Address2,
						            Address3,
						            City,
						            State,
                              PostalCode,
						            Country,
						            Phone,
						            Scope,
						            BankId,
						            BranchId,
						            AccountId,
						            AccountType,
						            AccountKey,
				                  LastUpdatedOn,
						            LastUpdatedBy) 
				         values ( @Id,
						            "",
