						            @Name,
						            @Address1,
						            @Address2,
						            @Address3,
						            @City,
						            @State,
						            @PostalCode,
						            @Country,
						            @Phone,
						            @Scope,
						            "",
						            "",
						            "",
						            "",
						            "",
						            getdate(),
						            @LastUpdatedBy)
   END


if (@@ERROR <> 0) GOTO on_error
	select 'Status'=1, 'PayeeID'=@id
	return (1)

on_error:
	select "Status"=-1
	return (-1)
	




