DECLARE  @SPName   varchar(255)

BEGIN TRY
   SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID)); 
   
	 --Get values from Configuration table for ONUSGLCode and ONUSComment
	SELECT @GLCode = [ConfigValue] FROM [Configuration] WHERE UPPER([ConfigName]) = UPPER('ONUSGLCode') 
	SELECT @Comment = [ConfigValue] FROM [Configuration] WHERE UPPER([ConfigName]) = UPPER('ONUSComment')
	SELECT @MortgageEnabled = ISNULL(ConfigValue, 'N') FROM Configuration WHERE ConfigName = 'OnUsMortgageEnabled'
	SELECT @MortgageIDLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'OnUsMortgageIDLength'


	SET @tmpUsersAccount = dbo.PackStr( @UsersAccount)
	IF @tmpUsersAccount like '%S%'
		SET @Indx = CHARINDEX('S', @tmpUsersAccount)
	ELSE
		SET @Indx = CHARINDEX('L', @tmpUsersAccount)
	
	SET @UsersAccountMemberId = SUBSTRING(@UsersAccount, 1, @Indx - 1 )
	SET @UsersAccountLoanID = SUBSTRING(@UsersAccount, @Indx, (LEN(@UsersAccount) - @Indx + 1) )
	 --do a gl transfer
	 EXEC dbo.BPG_GLAccountTransfer @UsersAccountMemberId, @UsersAccountLoanID, @GLCode, @Amount, @Comment, @errNumber OUTPUT, @errMessage OUTPUT 

	 IF (@errNumber = 0)
	 BEGIN
			UPDATE [Payment_History] 
				 SET		[UsersAccountAtPayee] = @UsersAccount, 
									[StatusCode] = 102,
									[FailedDate] = NULL, 
									[ProcessedDate] = GETDATE()
			WHERE  [PaymentID] = @PaymentID 
			
			UPDATE [User_Payee] 
				 SET [UsersAccountAtPayee] = @UsersAccount, 
							[LastUpdatedOn] = GETDATE() 
						OUTPUT DELETED.[PayeeListID], DELETED.[MemberID], DELETED.[PayeeID], DELETED.[CreatedOn], 
										 DELETED.[LastUpdatedOn], DELETED.[UsersAccountAtPayee], DELETED.[NameOnAccount], 
										 DELETED.[Active], DELETED.[MinDaystoPay], DELETED.[PaymentMethod], 
										 1, @Comment, 'U', GETDATE(), @UpdatedBy  
							 INTO [User_Payee_Change_History] ([PayeeListID], [MemberID], [PayeeID], [CreatedOn], 
										 [LastUpdatedOn], [UsersAccountAtPayee], [NameOnAccount], [Active], [MinDaystoPay], 
										 [PaymentMethod], [User_Type], [Reason], [ModifyType], [ModifyDate], [ModifiedBy]) 
				 WHERE [MemberID] = @MemberID 
							 AND [PayeeListID] = @PayeeListID 
			SELECT "1", @errMessage, @errNumber		
			
			EXEC @rc = BPG_InsertOnUsTracking @PaymentID, @UpdatedBy, @errNumber, @ErrMessage, 'HB Admin Update posting'			
			IF @rc != 0
			BEGIN
				SET @Message = 'Error Adding OnUs Edit to Tracking Table, Paymentid = ' + @PaymentID	
				EXEC spLog @SPName, @Message
			END
						
	 END
	 ELSE IF (@errNumber = 13030) and @MortgageEnabled = 'Y'
	 BEGIN
		SET @NewLoanID = @UsersAccountLoanID + substring(@UsersAccountLoanID, len(@UsersAccountLoanID)-@MortgageIDLength+1, @MortgageIDLength)
		EXEC @rc = BPG_GLAccountTransferMortgage @UsersAccountMemberId, @NewLoanID, @GLCode, @Amount, @Comment, @errnumber OUTPUT, @errMessage OUTPUT
		IF (@errNumber = 0)
		BEGIN
			UPDATE [Payment_History] 
				 SET		[UsersAccountAtPayee] = @UsersAccount, 
									[StatusCode] = 102,
									[FailedDate] = NULL, 
									[ProcessedDate] = GETDATE()
			WHERE  [PaymentID] = @PaymentID 
			
			UPDATE [User_Payee] 
				 SET [UsersAccountAtPayee] = @UsersAccount, 
							[LastUpdatedOn] = GETDATE() 
						OUTPUT DELETED.[PayeeListID], DELETED.[MemberID], DELETED.[PayeeID], DELETED.[CreatedOn], 
										 DELETED.[LastUpdatedOn], DELETED.[UsersAccountAtPayee], DELETED.[NameOnAccount], 
										 DELETED.[Active], DELETED.[MinDaystoPay], DELETED.[PaymentMethod], 
										 1, @Comment, 'U', GETDATE(), @UpdatedBy  
							 INTO [User_Payee_Change_History] ([PayeeListID], [MemberID], [PayeeID], [CreatedOn], 
										 [LastUpdatedOn], [UsersAccountAtPayee], [NameOnAccount], [Active], [MinDaystoPay], 
										 [PaymentMethod], [User_Type], [Reason], [ModifyType], [ModifyDate], [ModifiedBy]) 
				 WHERE [MemberID] = @MemberID 
							 AND [PayeeListID] = @PayeeListID 
			SELECT "1", @errMessage, @errNumber		
			
			EXEC @rc = BPG_InsertOnUsTracking @PaymentID, @UpdatedBy, @errNumber, @ErrMessage, 'HB Admin Update posting'				
			IF @rc != 0
			BEGIN
				SET @Message = 'Error Adding OnUs Edit to Tracking Table, Paymentid = ' + @PaymentID	
				EXEC spLog @SPName, @Message
			END
		END
	 END
	 ELSE
		SELECT @errMessage, @errNumber 

END TRY
BEGIN CATCH
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

    RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH    

