/*
 ==================================================================
  Stored Procedure Name: dbo.admin_RptActivity

  Description:	 	

  Resources: 	payment_history
				   Payee
				   Payment_Method
  ------------------------------------------------------------------
  Accepts:		@dtLo		datetime = '01/01/1900 00:00:00',
				   @dtHi		datetime = '01/01/1900 00:00:00',
				   @strMemberID	varchar(32) = '',
				   @strPayee	varchar(64) = ''

  Returns:		MemberID      = RTRIM(ph.MemberID), 
               PaymentID     = RTRIM(ph.PaymentID), 
               PayeeName     = RTRIM(p.Name),
               PayeeID       = RTRIM(ph.PayeeID),
               ProcessedDate = ph.ProcessedDate,
               DueDate, 
			   Memo			= ph.Memo,
               StatusCode    = ph.StatusCode,
               PaymentMethod = RTRIM(pm.Description),
               Amount        = ph.Amount
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	01/03/2006	GNordstrom	Copied ListPmtHistAbridged to this new proc name.
	01/04/2006	GNordstrom	Modified to return only the data needed for the report 
							      and to return summary rows and to return data 
							      formatted as needed in the report.
	05/17/2006	GNordstrom	Removed table aliases used on column aliases in 
					            Order By clause and changed double quotes to single
					            quotes.			
	01/09/2007  GNordstrom	Added returning DueDate column.
	04/18/2019  GZhao		Added returning Memo column.
==================================================================
*/
CREATE    PROCEDURE [dbo].[admin_RptActivity]
					@dtLo		datetime = '01/01/1900 00:00:00',
					@dtHi		datetime = '01/01/1900 00:00:00',
					@strMemberID	varchar(32) = '',
					@strPayee	varchar(64) = ''
AS

/* if @dtLo or @dtHi is blank, act as if default */
IF @dtLo = '' SELECT @dtLo = '01/01/1900 00:00:00'
IF @dtHi = '' SELECT @dtHi = '01/01/1900 00:00:00'

CREATE TABLE #p1 (PaymentId char(10))

/* Validate data: at least a date range OR strMemberID must be set */
IF @dtLo = '01/01/1900 00:00:00' AND @dtHi = '01/01/1900 00:00:00' 
	BEGIN
		/* Date not range set */
		/* If date range not set strMemberID must be */
		IF @strMemberID = '' 
			GOTO on_error
		
		IF @strPayee = '' 
			/* strPayee is not part of criteria */
			INSERT INTO #p1 
				SELECT PaymentID 
				FROM Payment_History WITH (NOLOCK) 
				WHERE MemberID = @strMemberID 
		ELSE
			/* strPayee is part of criteria */
			INSERT INTO #p1 
				SELECT ph.PaymentID 
				FROM Payment_History ph WITH (NOLOCK) 
					INNER JOIN Payee p   WITH (NOLOCK) ON ph.PayeeID = p.PayeeID 
				WHERE ph.MemberID = @strMemberID 
					AND p.Name LIKE (RTRIM(@strPayee) + '%')	
	END
ELSE 
	BEGIN
		/* Date range is set */
		/* Invalid if only dtLo OR dtHi set */
		IF @dtLo = '01/01/1900 00:00:00' OR @dtHi = '01/01/1900 00:00:00' 
			GOTO on_error
	
		IF @strMemberID = '' 
			/* strMemberID and strPayee are not part of criteria */
			INSERT INTO #p1 
				SELECT PaymentID 
				FROM Payment_History WITH (NOLOCK) 
				WHERE ProcessedDate BETWEEN @dtLo AND @dtHi
		ELSE 
			BEGIN 
				IF @strPayee = '' 
					/* strPayee is not part of criteria */
					INSERT INTO #p1 
						SELECT PaymentID 
						FROM Payment_History WITH (NOLOCK) 
						WHERE (ProcessedDate BETWEEN @dtLo AND @dtHi)
							AND (MemberID = @strMemberID)
				ELSE 
					/* strPayeeID is part of criteria */
					INSERT INTO #p1 
						SELECT ph.PaymentID 
						FROM Payment_History ph WITH (NOLOCK) 
							INNER JOIN Payee p   WITH (NOLOCK) ON ph.PayeeID = p.PayeeID 
						WHERE (ProcessedDate BETWEEN @dtLo AND @dtHi)
							AND (MemberID = @strMemberID)
							AND (p.Name LIKE (RTRIM(@strPayee) + '%'))	
			END
	END				
		


SELECT 	Case Grouping(a.MemberID) WHEN 0 THEN MemberID 
			ELSE 'Total' END AS MemberID,
		a.PaymentID, 
		a.PayeeName, 
		a.PayeeID, 
		a.ProcessedDate,
		a.DueDate, 
		a.Memo,
		a.PaymentMethod, 
		a.StatusCode, 
		Sum(a.Amount) AS Amount,
		Count(a.PaymentID) AS PymtCount,  
		Case Grouping(a.StatusClass) WHEN 0 THEN StatusClass
			ELSE 'Total' END AS StatusClass  
FROM (
		SELECT 	RTRIM(ph.MemberID) AS MemberID, 
				CASE ph.StatusCode
							WHEN 101 THEN 'Paid'
							WHEN 102 THEN 'Paid'
							WHEN 103 THEN 'Cancel'
							WHEN 200 THEN 'NSF'
							ELSE 'Failed' 
						END AS StatusCode, 
				CASE ph.StatusCode 
							WHEN 101 THEN 'Paid'
							WHEN 102 THEN 'Paid'
							ELSE 'Failed'
						END AS StatusClass, 
			 	ph.ProcessedDate AS ProcessedDate, 
			 	ph.DueDate, 
				ph.Memo,
				RTRIM(ph.PaymentID) AS PaymentID, 
				RTRIM(p.Name) AS PayeeName, 
				RTRIM(ph.PayeeID) AS PayeeID,
				RTRIM(pm.Description) AS PaymentMethod,
				(CONVERT(money, amount)/100) AS Amount 
		FROM #p1 tmp 
			INNER JOIN Payment_History ph WITH (NOLOCK) ON tmp.PaymentID = ph.PaymentID 
			INNER JOIN Payee p  WITH (NOLOCK) ON p.PayeeID = ph.PayeeID 
		    INNER JOIN Payment_Method pm  WITH (NOLOCK) ON ph.PaymentMethod = pm.PaymentMethod 
		WHERE ph.StatusCode <> 103) a 
GROUP BY 	a.StatusClass, 
			a.MemberID, 
			a.PaymentID, 
			a.PayeeID, 
			a.PayeeName, 
			a.ProcessedDate, 
			a.DueDate, 
			a.Memo,
			a.PaymentMethod, 
			a.StatusCode WITH ROLLUP
HAVING (a.StatusClass IS NOT NULL AND a.MemberID IS NULL AND a.PaymentID IS NULL AND a.PayeeName IS NULL AND a.PayeeID IS NULL AND a.ProcessedDate IS NULL AND a.PaymentMethod IS NULL AND a.StatusCode IS NULL)
	OR (a.StatusClass IS NOT NULL AND a.MemberID IS NOT NULL AND a.PaymentID IS NOT NULL AND a.PayeeName IS NOT NULL AND a.PayeeID IS NOT NULL AND a.ProcessedDate IS NOT NULL AND a.PaymentMethod IS NOT NULL AND a.StatusCode IS NOT NULL)
	OR (a.StatusClass IS NOT NULL AND a.MemberID IS NOT NULL AND a.PaymentID IS NULL AND a.PayeeName IS NULL AND a.PayeeID IS NULL AND a.ProcessedDate IS NULL AND a.PaymentMethod IS NULL AND a.StatusCode IS NULL)
ORDER BY 	MemberID, 
		a.ProcessedDate DESC, 
		a.PaymentID, 
		StatusClass DESC


IF (@@error <> 0) GOTO on_error
	SELECT Status = '0'
	RETURN (0)
		
on_error:
	SELECT Status = '-1'
	RETURN (-1)

GO
