/*
 ==================================================================
  SP Name:     admin_RptErrorRecap

  Description: Obtains the error records from the ErrorRecap table 
               for the parameters passed in.

  Resources: 	ErrorRecap, 
               Payment_History, 
               Payee 
  ------------------------------------------------------------------
  Accepts:		@SearchType varchar(15), 
               @SearchValue varchar(50) 

  Returns:     e.RunID, 
               e.EntryDate, 
               e.MemberID, 
               e.PaymentID, 
               CONVERT(money, ph.Amount)/100 as Amount, 
               e.PayeeListID, 
               p.Name as PayeeName, 
               ph.UsersAccountAtPayee, 
               ph.NameOnAccount, 
               e.StatusCode, 
               s.HostCode, 
               e.Message 
   ==================================================================
   Created:  	03/22/2007
   By:    	 	GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE PROCEDURE [dbo].[admin_RptErrorRecap] 
                  @SearchType varchar(15), 
                  @SearchValue varchar(50) 
                  
AS

SET NOCOUNT ON 

DECLARE @Table table (ID int) 

IF @SearchType = 'PaymentID'
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE PaymentID = @SearchValue 
ELSE IF @SearchType = 'MemberID' 
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE MemberID = @SearchValue
ELSE IF @SearchType = 'PayeeListID'
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE PayeeListID = @SearchValue
ELSE IF @SearchType = 'StatusCode'
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE StatusCode = @SearchValue
ELSE IF @SearchType = 'RunID'
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE RunID = @SearchValue
ELSE IF @SearchType = 'Date'
   INSERT INTO @Table 
      SELECT ID FROM ErrorRecap WITH (NOLOCK) WHERE CONVERT(varchar, EntryDate, 112) =  CONVERT(varchar, CAST(@SearchValue as smalldatetime), 112)

SELECT   e.RunID, 
         e.EntryDate, 
         e.MemberID, 
         e.PaymentID, 
         CONVERT(money, ph.Amount)/100 as Amount, 
         e.PayeeListID, 
         p.Name as PayeeName, 
         ph.UsersAccountAtPayee, 
         ph.NameOnAccount, 
         e.StatusCode, 
         s.HostCode, 
         e.Message as Error 
   FROM ErrorRecap e WITH (NOLOCK) INNER JOIN @Table t ON e.ID = t.ID 
            LEFT JOIN Payment_History ph WITH (NOLOCK) ON e.PaymentID = ph.PaymentID 
            LEFT JOIN Payee p WITH (NOLOCK) ON ph.PayeeID = p.PayeeID 
            LEFT JOIN Status_Code s WITH (NOLOCK) ON e.StatusCode = s.StatusCode
   ORDER BY e.EntryDate 

SET NOCOUNT OFF

GO
