/*
 ==================================================================
  Stored Procedure Name: dbo.admin_Search

  Description: 	Obtains the records for the id and type passed in.

  Resources: 	Courtesy_Pay
				ErrorRecap
				Payee
				Payment
				Payment_Clear
				Payment_History
				Recurring_Payment
				User_Payee
				wc_NickNames
				wc_Payee_Combined
  ------------------------------------------------------------------
  Accepts:		@searchType varchar(10), 
				@id varchar(50), 
				@days int  
  
	Returns:	
   ==================================================================
   Created:  	12/11/2012
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE   PROCEDURE [dbo].[admin_Search]
							@searchType varchar(10), 
							@id varchar(50), 
							@days int 
AS

DECLARE @tblIDs TABLE (IDMember varchar(32), IDPayment varchar(10), IDRecPayment varchar(10), IDPayeeList varchar(12), IDPayee varchar(12));  

IF (@searchType = 'member')
BEGIN
	INSERT INTO @tblIDs (IDMember, IDPayment, IDRecPayment, IDPayeeList, IDPayee) 
		SELECT h.MemberID, h.PaymentID, h.RecPaymentID, h.PayeeListID, h.PayeeID   
		FROM Payment_History h 
		WHERE h.MemberID = @id  
		UNION 
		SELECT p.MemberID, p.PaymentID, p.RecPaymentID, p.PayeeListID, u.PayeeID 
		FROM Payment p LEFT JOIN User_Payee u ON p.PayeeListID = u.PayeeListID 
		WHERE p.MemberID = @id 
		UNION 
		SELECT r.MemberID, '', r.RecPaymentID, r.PayeeListID, u.PayeeID  
		FROM Recurring_Payment r LEFT JOIN User_Payee u ON r.PayeeListID = u.PayeeListID 
		WHERE r.MemberID = @id 
END

if (@searchType = 'payee')
BEGIN
INSERT INTO @tblIDs (IDMember, IDPayment, IDRecPayment, IDPayeeList, IDPayee) 
		SELECT h.MemberID, h.PaymentID, h.RecPaymentID, h.PayeeListID, h.PayeeID     
		FROM Payment_History h 
		WHERE h.PayeeID = @id AND h.ProcessedDate > (DATEADD(d, (@days * -1), GETDATE()))
		UNION ALL
		SELECT p.MemberID, p.PaymentID, p.RecPaymentID, u.PayeeListID, u.PayeeID    
		FROM User_Payee u LEFT JOIN Payment p ON u.PayeeListID = p.PayeeListID 
		WHERE u.PayeeID = @id 
		UNION ALL
		SELECT r.MemberID, '', r.RecPaymentID, u.PayeeListID, u.PayeeID     
		FROM User_Payee u LEFT JOIN Recurring_Payment r ON u.PayeeListID = r.PayeeListID 
		WHERE u.PayeeID = @id  AND r.LastProcessedDate > (DATEADD(d, (@days * -1), GETDATE()))
END

if (@searchType = 'payment')
BEGIN
	INSERT INTO @tblIDs (IDMember, IDPayment, IDRecPayment, IDPayeeList, IDPayee) 
		SELECT h.MemberID, h.PaymentID, h.RecPaymentID, h.PayeeListID, h.PayeeID     
		FROM Payment_History h 
		WHERE h.PaymentID = @id OR h.RecPaymentID = @id 
		UNION ALL
		SELECT p.MemberID, p.PaymentID, p.RecPaymentID, p.PayeeListID, u.PayeeID     
		FROM Payment p LEFT JOIN User_Payee u ON p.PayeeListID = u.PayeeListID 
		WHERE p.PaymentID = @id OR p.RecPaymentID = @id  
		UNION ALL
		SELECT r.MemberID, '', r.RecPaymentID, r.PayeeListID, u.PayeeID    
		FROM Recurring_Payment r LEFT JOIN User_Payee u ON r.PayeeListID = u.PayeeListID 
		WHERE r.RecPaymentID = @id 
END

if (@searchType = 'payeelist')
BEGIN
INSERT INTO @tblIDs (IDMember, IDPayment, IDRecPayment, IDPayeeList, IDPayee) 
		SELECT h.MemberID, h.PaymentID, h.RecPaymentID, h.PayeeListID, h.PayeeID     
		FROM Payment_History h 
