*/
CREATE PROCEDURE [dbo].[admin_SendSavedMail] 
                                                   @Recipient      varchar(512),   
                                                   @CC                  varchar(255),  
                                                   @EmailID         int  
AS

SET NOCOUNT ON

DECLARE	@SPName   varchar(255), 
                  @Sender     varchar(255), 
                  @Subject    varchar(256), 
                  @Message  varchar(5000)                      

BEGIN TRY
   SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID));
  
   IF (@EmailID IS NOT NULL AND @EmailID > 0)
  BEGIN  
      SELECT @Sender = ISNULL([ConfigValue],'WRGBPResend') FROM [Configuration] WITH (NOLOCK) WHERE [ConfigName] = 'EmailProfileResend'
      
      SELECT @Subject = [Subject], 
                   @Message = [Body] 
         FROM [Saved_Emails] 
         WHERE [ID] = @EmailID     
         
      DECLARE @MailID int
               
      EXEC [msdb].[dbo].[sp_send_dbmail] 
		                                 @profile_name = @Sender,
		                                 @recipients = @Recipient, 
		                                 @copy_recipients = @CC, 
		                                 @subject = @Subject, 
		                                 @body = @Message, 
		                                 @body_format = 'TEXT',
		                                 @mailitem_id = @MailID OUTPUT 
		                               
	   SELECT @MailID as MailID          
   END 
   ELSE
      RAISERROR ('Invalid Parameters', 1, 1)
		                              
END TRY
BEGIN CATCH 
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

   EXEC [spLog]  @ErrorMessage, @SPName	  
   RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH



