/*
 ==================================================================
  SP Name:     admin_UpdateGeneratorJobSchedule

  Description: Update Generator Job Schedule by passing in Runtimes

  Resources:   Configuration
  ------------------------------------------------------------------
  Accepts:		@Days varchar(100), 
	            @RunTimes varchar(100)

  Returns:	   0 if successful 
               -1 if error
   ==================================================================
   Created:  	acheng
   By:    	 	3/12/2007
  ------------------------------------------------------------------
  Modifications: 
   04/12/2007  GNordstrom  Removed call to admin_GetGeneratorJobScheduleID
                           because it is not necessary and added execution 
                           context for msdb sps.
   06/12/2007  GNordstrom  Added run days parameter.  Modified to use  
                           TRY...CATCH instead of goto statements and to
                           include updates to the configuration table and to
                           wrap the updates in a transaction.  Also modified
                           to determine the generator version and type; so 
                           that if the version is the old generator then only
                           the configuration table is updated and if the type
                           is 'Batch' the appropriate job name is set.
   10/25/2007  GNordstrom  Modified to obtain Execute as login id from the 
                           configuration table.
==================================================================
*/
CREATE PROCEDURE [dbo].[admin_UpdateGeneratorJobSchedule]
                  @Days varchar(100), 
	               @RunTimes varchar(100)
AS	

SET NOCOUNT ON

DECLARE  @jobName varchar(255),
		   @message varchar(200),
		   @SPName  varchar(255), 
		   @Type    varchar(255), 
		   @Version varchar(255), 
		   @Period1 int, 
		   @Period2 int,  		   
         @GenID varchar(50)

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))

BEGIN TRY
   SELECT @Version = ISNULL(ConfigValue, '')  
      FROM Configuration WITH (NOLOCK) 
      WHERE UPPER(configName) = UPPER('GeneratorVersion')
   SELECT @GenID = ConfigValue 
      FROM Configuration WITH (NOLOCK) 
      WHERE UPPER(configName) = UPPER('EmailProfile')

   SET @Period1 = CHARINDEX('.', @Version, 1)
   SET @Period2 = CHARINDEX('.', @Version, (@Period1 + 1))
   
   --If the generator version number is equal to or greater than 2.8 
   IF (CONVERT(int, SUBSTRING(@version, 1, (@Period1 - 1))) > 2) OR 
         ((CONVERT(int, SUBSTRING(@version, 1, (@Period1 - 1))) = 2) AND ( CONVERT(int, SUBSTRING(@version, (@Period1 + 1), (@Period2 - @Period1 - 1))) >= 8))
   BEGIN      
      --get CU Name from the configuration table.
      SELECT @jobName = ISNULL(ConfigValue, '')  
         FROM Configuration WITH (NOLOCK) 
         WHERE UPPER(configName) = UPPER('CreditUnionName')

      IF LEN(@jobName) > 0
      BEGIN 
      BEGIN TRANSACTION
         --Get the generator type:  batch or realtime.
         SELECT @Type = ISNULL(ConfigValue, '') 
            FROM Configuration WITH (NOLOCK) 
            WHERE UPPER(configName) = UPPER('GeneratorType')
      
         --Set the jobname based on the cu name and generator type.
         IF UPPER(@Type) = UPPER('Batch')
            SET @jobName = @jobName + ' Batch' 
         ELSE
            SET @jobName = @jobName + ' Generator'
         
         DECLARE  @time varchar(20),
                  @day  varchar(20),
                  @enable int, 
                  @scheduleid int,
                  @schedulename varchar(80),
                  @activetime int,
                  @freqinterval int

         --Split runtimes string, and store them in a table
         DECLARE @tmpTime TABLE (ID int identity(0, 1), item varchar(20))     
         INSERT INTO @tmpTime SELECT ISNULL(item, '') FROM dbo.udfSplit(@RunTimes, ',')

         DECLARE cuTimes CURSOR FOR 
            SELECT  CASE ID
		                  WHEN 0 THEN 'Sunday'
		                  WHEN 1 THEN 'Monday'
		                  WHEN 2 THEN 'Tuesday'
		                  WHEN 3 THEN 'Wednesday'
		                  WHEN 4 THEN 'Thursday'
		                  WHEN 5 THEN 'Friday'
	                 END as [Day],
	                 REPLACE(item, ':', '') as [Time] 
               FROM @tmpTime 
               WHERE ID BETWEEN 0 AND 5 

         OPEN cuTimes 
         FETCH NEXT FROM cuTimes INTO @day, @time

         --Loop thru the records in the cursor
         WHILE @@FETCH_STATUS = 0 
         BEGIN
            --Set schedule name according to day.
            SET @schedulename = @day + ' Run'

            --Set schedule enable if there is a runtime.
            IF LEN(@time) = 0 
               SET @enable = 0
            ELSE
               SET @enable = 1

            --Get schedule id by passing in job name and schedule name
            DECLARE @tmpSchedule TABLE (ScheduleID int, ScheduleName sysname, Enabled int, FreqType int, FreqInterval int, 
                                  FreqSubDayType int, FreqSubdayInterval int, FreqRelativeInterval int, FreqRecur int, 
                                  ActiveStartDate int, ActiveEndDate int, ActiveStartTime int, ActiveEndTime int, 
                                  Created datetime, Description nvarchar(4000), NextRunDate int, NextRunTime int, 
                                  ScheduleUID uniqueidentifier, JobCount int)   

            EXECUTE AS LOGIN = @GenID
            INSERT INTO @tmpSchedule EXEC msdb.dbo.sp_help_jobschedule @job_name = @jobName, @schedule_name = @schedulename

            SELECT @scheduleid = ScheduleID, 
                   @activetime = ActiveStartTime, 
                   @freqinterval = FreqInterval
               FROM @tmpSchedule

            SET @time = @time + '00'
            --Update job schedule time only if time passed in is different.
            IF (@time != CONVERT(varchar, @activetime))
               EXEC msdb.dbo.sp_update_schedule 
                             @schedule_id = @scheduleid,
                             @enabled = @enable, 
                             @freq_type = 8,  --Weekly
                             @freq_interval = @freqinterval, 
                             @active_start_time = @time, 
                             @active_END_time = 235959
            REVERT
           
            FETCH NEXT FROM cuTimes INTO @day, @time 
         END
         
         CLOSE cuTimes
         DEALLOCATE cuTimes
         
         --Update the configuration table with the runDays and runTimes passed in.
--         EXEC admin_UpdateConfiguration 'RunDays', @Days
         EXEC admin_UpdateConfiguration 'RunTimes', @RunTimes
         
         COMMIT TRANSACTION 
         RETURN 0
      END
      ELSE
      --Could not obtain cu name from configuration table
      BEGIN
         SET @message = 'CU Name was not found in Configuration table.' 
         RAISERROR(@message, 16, 1)
      END
   END
   ELSE
   --Generator version is less than 2.8; so old generator is running
   --So just update the configuration table with the runTimes passed in.
      EXEC admin_UpdateConfiguration 'RunTimes', @RunTimes
          
END TRY
BEGIN CATCH

   IF (@@Trancount > 0) OR ((XACT_STATE()) = -1)
      ROLLBACK TRANSACTION

	SET @message = ISNULL(ERROR_MESSAGE(), '')
	EXEC spLog @message, @SPName
	
	DECLARE  @severity int, 
	         @state int
   SET @severity = ERROR_SEVERITY()
   SET @state = ERROR_STATE()
	RAISERROR(@message, @severity, @state)
	
	RETURN -1
END CATCH



