CREATE  PROCEDURE [dbo].[admin_UpdateOnUsPaymentStatus]
					@PaymentID		varchar(32), 
					@ChangeReason	varchar(255),  
					@UpdatedBy		varchar(32) 
AS

DECLARE @SeqNo int,
		@Message varchar(255) 
DECLARE  @SPName   varchar(255)
SET @SeqNo = 0

BEGIN TRY
	SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID)); 

	BEGIN TRANSACTION
	BEGIN
		UPDATE [Payment_History] 
			 SET [StatusCode] = 102,
				 [FailedDate] = NULL, 
