			WHERE [PaymentID] = @PaymentID 

		IF @@Error = 0 
		BEGIN
			SELECT TOP 1 @SeqNo =  ISNULL(SeqNo,0) FROM OnUsPaymentsTRACKING WHERE PaymentID = @PaymentID ORDER BY SeqNo desc
			SET @SeqNo = @SeqNo + 1

			INSERT INTO OnUsPaymentsTRACKING
				SELECT  SeqNo =  @SeqNo, PaymentID, MemberID, AccountID, LoanID, Amount, Comment, GLCode, RunId, ErrorCode, ErrorDesc, SourceApp, 
