/*
 ==================================================================
  Stored Procedure Name: dbo.admin_UpdateUserPayee

  Description: Updates the record in the User_Payee table with the values 
			      passed in for the PayeeListId passed in.

  Resources: 	User_Payee
  ------------------------------------------------------------------
  Accepts:		@PayeeListId   char(12),
				   @MemberId      char(32),
				   @AcctAtPayee   char(32),
				   @NameOnAcct    char(60),
				   @user_type		tinyint,
				   @change_reason	char(255),
				   @LastUpdatedBy char(32), 
				   @Active        varchar(1)

  Returns:		Status (0 if successful, -1 if error)
   ==================================================================
   Created:  	GNordstrom
   By:    		01/05/2006
  ------------------------------------------------------------------
  Modifications: 
	06/15/2006  GNordstrom  Added the output clause to the update statement
	                        and removed the "exec admin_InsertChangeHistory".
	09/18/2007  GNordstrom  Added code to determine if the status of the record
	                        is being inactivated and if it is then determine if
	                        there are any associated payments for the record and 
	                        if there are then cancel those payments.  Also added 
	                        updating of the Active field in the user_payee
	                        update statement.
=========================================================================
*/
CREATE    PROCEDURE [dbo].[admin_UpdateUserPayee]
					      @PayeeListId   char(12),
					      @MemberId      char(32),
					      @AcctAtPayee   char(32),
					      @NameOnAcct    char(60),
					      @user_type		tinyint,
					      @change_reason	char(255),
					      @LastUpdatedBy char(32), 
				         @Active        varchar(1) = NULL
AS

BEGIN TRY 
   DECLARE  @PymtCnt    int, 
            @RecurCnt   int, 
            @Result     int,
            @PaymentID  varchar(32), 
            @CurrStatus varchar(1)  

   BEGIN TRANSACTION 
      --Get the current status of the user payee record.
      SELECT @CurrStatus = Active FROM User_Payee WITH (NOLOCK) WHERE PayeeListID = @PayeeListId
      IF (@Active IS NULL)
         SET @Active = @CurrStatus
      
      --If the user payee record is being inactivated
      IF (@CurrStatus = 'Y' AND @Active = 'N')
      BEGIN 
         --Get the number of payments associated with this PayeeListID
         SET @PymtCnt = (SELECT COUNT(PayeeListID) FROM Payment WITH (NOLOCK) WHERE PayeeListID = @PayeeListId) 
         --Get the number of active recurring payments associated with this PayeeListID
         SET @RecurCnt =  (SELECT COUNT(PayeeListID) FROM Recurring_Payment WITH (NOLOCK) WHERE PayeeListID = @PayeeListId AND Active = 'Y')

         --If payments were found for this PayeeListID, then canel them.
         IF (@PymtCnt > 0) 
         BEGIN
            --Open a cursor with the PaymentID's 
            DECLARE curPymts CURSOR 
               FOR SELECT PaymentID 
                     FROM Payment WITH (NOLOCK) 
                     WHERE PayeeListID = @PayeeListId 
            
            OPEN curPymts         
            FETCH NEXT FROM curPymts INTO @PaymentID
            WHILE @@FETCH_STATUS = 0 
            BEGIN
               --Cancel the payment.
               EXEC @Result = admin_CancelPayment @MemberId, @PaymentID, 1, @change_reason, null, @LastUpdatedBy
               --If an error occurred during cancellation, then raise an error
               IF @Result = -1
                  RAISERROR('Payment Cancel Failed', 1, 1)
               FETCH NEXT FROM curPymts INTO @PaymentID
            END
            
            CLOSE curPymts
            DEALLOCATE curPymts
         END

         --If there are active recurring payments in the recurring_payment table then cancel them
         IF (@RecurCnt > 0) 
         BEGIN
            --Open a cursor with the RecPaymentID's 
            DECLARE curRecur CURSOR 
               FOR SELECT RecPaymentID 
                     FROM Recurring_Payment WITH (NOLOCK) 
                     WHERE PayeeListID = @PayeeListId 
                        AND Active = 'Y'
            
            OPEN curRecur         
            FETCH NEXT FROM curRecur INTO @PaymentID
            WHILE @@FETCH_STATUS = 0 
            BEGIN
               --Cancel the payment.
               EXEC @Result = admin_CancelRecurPayment @PaymentID, 1, @change_reason, null, @LastUpdatedBy
               --If an error occurred during cancellation, then raise an error
               IF @Result = -1
                  RAISERROR('Payment Cancel Failed', 1, 1)

               FETCH NEXT FROM curRecur INTO @PaymentID
            END
            
            CLOSE curRecur
            DEALLOCATE curRecur
         END
      END

      --Update the user_payee record
      UPDATE User_Payee SET
	      UsersAccountAtPayee = @AcctAtPayee,
	      NameOnAccount  = @NameOnAcct,
	      LastUpdatedOn = GETDATE(), 
	      Active = @Active   
      OUTPUT DELETED.PayeeListID, DELETED.MemberID, DELETED.PayeeID, DELETED.CreatedOn, 
               DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, 
               DELETED.Active, DELETED.MinDaystoPay, DELETED.PaymentMethod, 
               @user_type, @change_reason, 'U', GETDATE(), @LastUpdatedBy  
         INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
               LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
               PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
      WHERE PayeeListId = @PayeeListId 
	      AND MemberID = @MemberId
      
   COMMIT TRANSACTION

   SELECT "Status"=0
   RETURN (0)
   
END TRY
BEGIN CATCH
   IF (@@TRANCOUNT > 0) OR ((XACT_STATE()) = -1)
      ROLLBACK TRANSACTION

	SELECT "Status" = -1
	RETURN (-1)
END CATCH
GO
