   BEGIN      
      --Get the run days
      SELECT @RunDays = ConfigValue 
         FROM Configuration WITH (NOLOCK) 
         WHERE UPPER(ConfigName) = 'RUNDAYS'

      --If the due date passed in is on a non-business day (Saturday, 
      --Sunday, or a holiday) then set the due date to the first
      --valid business day preceding the due date passed in.
      WHILE (DATENAME(dw, @DueDate) IN ('Saturday', 'Sunday')
            OR (EXISTS (SELECT Date FROM Holidays WITH (NOLOCK) WHERE Date = @DueDate)))
         SET @DueDate = DATEADD(d, -1, @DueDate)
         
      --Obtain the payment method of the payee list id passed in.
      SELECT @PayType = CASE ISNULL(PaymentMethod, '')  
                        WHEN '' THEN 'C'
                        ELSE PaymentMethod 
