         FROM User_Payee WITH (NOLOCK)  
         WHERE PayeeListID = @PayeeListID 

      --Obtain the payment method business days configuration value to determine
      --how many business days are required to get the payment to the payee.
      IF @PayType = 'E' 
         SELECT @BusinessDays = ConfigValue 
            FROM Configuration WITH (NOLOCK)
            WHERE UPPER(ConfigName) = 'ELECTRONICBUSINESSDAYS'
      ELSE 
         SELECT @BusinessDays = ConfigValue 
            FROM Configuration WITH (NOLOCK)
            WHERE UPPER(ConfigName) = 'CHECKBUSINESSDAYS'
                   
      --Obtain the process date, which is the first valid generator run day   
      --preceding the due date minus the number of valid business days designated 
      --as the number of processing days needed for the payee list id passed in.              
      SELECT @ProcessDate = CalDate   
         FROM (
              SELECT CalDate, 
                     ROW_NUMBER() OVER(ORDER BY CalDate DESC) as RowNum, 
                     CHARINDEX(DATENAME(dw, CalDate), @RunDays) as RunDay
               FROM Calendar WITH (NOLOCK) 
               WHERE DateName(dw, CalDate) NOT IN ('Saturday', 'Sunday') 
                  AND CalDate NOT IN (SELECT Date FROM Holidays WITH (NOLOCK)) 
                  AND CalDate <= @DueDate  
            ) c 
         WHERE RowNum = @BusinessDays 
         
      IF @ProcessDate IS NULL
         SET @ProcessDate = CONVERT(smalldatetime, CONVERT(varchar, GETDATE(), 101))

--      --Get the process time for the process date 
