				06/2007 Modified: Join with Shares Table
						Added:    s.Account,
								  s.NickName as AcctDescription,
								  s.NativeType
				06/13/07 Change name to BatchAdmin_GetMemberInfo
==================================================================
*/

CREATE PROCEDURE [dbo].[BatchAdmin_GetMemberInfo]
	  @MemberID varchar(32),
	  @CUID varchar(32)
AS

DECLARE @FirstName varchar(32),
		@MiddleName varchar(32), 
		@LastName varchar(32),
		@Suffix varchar(5),
		@Address1 varchar(32), 
		@Address2 varchar(32),
		@City varchar(32),
		@State varchar(32),
		@PostalCode varchar(10), 
		@Phone varchar(15),
		@EmailAddress varchar(50), 
		@status int

SET @Status = 0 

IF @MemberID =' ' or @CUID = ' '
BEGIN
	SELECT  0 as Status
	RETURN
END

IF NOT EXISTS (SELECT MemberID FROM ENROLLMENT WITH (NOLOCK) WHERE MemberID = @MemberID and CUID = @CUID and Flag = 'A')
BEGIN
	SELECT  0 as Status    --  this is the return code for member not being enrolled
	RETURN 0
END

SELECT 
		@Status as Status,
		'' as DOB,
		FirstName,
		ISNULL(MiddleName,'') as MI,
		LastName, 
		'' as SSN,
		Address1,
		ISNULL(Address2,'') as Address2,
		City,
		State,
		PostalCode as ZipCode,
		REPLACE(HomePhone,'-','') as HomePhone,
		ISNULL(WorkPhone,'') as WorkPhone,
		ISNULL(Extension,'') as WorkPhoneExt,
		'' as Maiden,
		ISNULL(Suffix,'') as Suffix,
		'' as Title,
		EmailAddress as Email,
		LocatorID, 
		UpdateDate as LastUpdatedDate
FROM Member WITH (NOLOCK)
WHERE MemberID = @MemberID 
AND CUID = @CUID 
AND Active = 'A'

SELECT ID,
	  SUBSTRING(Account,CHARINDEX('S', Account)+1,len(Account)) AS Account,
	  NickName,
	  NativeType,
	  Active
FROM Shares WITH (NOLOCK)
WHERE MemberID = @MemberID 
AND CUID = @CUID		 


IF @@ROWCOUNT = 0
	SELECT  0 as Status




