		@State varchar(32),
		@PostalCode varchar(32), 
		@Phone varchar(15),
		@EmailAddress varchar(50),
		@CUID varchar(9),
		@Account varchar(22),
		@AccountDescription varchar(30),
		@NativeType int
AS

DECLARE @rc int,  @now datetime
SET @Now = GETDATE()
IF EXISTS (SELECT MEMBERID FROM Member WITH (NOLOCK) WHERE Active = 'A' and MemberID = @MemberID and CUID = @CUID)
BEGIN
	EXEC @rc = BatchAdmin_UpdateMember @MemberID, @CUID, @FirstName, @MiddleName, @LastName, @Suffix, @Address1, @Address2, @City,
		 @State, @PostalCode, @Phone, @EmailAddress, @MemberID
	IF (@@ERROR <> 0) GOTO on_error
	IF @rc != 0
		Return -1
END
ELSE
BEGIN
	BEGIN TRANSACTION	

		INSERT INTO Member(
					  MemberID,
					  CUID, 
					  Suffix, 
					  FirstName, 
					  MiddleName, 
					  LastName, 
					  Address1, 
					  Address2, 
					  City, 
					  State, 
					  PostalCode, 
					  HomePhone, 
					  WorkPhone, 
					  EmailAddress, 
					  Active, 
					  UpdateBy,
					  EntryDate, 
					  UpdateDate, 
					  SubscriberID)
		VALUES (@MemberID, 
					@CUID,
					@Suffix, 
					@FirstName, 
					@MiddleName, 
					@LastName, 
					@Address1, 
					@Address2, 
					@City, 
					@State, 
					@PostalCode, 
					@Phone,
					'',
					@EmailAddress, 
					'A',
					@MemberID,
					@Now, 
					@Now,
					NULL )
		IF (@@ERROR <> 0) GOTO on_error    	
	
		SET @Account = @MemberID + '-S' + @Account
		INSERT INTO SHARES( 
				  MemberID, 
				  CUID, 
				  NickName, 
				  Account, 
				  AccountType, 
				  EntryDate, 
				  UpdateDate, 
				  UpdateBy, 
				  Active, 
				  NativeType)
		VALUES ( 
				  @MemberID, 
				  @CUID, 
				  @AccountDescription, 
				  @Account, 
				  'CHK', 
				  @Now, 
				  @Now, 
				  @MemberId, 
				  'A',
				  @NativeType)
		IF (@@ERROR <> 0) GOTO on_error			
		
		INSERT INTO ENROLLMENT ( 
					  MemberID, 
					  CUID, 
					  BPEnrollDate, 
					  Flag, 
					  FirstSignon, 
					  BPDenrollDate, 
					  LastSignon, 
					  BPLastActivity, 
					  BPDisabled )
		VALUES ( 
			  @MemberID, 
			  @CUID, 
			  @Now, 
			  'A',
			  @Now, 
			  NULL, 
			  @Now, 
			  @Now, 
			  0)
		IF (@@ERROR <> 0) GOTO on_error		
	COMMIT TRANSACTION
END
RETURN 0

