*/
CREATE PROCEDURE [dbo].[BPEGetPayeeInfo]
	@member_id		char(32),
	@payee_list_id		char(12)
AS
--
-- Here's the set of values we'll return, in order:
--
DECLARE @acct		char(32)
DECLARE @nameonacct	char(60)
DECLARE @payee_id	char(12)
DECLARE @provider_id	int
DECLARE @payee_name	char(32)
DECLARE @payee_address1	char(32)
DECLARE @payee_address2	char(32)
DECLARE @payee_address3	char(32)
DECLARE @payee_city	char(32)
DECLARE @payee_state	char(5)
DECLARE @payee_zip	char(11)
DECLARE @payee_country	char(3)
DECLARE @payee_phone	char(32)
DECLARE @payee_alias_id	char(12)
DECLARE @payee_payment_method char(1)
--
-- First, assume that the payee isn't aliased, and just get
-- the straight info from the PAYEE and USER_PAYEE tables:
--
SELECT 	@acct		= U.UsersAccountAtPayee, 
	@nameonacct	= U.NameOnAccount,
	@payee_id	= U.PayeeID,
	@provider_id	= P.ProviderID,
	@payee_name	= P.Name,
	@payee_address1	= P.Address1,
	@payee_address2	= P.Address2,
	@payee_address3	= P.Address3,
	@payee_city	= P.City,
	@payee_state	= P.State,
	@payee_zip	= P.PostalCode,
	@payee_country	= P.Country,
	@payee_phone 	= P.Phone,
	@payee_alias_id	= P.PayeeAliasID,
	@payee_payment_method = U.PaymentMethod
FROM	PAYEE P, USER_PAYEE U
WHERE	U.PayeeID 	= P.PayeeID
AND	U.MemberID	= @member_id
AND	U.PayeeListID	= @payee_list_id
IF (@@ERROR <> 0) GOTO on_error
IF (@payee_alias_id = '') GOTO done
--
-- Found a non-blank alias ID, so go look up the alias:
--
SELECT 	@payee_id	= PayeeID,
	@provider_id	= ProviderID,
	@payee_name	= Name,
	@payee_address1	= Address1,
	@payee_address2	= Address2,
	@payee_address3	= Address3,
	@payee_city	= City,
	@payee_state	= State,
	@payee_zip	= PostalCode,
	@payee_country	= Country,
	@payee_phone    = Phone,
	@payee_alias_id	= PayeeAliasID
FROM	PAYEE
WHERE	PayeeID 	= @payee_alias_id
IF (@@ERROR <> 0) GOTO on_error
done:
SELECT 	@acct, 
	@nameonacct,
	@payee_id, 
	@provider_id, 
	@payee_name, 
	@payee_address1, 
	@payee_address2, 
	@payee_address3, 
	@payee_city, 
	@payee_state, 
	@payee_zip, 
	@payee_country, 
	@payee_Phone,
	@payee_alias_id,
	@payee_payment_method
RETURN (0)
on_error:
RETURN (-1)
GO
