	7/8/2013	Fixed checking for Auto posting onus loans

===================================================================================*/

CREATE PROCEDURE [dbo].[BPG_CreateAllPaymentFiles]
@CUID varchar(9),
@RunID int
AS

BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

    -- Insert statements for procedure here
DECLARE	@ProviderID int,
		@rc int,
		@CourtesyPay int,
		@AutopostOnUs char(1)

DECLARE	@FileName varchar(255),
		@ProviderName varchar(50),
		@OutBoundFormat varchar(50),
		@Message varchar(255),
		@SPName varchar(50)

SET @SPName = 'BPG_CreateAllPaymentFiles'

SELECT @CourtesyPay = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'SpecialCourtesyPay'
SELECT @AutoPostOnUs = ISNULL(ConfigValue,'N' ) FROM Configuration WHERE ConfigName = 'OnUsPostingEnabled'

-- Loop over payment processors and create payment request files with all due payments; Order by default provider so default provider will be handled last
DECLARE @tmpProviders TABLE ( ProviderID int, ProviderName varchar(50),	OutBoundFormat varchar(50) )

INSERT INTO @tmpProviders
	SELECT	ProviderID, 
		ProviderName, 
		OutBoundFormatSP
	FROM Provider 
	WHERE Active = 'Y'

NextProvider:

SELECT TOP 1 @ProviderID = ProviderId, 
			 @ProviderName = ProviderName, 
			 @OutBoundFormat = OutBoundFormat
	FROM @tmpProviders 

	IF @@RowCount = 0
		GOTO BuildNSF

	IF @AutoPostOnUs = 'Y' and @providerid = 101
	GOTO GetNextProvider
		
	EXEC @rc = @OutBoundFormat @ProviderID, @RunID  --  Execute the appropriate sp that builds the file
	IF @rc != 0
	BEGIN
		SET @Message = 'Error Building OutPut File for: ' + @ProviderName
		EXEC spLog @Message, @SPName
	END		

GetNextProvider:

    DELETE FROM @tmpProviders WHERE ProviderID = @ProviderID
	GOTO NextProvider

BuildNSF:
	IF @CourtesyPay = 1
	BEGIN
		SET @Message = 'Error creating bpsym.out file'
		EXEC @rc = spCreateFees @RunID
		IF @rc != 0
			EXEC spLog @Message, @SPName, @RunID
	END

RETURN 
END



