-- ==============================================================================================
-- Author:		Helen Liou
--
-- Create date: 8/23/2006
--
-- Description:	This procedure builds the credit card file that is processed by the host.  There
--				is a process on the host that takes these payments and batches the information up
--				and sends payments to the credit card vendor to process.
--
-- Parameters:  ProviderID : This is the lookup id in the provider table to get format information.
--				RunID : Current runid
-- ===============================================================================================
CREATE PROCEDURE [dbo].[BPG_CreateCreditCardFile]
@ProviderID int,
@RunID int
AS
BEGIN

SET NOCOUNT ON

DECLARE @rc int,  
		@id int,  
		@count int, 
		@totalrequests int, 
		@totalamount money, 
		@Filecount int

DECLARE @ArchiveDir varchar(255),
		@ArchiveFiles varchar(255),
		@Message varchar(500), 
		@SQL varchar(500),
		@data varchar(1000), 
		@FileName varchar(255), 
		@FormatData varchar(255),
		@CUName char(19), 
		@CURouting varchar(9),	
		@SPName varchar(50), 
		@CompanyName char(12), 
		@OutFilePath varchar(255), 
		@NewFileName varchar(255), 
		@CreditCardType varchar(5),
		@SearchString varchar(20)

DECLARE @Now datetime

SET @Now = GETDATE()
SET @SPName = 'BPG_CreateCreditCardFile'

EXEC BPG_InsertGeneratorStatus @RunID, 13, 'Creating Credit Card File'

SELECT @ArchiveDir= OutBoundPath + ArchiveDirectoryName + '\',
	@OutFilePath= ISNULL(OutBoundPath,'C:\Billpay\'),
	@FormatData = FormatSpecificData
	FROM Provider WHERE ProviderID=  @ProviderID

SET @SearchString = 'CompanyName='
SELECT @CUName = ISNULL(SubString(@FormatData, charindex(@searchstring,@FormatData)+len(@searchstring),charindex(';',@FormatData)-charindex('=',@formatdata)+1),'CUNAME HERE')

SELECT @CompanyName = ConfigValue FROM Configuration WHERE ConfigName = 'CreditUnionName'
SELECT @CURouting = ConfigValue FROM Configuration WHERE ConfigName = 'CURoutingNumber'
SELECT @CreditCardType = ConfigValue FROM Configuration WHERE ConfigName = 'ClearedCheckExtension'

SET @Message = 'Invalid Archive Directory defined in configuration table.'
IF dbo.udfPathExists( @ArchiveDir ) = 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
	IF dbo.udfCreateDirectory (@ArchiveDir) = 0 
		GOTO spError
END

-- If the file exists, move it to the archive dir

SET @FileCount = 1
SET @SQL = RTRIM(@CURouting)  + '_'+ @CreditCardType + '_*.*'
CREATE TABLE #ArchiveFiles(C1 varchar(256)) INSERT INTO #ArchiveFiles 
		SELECT * FROM dbo.udfGetDirectoryFiles (@OutFilePath, @SQL)

DELETE #ArchiveFiles WHERE (C1 IS NULL)
IF @@ERROR <> 0
	GOTO spError

DECLARE cArchiveFiles CURSOR FOR SELECT C1 FROM #ArchiveFiles
OPEN cArchiveFiles

FETCH NEXT FROM cArchiveFiles INTO @FileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @ArchiveFiles = RTRIM(@OutFilePath) + RTRIM(@FileName) 
	IF @FileName = 'File Not Found'
		GOTO spContinue

	-- Make sure the file exists in case of File Not Found is returned from dir command
	IF dbo.udfPathExists( @ArchiveFiles ) = 0
		GOTO spContinue  -- Then it does not exist, otherwise you get a zero

-- Look for new filename (renamed file) to archive
	SET @Message = 'Could not move Payment Request File to archive directory: ' + @ArchiveFiles
	IF RIGHT(@ArchiveDir, 1) <> '\'
		SET @ArchiveDir = @ArchiveDir  + '\'

	IF dbo.udfMoveFile ( @ArchiveFiles, @ArchiveDir + @FileName) = 0
		BEGIN
			SET @Message = 'Already a file named  : ' + @ArchiveFiles + ' renaming file to '
			SET @ArchiveFiles = @ArchiveFiles +  Convert(varchar,@FileCount)
			SET @Message = @Message + @ArchiveFiles

			EXEC spLog @Message, @SPName
		 	GOTO spContinue
		END

	SET @FileCount = @FileCount + 1
		
FETCH NEXT FROM cArchiveFiles INTO @FileName

END

spContinue:
	CLOSE cArchiveFiles
	DEALLOCATE cArchiveFiles
--  Check if there are any payments for the file, if not, don't create one
SELECT @Count = Count(PaymentID) 
	FROM Payment_History PH INNER JOIN Payee P ON PH.PayeeID = P.PayeeID
	WHERE RunID = @RunID and PH.ProviderID = @ProviderID

IF @Count = 0
BEGIN
	EXEC BPG_UpdateGeneratorStatus @RunID, 13, 'No Payments for Credit Card File'
	RETURN 0
END

-- Format the payments from the payment history table for the run and processor ID into a table
-- Recreate the temp request table
IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CC_RQ]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CC_RQ]

CREATE TABLE [dbo].[TEMP_CC_RQ]
(
	[Request_ID] [int] IDENTITY (1, 1) NOT NULL ,
	[Request_Data] [varchar] (1000) NULL 
) ON [PRIMARY]


-- Add a file header and batch header row to the temp request table for the current payment run id
INSERT INTO TEMP_CC_RQ
	VALUES ('HEADER' + @CUName + Convert(char(12),@CompanyName) + REPLACE(CONVERT(char(10), CURRENT_TIMESTAMP, 101), '/', '') + 'RUN # ' + CONVERT(varchar(5), @RunID))

-- Add them to the princeton request table
INSERT INTO TEMP_CC_RQ
	SELECT 
		REPLACE(CONVERT(char(10), CURRENT_TIMESTAMP, 101), '/', '') + REPLACE(CONVERT(char(5), CURRENT_TIMESTAMP, 108), ':', '') + 
		CONVERT(char(2), 'E') + 
		CONVERT(char(12), P.PayeeID)  + 
		REPLACE(STR(Amount,15), ' ', '0') + 
		CONVERT(char(34), UsersAccountAtPayee) + 
		CONVERT(char(60), (NameOnAccount)) + 
		CONVERT(char(20), Memo) + 
		CONVERT(char(35), P.Name) + 
		CONVERT(char(35), P.Address1) + 
		CONVERT(char(35), P.Address2) + 
		CONVERT(char(35), P.City) + 
		CONVERT(char(5), P.State) + 
		CONVERT(char(3), 'USA') + 
		CONVERT(char(10), P.PostalCode) + 
		CONVERT(char(35), (RTRIM(MemberFirstName) + ' ' + MemberLastName)) + 
		CONVERT(char(35), (MemberAddress1)) + 
		CONVERT(char(35), (MemberAddress2)) + 
		CONVERT(char(35), (MemberCity)) + 
		CONVERT(char(3), (RTRIM(MemberState)))+
		CONVERT(char(4), 'USA ') +
		CONVERT(char(28), RTRIM(MemberPostalCode)) + 
		CONVERT(char(10), PaymentID) 
	FROM  PAYMENT_HISTORY PH
		JOIN PAYEE P ON PH.PayeeID = P.PayeeID
	WHERE
		PH.RunID = @RunID AND 
		PH.StatusCode = 101 AND
		P.ProviderID = @ProviderID

-- Get the number of requests and total amount for the batch
SELECT
	@TotalRequests = ISNULL(COUNT(PaymentID), 0), 
	@TotalAmount = ISNULL(SUM(Amount), 0) 
FROM  PAYMENT_HISTORY PH
	JOIN PAYEE P ON PH.PayeeID = P.PayeeID
WHERE
	PH.RunID = @RunID AND 
	PH.StatusCode = 101 AND
	P.ProviderID = @ProviderID

-- Add the file trailer
INSERT INTO TEMP_CC_RQ
	VALUES ('TRAILER' + REPLACE(STR(@TotalRequests, 8),' ','0') + REPLACE(STR(@TotalAmount, 12),' ','0'))

--SELECT * FROM TEMP_CC_RQ

-- Export the file
SET @Message = 'Could not create Credit Card payment request file.'
SET @NewFileName =  @OutFilePath + RTRIM(@CURouting)+ '_CC_'+REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') +'.OUT'
SET @SQL = 'bcp "SELECT Request_Data FROM ' + DB_Name() + '.dbo.TEMP_CC_RQ ORDER BY Request_ID" queryout "' + @NewFileName + '" -c -S' + @@SERVERNAME  + ' -T'
EXEC @rc = master..xp_cmdshell @SQL
IF @rc != 0
	GOTO spError

-- Make sure the file was created
SET @Message = 'On Us Credit Card request file was not created..  File = ' + @NewFileName
IF dbo.udfPathExists ( @NewFileName ) = 0
	GOTO spError   -- Then it exists, otherwise you get a zero

-- Update the status code to 102
UPDATE PAYMENT_HISTORY 
	SET StatusCode = 102 
	FROM PAYMENT_HISTORY PH JOIN PAYEE  P ON PH.PayeeID = P.PayeeID
	WHERE 
		PH.StatusCode = 101 AND 
		PH.RunID = @RunID AND 
		P.ProviderID = @ProviderID 

-- Drop the temp table
IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CC_RQ]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CC_RQ]

EXEC BPG_UpdateGeneratorStatus @RunID, 13, 'Credit Card File Created Sucessfully'

RETURN 0

spError:
	EXEC spLog @Message, @SPName
	EXEC BPG_UpdateGeneratorStatus @RunID, 13, 'Credit Card File Creation Failed'
	RETURN 1

END

