--print 'totalcount = '+ convert(varchar, @totalcount)
--print 'Detailcount = '+ convert(varchar, @detailblock)
--print 'Blockcount ='+Convert(varchar,@blockCount)
	SET @Block = @BlockCount % 10
	SET @TotalBlock = @BlockCount/10
	IF @Block > 0
	BEGIN
		SET @Block = 10 - @Block
		SET @TotalBlock = @TotalBlock + 1
	END

	INSERT INTO TEMP_REQUESTS VALUES 
