		REPLACE(STR(@BatchCount,6),' ','0')+
		REPLACE(STR(@TotalBlock,6),' ','0') +
		REPLACE(STR(@DetailCount-1,8),' ','0')  + 
		REPLACE(STR(RIGHT(@FileHash,10), 10,  0),' ','0') +
		REPLACE(STR(0,12),' ','0')+
		REPLACE(STR(@totalamount,12),' ','0') +
		@Filler39)

	WHILE @Block > 0
	BEGIN
		INSERT INTO TEMP_REQUESTS
			VALUES (REPLICATE('9',94))
		SET @Block = @Block - 1
	END


-- Export the file
SET @Message = 'Could not create On Us payment request file.'
SET @NewFileName = @OutFilePath + @CURouting + '_'+ @FileType+ '_'+REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') +'.OUT'
