SELECT @AccountLength = ISNULL(ConfigValue,11) 
	FROM Configuration 
	WHERE ConfigName = 'AccountLength'

SELECT @SuffixLength = ISNULL(ConfigValue,2) 
	FROM Configuration 
	WHERE ConfigName = 'AccountSuffixLength'

SET @Message = 'Invalid Archive Directory defined in configuration table.'
IF dbo.udfPathExists( @ArchiveDir ) = 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
	IF dbo.udfCreateDirectory (@ArchiveDir) = 0 
		GOTO spError
END

-- If the file exists, move it to the archive dir

SET @FileCount = 1
SET @SQL = RTRIM(@CURouting)  + '_ONUS_*.*'
CREATE TABLE #ArchiveFiles(C1 varchar(256)) INSERT INTO #ArchiveFiles 
	SELECT * FROM dbo.udfGetDirectoryFiles (@OutFilePath, @SQL)

DELETE #ArchiveFiles WHERE (C1 IS NULL)
IF @@ERROR <> 0
	GOTO spError

DECLARE cArchiveFiles CURSOR FOR SELECT C1 FROM #ArchiveFiles
OPEN cArchiveFiles

FETCH NEXT FROM cArchiveFiles INTO @FileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @ArchiveFiles = RTRIM(@OutFilePath) + RTRIM(@FileName) 
	IF @FileName = 'File Not Found'
		GOTO spContinue

	-- Make sure the file exists in case of File Not Found is returned from dir command
	IF dbo.udfPathExists ( @ArchiveFiles ) = 0
	-- Then it does not exist, otherwise you get a zero
		GOTO spContinue

