-- ================================================================================================
-- Author:		Helen Liou
--
-- Create date: 8/23/2006
--
-- Description:	Builds the WRGBPS payment file to be sent to WRG for processing
--
-- Parameters:	ProviderID : Id of the provider in the payee record that determines if these
--							 payments will be sent to WRG for processing.
--				RunID : Current runid
--
-- Modifications:
-- HLiou	9/5/2007  - added memo line to the WRGBPS payment file
-- HLiou	2/10/2009 - changed convert from int to bigint when creating wrg outfile
-- HLiou	12/11/2013 - changed to use new providerid column in payment_history table instead of payee table.
--
-- ================================================================================================
CREATE PROCEDURE [dbo].[BPG_CreateWRGPaymentFile]
@ProviderID int,
@RunID int
AS

BEGIN

SET NOCOUNT ON;

    -- Insert statements for procedure here
DECLARE @SPName					varchar(50),
		@OutFilePath			varchar(255),
		@ArchiveDir				varchar(255),
		@FormatData				varchar(255),
		@ProviderName			varchar(32),
		@FileName				varchar(255),
		@Message				varchar(512),
		@ImmediateDestination	varchar(9),
		@ImmediateDestinationName varchar(32),
		@Extension				varchar(5),
		@SQL					varchar(512),
		@ArchiveFiles			varchar(255),
		@PaymentFileType		varchar(5)
	
DECLARE @Now		DateTime
		
DECLARE @rc				int,
		@Count			int,
		@NumPayments	int

DECLARE @TotalAmount	money

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
	
EXEC BPG_InsertGeneratorStatus @RunID, 11, 'Creating WRGBPS Payment File'

SELECT  @OutFilePath = RTRIM(OutBoundPath), 
		@ArchiveDir = ArchiveDirectoryName, 
		@FormatData = FormatSpecificData,  
		@ProviderName = ProviderName, 
		@Extension = ISNULL(OutExtension,''),
		@FileName = OutBoundPaymentsFile
	FROM Provider 
	WHERE ProviderID = @ProviderID

SET @Message = 'Credit Union Name is missing from the configuration table'
SELECT @ImmediateDestination = ISNULL(ConfigValue,'') FROM Configuration WHERE ConfigName = 'CURoutingNumber'
IF @ImmediateDestination = ''
	GOTO spError

SET @Message = 'Credit Union Name is missing from the configuration table'
SELECT @ImmediateDestinationName = ISNULL(ConfigValue,'') FROM Configuration WHERE ConfigName = 'CreditUnionName'
IF @ImmediateDestinationName = ''
	GOTO spError

SELECT @PaymentFileType = ISNULL(ConfigValue,'PRF') FROM Configuration WHERE ConfigName = 'PaymentFileExtension'

SET @now = GETDATE ()
SET @ArchiveDir = @OutFilePath + @ArchiveDir + '\'
SET @Message = 'Invalid Archive Directory defined in configuration table.'
--EXEC @rc = spPathValidate @ArchiveDir
IF dbo.udfPathExists( @ArchiveDir ) = 0
--IF @rc <> 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
--	EXEC @rc = spCreateDirectory @ArchiveDir
	IF dbo.udfCreateDirectory (@ArchiveDir) = 0 
		GOTO spError
END

SET @Count = 1

--CREATE TABLE #ArchiveFiles(C1 varchar(256)) INSERT #ArchiveFiles EXEC master..xp_cmdshell @Command_String = @SQL
--DELETE #ArchiveFiles WHERE (C1 IS NULL)
--IF @@ERROR <> 0
--	GOTO spError
SET @SQL = RTRIM(@ImmediateDestination)  + '_' + @PaymentFiletype + '_*.' + @Extension
CREATE TABLE #ArchiveFiles (C1 varchar(256)) 
	INSERT INTO #ArchiveFiles SELECT * FROM dbo.udfGetDirectoryFiles (@OutFilePath, @SQL)

DECLARE cArchiveFiles CURSOR FOR SELECT C1 FROM #ArchiveFiles
OPEN cArchiveFiles

FETCH NEXT FROM cArchiveFiles INTO @FileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @ArchiveFiles = RTRIM(@OutFilePath) + RTRIM(@FileName) 
	IF @FileName = 'File Not Found'
		GOTO spContinue

	-- Make sure the file exists in case of File Not Found is returned from dir command
	--EXEC @rc = spFileExists @ArchiveFiles
	IF dbo.udfPathExists ( @ArchiveFiles ) = 0
--	IF @rc <> 0    -- Then it does not exist, otherwise you get a zero
		GOTO spContinue

-- Look for new filename (renamed file) to archive
	SET @Message = 'Could not move Payment Request File to archive directory: ' + @ArchiveFiles
	IF RIGHT(@ArchiveDir, 1) <> '\'
		SET @ArchiveDir = @ArchiveDir  + '\'

	IF dbo.udfMoveFile ( @ArchiveFiles, @ArchiveDir + @FileName) = 0
	BEGIN
		SET @Message = 'Already a file named  : ' + @ArchiveFiles + ' renaming file to '
		SET @ArchiveFiles = @ArchiveFiles +  Convert(varchar,@Count)
		SET @Message = @Message + @ArchiveFiles

		EXEC spLog @Message, @SPName
		 	GOTO spContinue
	END

	SET @Count = @Count + 1
		
	FETCH NEXT FROM cArchiveFiles INTO @FileName

END
spContinue:
	CLOSE cArchiveFiles
	DEALLOCATE cArchiveFiles

--  Initialize variables

DECLARE @tmptable TABLE (PaymentID varchar(10), UsersAccountAtPayee varchar(40), MemberID varchar(22), Account varchar(32), Amount int, Name varchar(32), 
		PayeeListID varchar(12), MemberFirst varchar(32), MemberLast varchar(32))

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_REQUESTS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_REQUESTS]

CREATE TABLE [dbo].[TEMP_REQUESTS]
(
	[Request_ID] [int] IDENTITY (1, 1) NOT NULL ,
	[Request_Data] [varchar] (1000) NULL 
) ON [PRIMARY]

--  Build File Header
--  'FHRQ', WESCOM CU, RunID Char(10), FileDate YYYYMMDDHHMMSS

INSERT INTO TEMP_REQUESTS
	VALUES ('FHRQ' + 'WESCOM CU' + CONVERT(char(10), @RunID) + replace(replace(replace(CONVERT(varchar(20), CURRENT_TIMESTAMP, 120), '-', ''), ' ', ''), ':', ''))

--  Build Batch Header
--  'BH          ', CURouting CHAR(9), 

INSERT INTO TEMP_REQUESTS 
	VALUES ('BH' +	CONVERT(char(10), 1) + @ImmediateDestination  + REPLICATE(' ', 64))

--  Build Detail records
--  'TX', PaymentID char(10), amount (15), 

INSERT INTO TEMP_REQUESTS
	SELECT 
		'TX' + 
		CONVERT(char(10), CONVERT(bigint, PaymentID))  + 
		REPLACE(STR(Convert(money, Amount)/100, 15,2), ' ', '0') + 
		CONVERT(char(32), (MemberFirstName)) + 
		CONVERT(char(32), (MemberMiddleName)) + 
		CONVERT(char(32), (MemberLastName)) + 
		CONVERT(char(32), (MemberAddress1)) + 
		CONVERT(char(32), (MemberAddress2)) + 
		CONVERT(char(32), (MemberAddress3)) + 
		CONVERT(char(32), (MemberCity)) + 
		CONVERT(char(2), (MemberState)) + 
		CONVERT(char(11), (MemberPostalCode)) + 
		CONVERT(char(32), ('')) + 
		CONVERT(char(32), Payee.Name) + 
		CONVERT(char(32), Payee.Address1) + 
		CONVERT(char(32), Payee.Address2) + 
		CONVERT(char(32), Payee.Address3) + 
		CONVERT(char(32), Payee.City) + 
		CONVERT(char(2), Payee.State) + 
		CONVERT(char(11), Payee.PostalCode) + 
		CONVERT(char(32), Payee.Phone) + 
		CONVERT(char(32), UsersAccountAtPayee) + 
		CONVERT(char(32), (NameOnAccount)) + 
		CONVERT(char(64), memo ) + 
		CONVERT(char(12), PayeeListID) + 
		CONVERT(char(22), MemberID) +
		CONVERT(char(10), '') 
	FROM PAYMENT_HISTORY  
      INNER JOIN PAYEE  ON PAYMENT_HISTORY.PayeeID = Payee.PayeeID
	WHERE 
		PAYMENT_HISTORY.RunID = @RunID AND 
		PAYMENT_HISTORY.StatusCode = 101 AND
		PAYMENT_HISTORY.ProviderID = @ProviderID

--  Calculate file and batch footer information
SELECT 
	@NumPayments = ISNULL(COUNT(PaymentID), 0), 
	@TotalAmount = ISNULL(SUM(Convert(money,Amount))/100, 0) 
FROM PAYMENT_HISTORY  
WHERE 
	PAYMENT_HISTORY.RunID = @RunID AND 
	PAYMENT_HISTORY.StatusCode = 101 AND
	PAYMENT_HISTORY.ProviderID = @ProviderID

-- Batch footer
-- 'BT', NumPayments char(10), TotalAmount char(15)

INSERT INTO TEMP_REQUESTS
	VALUES ('BT' + CONVERT(char(10), @NumPayments) + REPLACE(STR(@TotalAmount, 15, 2) , ' ', '0'))

-- File Footer
INSERT INTO TEMP_REQUESTS
	VALUES ('FT' + CONVERT(char(10), @NumPayments) + REPLACE(STR(@TotalAmount, 15, 2) , ' ', '0') + CONVERT(char(10), 1) + REPLICATE(' ', 16))


SET @FileName = @OutFilePath + RTRIM(@ImmediateDestination)  + '_' + @PaymentFiletype +'_'+ REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') + '.' + @Extension

SET @Message = 'Could not create WRGBPS payment request file.'
SET @SQL = 'bcp "SELECT Request_Data FROM ' + DB_Name() + '.dbo.TEMP_REQUESTS ORDER BY Request_ID" queryout "' + @FileName + '" -c -S' + @@SERVERNAME  + ' -T'
EXEC @rc = master..xp_cmdshell @SQL
IF @rc <> 0
	GOTO spError

-- Make sure the file was created
SET @Message = 'Payment request file was not created..  File = ' + @FileName
IF dbo.udfPathExists ( @FileName ) = 0
	GOTO spError    -- Then it exists, otherwise you get a zero

-- Drop the temp table
IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_REQUESTS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_REQUESTS]

EXEC BPG_UpdateGeneratorStatus @RunID, 11, 'WRGBPS Payment File Completed'


RETURN 0

spError:
	EXEC spLog @Message, 'BPE_CreateWRGPaymentFile'
	RETURN 1

END

