-- =============================================
-- Author:		Helen Liou
-- Create date: 8/23/2006
-- Description:	This sp selects all the recurring payments that are due and creates 
--				individual payments for each one due.  If the recurring model has 0
--				left to process, then it will email the member to inform them that the 
--				payment model has ended.
-- Parameters:	CUID : Credit Union routing ID
--				RunID : Current runid
--				EffectiveDate : Date for which payments will be processed, only in the past
--				Multicycle : Flay to determine if this is a multicycle run or not (currently not used)
--  ------------------------------------------------------------------
--  Modifications: 
--   09/12/2007  GNordstrom  Added selection of new field TrsfSource 
--                           to insert into Payment table. 
--   12/18/2007  HLiou		 Changed the statuscode to 101 for the payments that are processing from 
--                           recurring payment table to the payments table.
--   12/16/2008	 HLiou		 Added check to make sure User Payee was active as well as the payment being active
--   03/25/2009  GNordstrom  Added pulling memo field from recurring payment table and passing it to the payment table.
--   06/19/2009  GNordstrom  Added code to reset the day of the calculated NextProcessDate in order to maintain the same
--                                                    processing day if that processing day is a valid day of the calculated NextProcessDate month and year.         
--   04/28/2013	Hliou		Added Delivery Date field
-- =============================================
CREATE PROCEDURE [dbo].[BPG_GenerateRecurringPayment]
@CUID varchar(9),
@RunID int,
@EffectiveDate datetime = NULL,
@MultiCycle int = 0
AS

BEGIN
/*  This stored procedure is called to create one time payments from the recurring_payments table model.
 	1.  Select all recurring payments that have a nextduedate <= today.
	2.  Insert a row into the payments table.  If the model has no more payments to generate, set payments to 0 and send email to 

member.
	3.  Update the nextduedate and the payment count

*/
SET NOCOUNT ON

DECLARE	@Account varchar(40), 
        @AltEmailAddress varchar(80),
        @Frequency varchar(20), 
        @MemberID varchar(22), 
        @Message varchar(255),
        @PayeeListID varchar(12), 
        @PaymentID varchar(10), 
        @RecipientAddress varchar(255), 
        @RecPaymentID varchar(10), 
        @SPName varchar(255),
        @SourceApplication varchar(20), 
        @TrsfSource varchar(22), 
        @Memo varchar(50) , 
        @ProcessDay varchar(2),
		@PaymentMethod char(1) 

DECLARE	@Amount int, 
        @CompletedCount int,
        @NumPayments int, 
        @PaymentCount int, 
        @PaymentsProcessed int, 
        @rc int,
        @RunStatus int

DECLARE	@CurrentDate datetime,
        @NextProcessDate datetime,
		@DeliveryDate datetime,
		@NextDeliveryDate datetime
		

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))

SET @PaymentCount = 0
SET @CompletedCount = 0
SET @RecipientAddress = ''
SET @RunStatus = 5

-- Get configuration information here
SELECT @AltEmailAddress = ISNULL(ConfigValue ,'hliou@wescom.org')
	FROM Configuration 
	WHERE ConfigName = 'EmailMemberServicesAddress' 

EXEC @rc = BPG_InsertGeneratorStatus @RunID, @RunStatus, '' 

IF @EffectiveDate  is NULL
	SET @CurrentDate = GETDATE()
ELSE
	SET @CurrentDate = @EffectiveDate

DECLARE @RecPayments TABLE (RecPaymentID varchar(10), PayeeListID varchar(12), MemberID varchar(22), 
                            Account varchar(40), Amount int, NumPayments int, Frequency varchar(30),
					        SourceApplication varchar(50), PaymentsProcessed int, NextProcessDate datetime, 
					        TrsfSource varchar(22), Memo varchar(50), ProcessDay varchar(2), 
							PaymentMethod char(1), DeliveryDate datetime )

INSERT INTO @RecPayments
	SELECT
		RecPaymentID,  
		R.PayeeListID,
		R.MemberID, 
		Account,
		Amount,
		NumPayments, 
		Frequency,
		SourceApplication,
		PaymentsProcessed,
		NextProcessDate, 
		TrsfSource, 
		Memo, 
		ProcessDay,
		U.PaymentMethod,
		DeliveryDate   
	FROM Recurring_Payment R WITH (NOLOCK) INNER JOIN User_Payee U WITH (NOLOCK) ON R.PayeeListID = U.PayeeListID
	WHERE NextProcessDate <= @CurrentDate and R.Active = 'Y' and U.Active = 'Y' --and CUID = @CUID

SELECT @PaymentCount = Count(RecPaymentID) FROM @RecPayments

SET @Message = 'Checking ' + Convert(varchar,@PaymentCount) + ' recurring payments to see what has come due'
EXEC @rc = BPG_UpdateGeneratorStatus @RunID, @RunStatus, @Message
EXEC spLog @Message, @SPName, @RunID
SET @Message = 'Phase 1 processing began at : ' + Convert(varchar,GETDATE())
EXEC spLog @Message, @SPName, @RunID
SET @Message = 'Processing for effective date of :' + Convert(varchar,@CurrentDate)
EXEC spLog @Message, @SPName, @RunID

SET @PaymentCount = 0

NextPayment:

	SELECT TOP 1 @RecPaymentID= RecPaymentID, 
				                @PayeeListID= PayeeListID, 
				                @MemberID= RTRIM(MemberID),
				                @Account = Account,
				                @Amount = Amount,
				                @NumPayments = NumPayments, 
		 		                @Frequency= Frequency,
				                @SourceApplication=SourceApplication,
				                @PaymentsProcessed = PaymentsProcessed,
				                @NextProcessDate = NextProcessDate, 
				                @TrsfSource = TrsfSource, 
		                        @Memo = Memo, 
		                        @ProcessDay = ProcessDay,
								@PaymentMethod = PaymentMethod,
								@DeliveryDate = DeliveryDate    
	FROM @RecPayments

	IF @@ROWCOUNT = 0
		GOTO Done

--***************  Need to figure out how to get ID by CU *****************
--  Get a new paymentid
	SET @Message = 'Error when getting a new PaymentID'
	EXEC @rc = GetID 'PaymentID', @PaymentID OUTPUT
	IF @rc != 0
		GOTO spError
	
--  Create a record and insert into the payments table with the infomation obtained from the recurring payments table
	INSERT INTO Payment (PaymentID, PayeeListID, Account, Amount, WillProcessDate, RecPaymentID, StatusCode, Memo, 
	                     BillReference, LastUpdateDateTime, MemberID, SourceApplication, EntryDate, DueDate, TrsfSource,
						 DeliveryDate )-- , CUID )
	VALUES (@PaymentID, @PayeeListID, @Account, @Amount, @NextProcessDate, @RecPaymentID, 101, @Memo, 
	        '',@CurrentDate, @MemberId, @SourceApplication,@CurrentDate, @NextProcessDate, @TrsfSource, 
			@DeliveryDate)  -- , @CUID )

	SET @Message = 'Recurring Payment ID: ' + @RecPaymentID + Convert(varchar,@PaymentsProcessed) + '/' + 
		Convert(varchar, @NumPayments) + ' due (' + Convert(varchar(10), @NextProcessDate, 101) + ') generates payment ' + 

@PaymentID
		
	EXEC spLog @Message, @spName, @RunID

	SET @PaymentsProcessed = @PaymentsProcessed + 1
	SET @PaymentCount = @PaymentCount + 1

--  Enter a Log with the payment information
--  Check if the payment model has completed, Paymentsprocess >= Numpayments
	SET @NextProcessDate = 
	CASE 
		WHEN @Frequency = 'WEEKLY' 	THEN DATEADD(week, 1, @NextProcessDate)
		WHEN @Frequency = 'BIWEEKLY' 	THEN DATEADD(week, 2, @NextProcessDate)
		WHEN @Frequency = 'FOURWEEKS' 	THEN DATEADD(week, 4, @NextProcessDate)
--		WHEN (@Frequency = 'TWICEMONTHLY' AND DATEPART(day, @NextProcessDate) <= 15)
--							THEN DATEADD(day, 15, @NextProcessDate)
--		WHEN (@Frequency = 'TWICEMONTHLY' AND DATEPART(day, @NextProcessDate) > 15)
--							THEN DATEADD(month, 1, DATEADD(day, -15, @NextProcessDate))
		WHEN @Frequency = 'MONTHLY' 	THEN DATEADD(month, 1,  @NextProcessDate)
		WHEN @Frequency = 'BIMONTHLY' 	THEN DATEADD(month, 2, @NextProcessDate)
		WHEN @Frequency = 'QUARTERLY' 	THEN DATEADD(quarter, 1, @NextProcessDate)
		WHEN @Frequency = 'SEMIANNUALLY' THEN DATEADD(month, 6, @NextProcessDate)
		
		WHEN @Frequency = 'ANNUALLY' 	THEN DATEADD(year, 1, @NextProcessDate)
		ELSE	'1/1/2079' --  Set to an invalid date if frequency doesnt equal any of these
	END
	
	IF ((@ProcessDay IS NOT NULL) AND (@Frequency = 'MONTHLY' OR @Frequency = 'BIMONTHLY' OR @Frequency = 'QUARTERLY' OR @Frequency = 'SEMIANNUALLY'))
	BEGIN
      --Start the loop to reset the calculated NextProcessDate using the ProcessDay.
      --This is done in order to return the payment process date to the original ProcessDay because there are some months with fewer days then other months.
     --For example:  if the ProcessDay was originally set to the 31st of a month it would be changed on those months that only have 30 days and on Feburary 
      --which only has 28 days or 29 days every four years.  So this process will return it to the 31st for those months that have 31 days, etc.   
      DECLARE @Error  Bit, @Break Bit 
      SET @Break = 0 
      WHILE @Break = 0
      BEGIN 
         BEGIN TRY
            --Reset the error indicator to zero
            SET @Error = 0 
            --Try to set the NextProcessDate  to the month and year of the caculated NextProcessDate  and the ProcessDay as the day  
            SET @NextProcessDate = CONVERT(smalldatetime, (CONVERT(varchar, MONTH(@NextProcessDate)) + '/' + @ProcessDay + '/' + CONVERT(varchar, YEAR(@NextProcessDate))))
         END TRY    
         
         BEGIN CATCH 
            --If setting the NextProcessDate using the ProcessDay results in an invalid date then the error 
            --number will be 296 which is "...resulted in an out-of-range smalldatetime value."; the ProcessDay
            --will be recalculated by subtracting one and then looping back to try setting the NextProcessDate again.  
            IF ERROR_NUMBER() = 296
            BEGIN  
               SET @ProcessDay = (CONVERT(int, @ProcessDay) - 1)
               SET @Error = 1 
            END
               
         END CATCH 
         -- If the error indicator is 0 then the NextProcessDate is valid so get out of the loop.
         IF (@Error = 0)
            SET @Break = 1
      END 	
	END

	IF @NumPayments > 0	-- Indicates indefinite payments
	BEGIN
		IF @PaymentsProcessed >= @NumPayments  --  Model has ended
		BEGIN
			UPDATE Recurring_Payment 
			SET	Active = 'N',
				NextProcessDate = '1/1/2079',
				NextDueDate = '1/1/2079',
				DeliveryDate = '1/1/2079',
				PaymentsProcessed = @PaymentsProcessed,
				LastUpdateDateTime = @CurrentDate,
				LastProcessedDate = @CurrentDate
			WHERE RecPaymentID = @RecPaymentID

			DECLARE @tmpMember TABLE (MemberID varchar(32), First varchar(20),
				Middle varchar(10), Last varchar(40), Street varchar(40), Street2 varchar(40),
				City varchar(40), State varchar(10), Zip varchar(10), Country varchar(20),
				Email varchar(40), HomePhone varchar(16), Work varchar(16), ext varchar(6),
				err int, errdesc varchar(500) )

--  Get members email address to send the email about the ending of the recurring payment model.

			INSERT INTO @TmpMember
				EXEC BPG_GetMemberHostAccountInfo @MemberID--, @RunID

			SELECT @RecipientAddress = Email
				FROM @TmpMember
				WHERE MemberID = @MemberID

			EXEC @rc = spSendMail 3000, @RecipientAddress, @RecPaymentID
			SET @CompletedCount = @CompletedCount + 1

		END
		ELSE
		BEGIN
			EXEC wc_GetPayeeDeliveryDate @NextProcessDate, @PaymentMethod, @NextDeliveryDate OUTPUT
			UPDATE Recurring_Payment 
			SET	NextProcessDate = @NextProcessDate, 
				NextDueDate = @NextProcessDate,
				PaymentsProcessed = @PaymentsProcessed,
				LastProcessedDate = @CurrentDate,
				DeliveryDate = @NextDeliveryDate
			WHERE RecPaymentID = @RecPaymentID
		END
	END
	ELSE
	BEGIN
		EXEC wc_GetPayeeDeliveryDate @NextProcessDate, @PaymentMethod, @NextDeliveryDate OUTPUT

		UPDATE Recurring_Payment 
		SET NextProcessDate = @NextProcessDate,
			NextDueDate = @NextProcessDate,
			PaymentsProcessed = @PaymentsProcessed,
			LastProcessedDate = @CurrentDate,
			DeliveryDate = @NextDeliveryDate
		WHERE RecPaymentID = @RecPaymentID
	END
	
	DELETE FROM @RecPayments WHERE RecPaymentID = @RecPaymentID

--  Get the next payment in the table
	GOTO NextPayment
			
Done:

SET @Message = 'Payments generated = ' + CONVERT(varchar(20), @PaymentCount) + ', Recurring payment model ended (last payment generated) = ' 

+ CONVERT(varchar(20), @CompletedCount) + '.'
EXEC spLog @Message, @SPName, @RunID

SET @Message = 'Recurring Payment Processing completed successfully'
EXEC spLog @Message, @SPName, @RunID
--  Log completion message, with summary of number of model ending and number of payments processed
--UPDATE Configuration SET ConfigValue = 'Recurring Payments Complete' WHERE ConfigName = 'RunStatus'

EXEC @rc = BPG_UpdateGeneratorStatus @RunID, @RunStatus, NULL

RETURN 0

spError:
	EXEC spLog @Message ,@SPName
	return -1

END


