		WHEN @Frequency = 'FOURWEEKS' 	THEN DATEADD(week, 4, @NextDueDate)
--		WHEN (@Frequency = 'TWICEMONTHLY' AND DATEPART(day, @NextProcessDate) <= 15)
--							THEN DATEADD(day, 15, @NextProcessDate)
--		WHEN (@Frequency = 'TWICEMONTHLY' AND DATEPART(day, @NextProcessDate) > 15)
