		WHEN @Frequency = 'MONTHLY' 	THEN DATEADD(month, 1, @NextDueDate)
		WHEN @Frequency = 'BIMONTHLY' 	THEN DATEADD(month, 2, @NextDueDate)
		WHEN @Frequency = 'QUARTERLY' 	THEN DATEADD(quarter, 1, @NextDueDate)
		WHEN @Frequency = 'SEMIANNUALLY' THEN DATEADD(month, 6, @NextDueDate)
		WHEN @Frequency = 'ANNUALLY' 	THEN DATEADD(year, 1, @NextDueDate)
		ELSE	'1/1/2079' --  Set to an invalid date if frequency doesnt equal any of these
	END
	
   IF ((@ProcessDay IS NOT NULL) AND (@Frequency = 'MONTHLY' OR @Frequency = 'BIMONTHLY' OR @Frequency = 'QUARTERLY' OR @Frequency = 'SEMIANNUALLY'))
   BEGIN
      --Start the loop to reset the calculated NextDueDate using the ProcessDay.
