            --Try to set the NextDueDate  to the month and year of the caculated NextDueDate  and the ProcessDay as the day  
            SET @NextDueDate = CONVERT(smalldatetime, (CONVERT(varchar, MONTH(@NextDueDate)) + '/' + @ProcessDay + '/' + CONVERT(varchar, YEAR(@NextDueDate))))
         END TRY    
         
         BEGIN CATCH 
            --If setting the NextDueDate  using the ProcessDay results in an invalid date then the error 
            --number will be 296 which is "...resulted in an out-of-range smalldatetime value."; the ProcessDay 
            --will be recalculated by subtracting one and then looping back to try setting the NextDueDate again.  
            IF ERROR_NUMBER() = 296
            BEGIN  
               SET @ProcessDay = (CONVERT(int, @ProcessDay) - 1)
               SET @Error = 1 
            END
               
         END CATCH 
         -- If the error indicator is 0 then the NextDueDate is valid so get out of the loop.
         IF (@Error = 0)
            SET @Break = 1
      END 	
   END

	IF CONVERT(varchar,@NextDueDate,112) = '20790101'   --  Invalid date cause frequency was bad
	BEGIN
		SET @Message = 'Invalid Frequency for processing recurring paymentID: ' + @RecpaymentID
		EXEC spLog @Message, @SPName, @RunID
