-- =============================================
-- Author:		<Author,Aili Cheng>
-- Create date: <12/08/2006>
-- Description:	<Update job step's of "BillPay Manual Run", start this job manually >
--    04/11/2007  GNordstrom  Added execution context for msdb sps.
--    10/25/2007  GNordstrom  Modified to execute the correct sp if the generator 
--                            type is Batch.  Modified to obtain the Execute AS 
--                            login id from the configuration table.
--    08/11/2009 GNordstrom  Added check of job enabled status -- 
--                                                    so that job is only run if it is enabled.
-- =============================================
CREATE PROCEDURE [dbo].[BPG_GeneratorManualRun]
                        @RunDate DateTime,
                        @Message varchar(255) output
AS

SET NOCOUNT ON

--declare variable
DECLARE @rc int,
		@SPName varchar(50), 
		@command varchar(100),
		@JobName varchar(30),
		@Type    varchar(50),  
		@GenID varchar(50) 

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))

--Get job name from configuration table
SELECT @JobName = ConfigValue
	FROM dbo.Configuration WITH (NOLOCK) 
	WHERE ConfigName = 'BPManualRun'
--Get the generator type:  batch or realtime.
SELECT @Type = ISNULL(ConfigValue, '') 
   FROM Configuration WITH (NOLOCK) 
   WHERE UPPER(configName) = UPPER('GeneratorType')
--Get the email profile name 	
SELECT @GenID = ConfigValue 
   FROM Configuration WITH (NOLOCK) 
   WHERE UPPER(configName) = UPPER('EmailProfile')

IF (@JobName is null OR @JobName = '')
BEGIN
     SET @Message = 'Error: No such job name in Configuration table!'
     EXEC spLog @Message, @SPName
     return 2
END
ELSE -- if there is job in configuration table
BEGIN --#1
		--then check if the job exists in the job agent
   EXECUTE AS LOGIN = @GenID
	EXEC @rc = msdb.dbo.sp_help_job @job_name = @JobName, @job_aspect = N'job'
	REVERT 
	
 	IF @rc != 0
	BEGIN
	  SET @Message = 'Error: The specified ' + @JobName + ' does not exist!'
      EXEC spLog @Message, @SPName
      return 1
	END	
    ELSE -- if job name does exist...
    BEGIN -- #2
        --Check if the job is enabled 
        DECLARE @JobEnabled bit  
        EXECUTE AS LOGIN = @GenID
            SELECT @JobEnabled = [enabled] FROM [msdb].[dbo].[sysjobs_view] WHERE [name] = @JobName
        REVERT 
        
        IF (@JobEnabled = 0)
        BEGIN
            --Job is disabled so do not run
	        SET @Message = 'Error:  Job is currently disabled!'
           EXEC spLog @Message, @SPName
           RETURN 1
        END
        ELSE 
        BEGIN    -- #3 
            --Set the command variable based on the generator type.
            IF UPPER(@Type) = UPPER('Batch')
               SET @command = 'EXEC dbo.BPEngRun ''' + CONVERT(varchar, @RunDate, 112)+ ''''
		      ELSE 
               SET @command = 'EXEC dbo.BPG_Run '''', ''' + CONVERT(varchar, @RunDate, 112) + ''''
      		  
		        --update job command
              EXECUTE AS LOGIN = @GenID
		        EXEC  @rc = msdb..sp_update_jobstep 
				         @job_name = @JobName,
				         @step_id = 1,
				         @step_name = N'Manual Run',
				         @command = @command		
		        REVERT 
      		  		
		        IF @rc != 0
		        BEGIN
				      -- Log error and end run
				      SET @Message = 'ERROR: Unable to update job step, return code = ' + CONVERT(varchar, @rc)
				      EXEC spLog @Message, @SPName
				      RETURN -1
		        END
		        ELSE --if update job successfully, start to run the job...
		        BEGIN
				      --run the job
                  EXECUTE AS LOGIN = @GenID
				      EXEC @rc = msdb..sp_start_job 
					        @job_name = @JobName,
					        @step_name = N'Manual Run'
				      REVERT
      				
				      IF @rc != 0
				      BEGIN
					      -- Log error and end run
					      SET @Message = 'ERROR: Unable to start job, return code = ' + CONVERT(VARCHAR, @rc)
					      EXEC spLog @Message, @SPName
					      RETURN -2
				      END	
		         END
        END --end of #3
	  END --end of #2
END --end of #1


