/*
 ==================================================================
  SP Name:     BPG_GetAccountsForPayments

  Description: Obtains the distinct member id and account numbers 
               that have payments to be processed for the date 
               passed in. 	

  Resources: 	Payment
  ------------------------------------------------------------------
  Accepts:     @effDate smalldatetime
  
  Returns:     DISTINCT RTRIM(MemberID) AS MemberID, 
                     RTRIM(Account) AS Account,
                     RIGHT(RTRIM(Account), @IDLength) AS AccountID  
   ==================================================================
   Created:	   07/28/2006
   By:       	GNordstrom
  ------------------------------------------------------------------
  Modifications:  HLiou   1/28/2008 - Look for payments with status code of 101 and 100
==================================================================
*/
CREATE PROCEDURE [dbo].[BPG_GetAccountsForPayments]
		               @effDate smalldatetime
AS

BEGIN TRY 
   DECLARE @IDLength smallint 
   SELECT @IDLength = ConfigValue 
   FROM Configuration WITH (NOLOCK) 
   WHERE UPPER(ConfigName) = 'ACCOUNTSUFFIXLENGTH'
   
   IF @IDLength > 0 
      BEGIN
         SET @IDLength = (@IDLength + 1)
      
         SELECT DISTINCT RTRIM(MemberID) AS MemberID, 
                     RTRIM(Account) AS Account,
                     RIGHT(RTRIM(Account), @IDLength) AS AccountID  
         FROM Payment WITH (NOLOCK) 
         WHERE StatusCode in (101, 100)
            AND DATEDIFF(day, WillProcessDate, @effDate) >= 0
         ORDER BY MemberID, AccountId 
      END
      
END TRY 
BEGIN CATCH

