   ==================================================================
   Created:  	04/18/2007
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
   07/20/2007  GNordstrom  Modified to set job name based on generator type.               
===================================================================
*/
CREATE PROCEDURE [dbo].[BPG_GetGeneratorJobStatus] 
                  @CUName varchar(32), 
                  @JobEnabled bit OUTPUT
AS 
 
   DECLARE @jobName varchar(100), 
           @Type    varchar(50) 
           
   --Get the generator type:  batch or realtime.
   SELECT @Type = ISNULL(ConfigValue, '') 
      FROM Configuration WITH (NOLOCK) 
      WHERE UPPER(configName) = UPPER('GeneratorType')

   --Set the jobname based on the cu name and generator type.
   IF UPPER(@Type) = UPPER('Batch')
      SET @jobName = @CuName + ' Batch' 
   ELSE
      SET @jobName = @CuName + ' Generator'

   DECLARE @QueryResult int  
   --Determine if the job is enabled.
   SELECT @QueryResult = [Enabled] 
      FROM  msdb.dbo.sysjobs_view 
      WHERE [name] = @jobName
      
   --If the job is enabled
   IF @QueryResult > 0 
      BEGIN
         DECLARE @tmpJob TABLE (StepID int, [Name] sysname, SubSys nvarchar(40), Command nvarchar(3200), 
                           Flags nvarchar(4000), SuccessCode int, SuccessAction nvarchar(4000), SuccessStep int, 
                           FailAction nvarchar(4000), FailStep int, [Server] sysname NULL, DBName sysname, DBUser sysname NULL,
                           RetryAttempts int, RetryInt int, Priority varchar(4000), OutFile varchar(200) NULL, 
                           LRResult int, LRDuration int, LRRetries int, LRDate int, LRTime int, ProxyID int NULL)
         --Determine if the job has a job step
         INSERT INTO @tmpJob 
            EXEC msdb.dbo.sp_help_job @job_name = @jobName, @job_aspect = 'STEPS'
            
         SELECT @QueryResult = (@QueryResult * COUNT(StepID)) 
            FROM @tmpJob

         --If the job is enabled and has at least one job step
         IF @QueryResult > 0 
            BEGIN
               DECLARE @tmpSch TABLE (ID int, [Name] sysname, [Enabled] int, FreqType int, FreqInt int, FreqSDType int,
                                 FreqSDInt int, FreqRelInt int, FreqRecur int, ActiveStartDate int, ActiveEndDate int, 
                                 ActiveStartTime int, ActiveEndTime int, DateCreated datetime, [Desc] nvarchar(4000), 
                                 NextRunDate int, NextRunTime int, [UID] uniqueidentifier, JobCount int) 
               --Determine if the job has at least one enabled shedule
               INSERT INTO @tmpSch 
                  EXEC msdb.dbo.sp_help_job @job_name = @jobName, @job_aspect = 'SCHEDULES'
                  
               SELECT @QueryResult = @QueryResult * SUM([Enabled]) 
                  FROM @tmpSch 
            END
      END

   --If the job is enabled and has at least one job step and at least one enabled schedule 
   IF @QueryResult > 0 
      --Return job is enabled
      SET @JobEnabled = 1
   ELSE 
      --Return job is not enabled
      SET @JobEnabled = 0



GO
