/*
 ==================================================================
  SP Name:        BPG_GetRunInfo

  Description: 	Obtains the generator run status information.

  Resources: 	   RunLog
                  GeneratorStatus
                  GeneratorStatusCodes
                  BPG_GetGeneratorJobStatus 
                  Configuration
  ------------------------------------------------------------------
  Accepts:		   @CuName varchar(32)

  Returns:		   RTRIM(rl.ID) as RunID, 
                  gs.StatusID, 
                  c.Description, 
                  gs.Message, 
                  c.ProcName, 
                  gs.StartTime, 
                  gs.EndTime, 
                  dbo.BPG_udfElapsedTimeFromSeconds(DateDiff(ss, gs.StartTime, gs.EndTime)) as ElapsedTime, 
                  dbo.BPG_GetGeneratorJobStatus(@CuName) as JobEnabled, 
                  (SELECT ConfigValue FROM Configuration WHERE UPPER(ConfigName) = 'SKIPHOLIDAY') as SkipHoliday 
   ==================================================================
   Created:  	08/10/2006
   By:    		GNordstrom	
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE  PROCEDURE [dbo].[BPG_GetRunInfo]
                     @CuName varchar(32) 
AS

SET NOCOUNT ON

DECLARE @JobEnabled bit 
EXEC BPG_GetGeneratorJobStatus @CUName, @JobEnabled OUTPUT

SELECT   RTRIM(rl.ID) as RunID, 
         gs.StatusID, 
         c.Description, 
         gs.Message, 
         c.ProcName, 
         gs.StartTime, 
         gs.EndTime, 
         dbo.BPG_udfElapsedTimeFromSeconds(DateDiff(ss, gs.StartTime, gs.EndTime)) as ElapsedTime, 
         @JobEnabled as JobEnabled, 
         (SELECT ConfigValue FROM Configuration WHERE UPPER(ConfigName) = 'SKIPHOLIDAY') as SkipHoliday 
FROM GeneratorStatus gs WITH (NOLOCK) 
         INNER JOIN GeneratorStatusCodes c WITH (NOLOCK) ON gs.StatusID = c.ID 
         INNER JOIN RunLog rl WITH (NOLOCK) ON gs.RunID = rl.ID 
WHERE gs.RunID = (SELECT TOP 1 ID FROM RunLog ORDER BY DateProcessed DESC)
ORDER BY gs.StartTime DESC 

