-- Author:		Helen Liou
-- Create date: 10/10/2006
-- Description:	Processed cleared check file, updates the Payment_Clear table with the date the check 
--				clears.
--
-- Modification: HLiou	2/29/2008  - Modify the check for files found in the Infiles folder.
--				 HLiou	6/15/2010	 Modified the return when no files are found to sucesse.
-- ===================================================================================================
CREATE PROCEDURE [dbo].[BPG_ImportCheckCleared]
AS

BEGIN

SET NOCOUNT ON

DECLARE @ErrorCode int, 
		@rc int, 
		@StatusCode int, 
		@count int, 
		@processed int, 
		@failed int

DECLARE @PaymentID varchar(10), 
		@CURouting varchar(9),
		@SQL varchar(1000),
		@CCFile varchar(255),	
		@CCFileName varchar(255),
		@CCDirPath varchar(255),
		@CCFormat varchar(255),
		@ArchiveDirPath varchar(255),
		@Temp varchar(1000),
		@ErrorMessage varchar(2000),
 		@ArchiveFilename varchar(255),
		@SPName varchar(255), 
		@CCAddress varchar(255),
		@Subject varchar(255),
		@msgtext varchar(255),
		@AdminAddress varchar(255),
		@CheckNumber varchar(12),
		@ChNum	varchar(12),
		@ClearedDate varchar(8),
		@filefound  varchar(50), 
		@CCFileDir varchar(50),
		@CCExtension varchar(5)

DECLARE @Date smalldatetime


SET @CCFileDir = ''
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))

SELECT @CCFileDir = ISNULL(ConfigValue, 'ClearedCheckFiles') FROM Configuration WHERE ConfigName = 'ClearChecksPath'
SELECT @CCExtension = ISNULL(ConfigValue,'CC') FROM Configuration WHERE ConfigName = 'ClearedCheckExtension'
SELECT @CURouting = ISNULL(ConfigValue, '') FROM Configuration WHERE ConfigName = 'CURoutingNumber'

SET @ErrorMessage = 'Invalid rowcount in the Providers table'
SELECT	@CCDirPath = RTRIM([InBoundPath]),  
		@ArchiveDirPath = InBoundPath + @CCFIleDir + '\'
	FROM Provider WHERE ProviderName = 'WRGBPS'

IF @@ROWCOUNT <> 1
	GOTO spError

SET @SQL = @CURouting + '*.' + @CCExtension

CREATE TABLE #CCFiles(C1 varchar(256)) INSERT INTO #CCFiles 
	SELECT * FROM dbo.udfGetDirectoryFiles (@CCDirPath, @SQL)

DELETE #CCFiles WHERE (C1 IS NULL)
IF @@ERROR <> 0
	GOTO spError

SELECT @filefound = C1 FROM #CCFILES
--IF @filefound = 'File Not Found'
IF @@RowCount = 0
	GOTO noFile

-- Check to see if archive directory exists, if not, create it
SET @ErrorMessage = 'Archive Directory is invalid'
IF dbo.udfPathExists( @ArchiveDirPath ) = 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
	IF dbo.udfCreateDirectory (@ArchiveDirPath) = 0 
		GOTO spError
END

-- Create the request format file
SET @ErrorMessage = 'Could not create request format file'
SET @CCFormat = @CCDirPath + '~~CC_IN.FMT'
IF dbo.udfCreatePCFF( @CCFormat ) = 0
	GOTO spError

DECLARE cCCFiles CURSOR FOR SELECT C1 FROM #CCFiles
OPEN cCCFiles

FETCH NEXT FROM cCCFiles INTO @CCFileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @CCFile = @CCDirPath + @CCFileName

	-- Make sure the file exists in case of File Not Found is returned from dir command
	IF dbo.udfPathExists ( @CCFile ) = 0
	IF @rc <> 0    -- Then it does not exist, otherwise you get a zero
		GOTO spDone

	-- Create a temp table
	IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CHECK]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
		DROP TABLE [dbo].[TEMP_CHECK]

	CREATE TABLE [dbo].[TEMP_CHECK]
	(
		[ID] [int] IDENTITY (1, 1) NOT NULL ,
		[Data] [varchar] (1000) NOT NULL 
	) ON [PRIMARY]

	TRUNCATE TABLE TEMP_CHECK

	-- Load the PC file into the Transit_Payment_Requests table
	SET @ErrorMessage = 'Error inserting rows into TEMP_CHECK table from cleared check file: ' + @CCFile + ',  format file = ' + @CCFormat
	SET @SQL = 'BULK INSERT TEMP_CHECK FROM ''' + @CCFile + ''' WITH (FORMATFILE = ''' + @CCFormat + ''' , ROWTERMINATOR = ''\r\n'' ,TABLOCK)'
	EXEC (@SQL)
	IF @@ERROR <> 0
		GOTO spContinue

-- Insert the rows into the cu table with the log id

	SET @ErrorMessage = 'Importing the Cleared check file: '+@CCfile 
	EXEC @rc = spLog @ErrorMessage,@SPName
	SET @Failed = 0
	SET @Processed = 0
	
	DECLARE pc CURSOR FOR
		SELECT Data
		FROM TEMP_CHECK
		ORDER BY [ID]
	OPEN pc
	FETCH NEXT FROM pc
	INTO @Temp
	WHILE @@FETCH_STATUS = 0
	BEGIN
		IF LEFT(@Temp,2)= 'BH'
			BEGIN
--				SET @RoutingID = SUBSTRING(@Temp,3,9)
				IF SUBSTRING(@Temp,3,9) != @CURouting
					BEGIN 
						SET @ErrorMessage = 'Invalid Routing Number in Check Cleared File '+@CCFile
						EXEC @rc = spLog @ErrorMessage, @SPName
						SET @StatusCode = 2000  --CU defined status code range
--						EXEC @rc = spSendGeneralMail @StatusCode, @ErrorMessage
						IF @rc <> 0
							GOTO spError
						GOTO spSend
					END
			END
	
		IF LEFT(@Temp,2) = 'TX'
			BEGIN
				SET @PaymentID	= REPLACE(STR(SUBSTRING(@Temp,3,10), 10, 0), ' ', '0')
--print @paymentid
				SELECT @CHNum = RTRIM(CheckNum) 
					FROM PAYMENT_HISTORY 
					WHERE paymentID= @paymentid and statuscode in (101,102)
				
				IF @@ROWCOUNT != 1
					BEGIN
						SET @ErrorMessage = 'Unable to find paymentID ' + @PaymentID + ' in the payment history table to confirm the payment.'
						EXEC @rc = spLog  @ErrorMessage, @SPName
						SET @Failed = @Failed + 1
					END
				ELSE	
					BEGIN
						SET @ClearedDate	= SUBSTRING(@Temp,21,8)
						SET @CheckNumber      = RTRIM(SUBSTRING(@Temp,29,12))
						IF @CheckNumber <> @CHNum
							BEGIN
								SET @ErrorMessage = 'Check Number from the check cleared file does not match payment history informtion, for Payment ID: ' +@PaymentID
								EXEC @rc = spLog  @Errormessage, @SPName
								SET @Failed = @Failed + 1
							END
						ELSE
							BEGIN
								IF EXISTS (SELECT PaymentID FROM Payment_Clear WHERE PaymentID = @PaymentID)
									BEGIN
										SET @ErrorMessage = 'Duplicate PaymentID: ' + @PaymentID + ', can not insert into Cleared Check table'
										EXEC @rc = spLog @Errormessage, @SPName 
										SET @StatusCode = 2001  --CU defined status code range
--										EXEC @rc = spSendGeneralMail  @StatusCode, @ErrorMessage
--										GOTO spSend
									END
								ELSE
									BEGIN	
										SET @Processed = @Processed + 1
										INSERT INTO Payment_Clear
											(PaymentID, CheckNum, ClearedDate, NotifyCount, NotifyDate, LastAction, ActionDate)
										VALUES
											(@PaymentID,@CheckNumber, @ClearedDate, NULL, NULL, NULL, NULL)
									END
									
							END
					END
			END

		FETCH NEXT FROM pc INTO @Temp

	END

--	SET @ErrorMessage = 'Could not send summary email to the administrator'
--	EXEC @rc = spSendAdminImportSummaryMessage @AdminAddress, @CCFile, 0,0,@processed, @failed,0, 103
--	IF @rc <> 0
--		GOTO spDone

	EXEC @rc = spLog 'Check Cleared File Processed Successfully',@SPName
			
spSend:
	-- Move the file to the backup directory
	SET @ErrorMessage = 'Error moving CCfile to backup directory: ' + @ArchiveDirPath
	IF dbo.udfMoveFile ( @CCFile, @ArchiveDirPath + @CCFileName) = 0
	BEGIN
		exec spLog @ErrorMessage, @SpName
		GOTO spDone
	END

	CLOSE pc
	DEALLOCATE pc

spContinue:
   	FETCH NEXT FROM cCCFiles INTO @CCFileName

END

spDone:
	CLOSE cCCFiles
	DEALLOCATE cCCFiles

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CHECK]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CHECK]

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#CCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #CCFiles

spExit:
	SET @ErrorMessage = ''
 	RETURN 0

spError:
	EXEC @rc = spLog  @ErrorMessage, @SPName
	RETURN 2

noFile:
	IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#CCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
		DROP TABLE #CCFiles
	RETURN 0

END


