	SET @eCount = 0
	SET @Count = 0

	SET @Message = 'Importing the WRGBPS confirmation file: '+ @PCFile
	EXEC spLog  @Message,@SPName

	DECLARE pc CURSOR FOR
		SELECT Data
		FROM TEMP_CONFIRMATION
		ORDER BY [ID]
	OPEN pc
	FETCH NEXT FROM pc
	INTO @Temp
	WHILE @@FETCH_STATUS = 0
	BEGIN

	IF LEFT(@Temp,2) = 'FH'
		SET @RunID = CONVERT(varchar(10), RTRIM(SUBSTRING(@Temp, 5, 8)))

	IF LEFT(@Temp,2) = 'FT'
		SET @amount = Convert(money, SUBSTRING(@Temp,13,15))
	
	IF LEFT(@Temp,2) = 'TX'
	BEGIN
		SET @PaymentID = REPLACE(STR(SUBSTRING(@Temp,3,10), 10, 0), ' ', '0')
		IF EXISTS (SELECT PaymentID FROM Payment_History WHERE PaymentID = @PaymentID)
		BEGIN
			SET @StatusCode = Convert(int,SUBSTRING(@Temp,13,2))
			IF @StatusCode <> 0 
			BEGIN
				SET @StatusCode = @StatusCode + 1000  -- Add 1000 to get to our status codes
				EXEC @rc = BPG_UpdatePaymentHistory @StatusCode,@PaymentID,' ', ' '
						
				SELECT	@phamount= Amount, 
						@MemberID = PH.MemberID, 
						@RecipientAddress = MemberEmail
					FROM PAYMENT_HISTORY PH 
					WHERE PH.PaymentID = @PaymentID

				IF @RecipientAddress = '' or @RecipientAddress = ' '
				BEGIN
					SET @Message = 'Error Retrieving email address for member : ' + RTRIM(@MemberID) + ', email sent to administrator'
					EXEC spSendMail @StatusCode, @AdminAddress, @PaymentID
					EXEC spLog @Message, @SPName
				END
				ELSE
				BEGIN
					SET @Message = 'Error Sending email to member : ' + RTRIM(@MemberID) + ', email was set to administrator'
					EXEC @rc = spSendMail @StatusCode, @RecipientAddress, @PaymentID
					IF @rc <> 0
						EXEC spLog NULL, @Message, @SPName
				END
				SET @amount = @amount + @phamount
				SET @rCount = @rCount + 1
			END
			ELSE
			BEGIN
				SELECT	@phamount= Amount, 
						@MemberID = PH.MemberID,
						@PayeeListID = PayeeListID, 
						@RecipientAddress = MemberEmail 
					FROM PAYMENT_HISTORY PH 
					WHERE PH.PaymentID = @PaymentID

				SET @subtotal = @subtotal + @phamount    --  Sum amount
				SET @Method = Convert(int,SUBSTRING(@Temp,15,2))
				SET @CheckNum = ' '
				IF @Method = 2
				BEGIN
					SET @CheckNum = RTRIM(SUBSTRING(@Temp,29,12))
					SET @PaymentMethod = 'C'
					SET @cCount = @cCount + 1
				END
				ELSE
				BEGIN
					SET @eCount = @eCount + 1
					SET @PaymentMethod = 'E'
				END
				--  Update the UserPayee payment method and the PaymentHistory payment method to reflect the current method
				BEGIN TRAN
				EXEC @rc = BPG_UpdatePaymentHistory 102, @PaymentID, @CheckNum, @PaymentMethod
				IF @rc != 0
				BEGIN
					ROLLBACK
					SET @Message = 'Error occurred while updating the Payment method for paymentid : '+@PaymentID
					EXEC spLog @Message, @SPName
				END
				ELSE
				BEGIN
					EXEC @rc = BPG_UpdatePayee @PaymentMethod, @PayeeListID
					IF @rc != 0
					BEGIN
						ROLLBACK 
						SET @Message =  'Error occurred updating the User Payee table for Payment ID : ' +  @PaymentID 
						EXEC spLog @Message, @SPName
					END
					COMMIT
				END			
			END	
		END
		ELSE
		BEGIN
			SET @Message = 'Payment ('+@PaymentID+') was not found in Payment History'
			EXEC spLog @Message, @SPName
		END
	END

FETCH NEXT FROM pc INTO @Temp
END	

CLOSE pc
DEALLOCATE pc

--SET @subtotal = @subtotal + @amount --  Add up the successful payment amounts and the reject amounts and  compare to the file amount
--SET @Totalamt = @fileamount - (Convert(money,@amount)/100.)
SET @Count = @eCount + @cCount

--IF @fileamount != convert(money,(@subtotal/100.))
--	EXEC spLog 'FileAmount is different from payment total', @SPName

--Send email with the import summary information
SET @Message = 'Payments confirmed: '+ convert(varchar,@cCount+@eCount)+', payments rejected: '+convert(varchar,@rCount)
EXEC  spLog @Message,@SPName

EXEC @rc = spSendMail 102, @ReportAddress, '', @Message
IF @rc != 0
BEGIN
	SET @Message = 'Could not send summary email to the administrator'
	EXEC spLog @Message, @SPName
	GOTO spContinue
END

-- Move the file to the backup directory
--SET @ArchiveFilename = @ArchiveDirPath + '\' + @CURouting + '_' + @FileType + '_' + REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') + @InExtension
SET @ArchiveFilename = @ArchiveDirPath + @PCFileName
SET @Message = 'Error moving request file to backup directory: ' + @ArchiveFilename
IF dbo.udfMoveFile ( @PCFile, @ArchiveFileName) = 0
	GOTO spContinue

spContinue:
   	FETCH NEXT FROM cPCFiles INTO @PCFileName

END

spDone:
	CLOSE cPCFiles
	DEALLOCATE cPCFiles

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CONFIRMATION]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CONFIRMATION]

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#PCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #PCFiles

spExit:
	SET @Message = ''
 	RETURN 0

spError:
	EXEC @rc = spLog  @Message, @SPName
	RETURN 2

spNoFile:
	IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#PCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #PCFiles
	RETURN 0

END
