-- ==============================================================================================
-- Author:		Helen Liou
--
-- Create date: 2/1/2007
--
-- Description:	This stored procedure will process the confirmation file sent by the ORCC system
--
-- Modification; HLiou		6/15/2010	Changed the return code for no files to process to success.
--
-- ===============================================================================================
CREATE PROCEDURE [dbo].[BPG_ImportORCCConfirmation] AS

BEGIN

DECLARE	@ErrorCode		int,
		@ProviderID		int,
		@rc				int,
		@StatusCode		int,
		@eCount			int,
		@cCount			int,
		@rCount			int,
		@tcount			int,
		@eAmount		int,
		@phamount		int,
		@cAmount		int,
		@rAmount		int,
		@linenum		int,
		@notify			int,
		@tAmount		money,
		@fCount			int,
		@RunID			int

DECLARE	@PaymentID	 	 	varchar(10),
		@MemberID 			varchar(12),
		@SQL				varchar(1000),
		@PCFileName			varchar(255),
		@PCDirPath			varchar(255),
		@PCFile				varchar(255),
		@PCFormat			varchar(255),
		@ListFile			varchar(50),
		@ArchiveDirPath		varchar(255),
		@FileName			varchar(255),
		@Temp				varchar(1000),
		@Message			varchar(2000),
		@ArchiveFilename	varchar(255),
		@CheckNum			varchar(10),
		@SPName				varchar(255),
		@PaymentMethod 		char(1),
		@CURouting			char(9),
		@method 			char(1),
		@FileType			varchar(5),
		@CUID				varchar(10),
		@ClearDate			varchar(8),
		@ProviderName		varchar(30),
		@Extension			varchar(5),
		@PayeeListID		varchar(12),
		@Status				char(1),
		@FileID				varchar(5),
		@ErrorType			varchar(5),
		@ErrorNumber		varchar(15)

DECLARE	@Subject			varchar(255),		-- Email variables
		@RecipientAddress	varchar(255),
		@msgtext			varchar(255),
		@AdminAddress		varchar(255),
		@ReportAddress		varchar(255),
		@MemberSrvAddress	varchar(255)

DECLARE @now				datetime

SET NOCOUNT ON

SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
SET @Now = GETDATE()

EXEC BPG_InsertGeneratorStatus 0, 9, 'Importing Confirmation Files'

SET @Message = 'Invalid rowcount in the Providers table'
SELECT	@ProviderID = [ProviderID],
		@PCDirPath = RTRIM([InBoundPath]), 
		@ProviderName = RTRIM(ProviderName),
		@Extension = InExtension,
		@ArchiveDirPath = ArchiveDirectoryName  
	FROM Provider 
	WHERE ProviderID = 103

IF @@ROWCOUNT != 1
	GOTO spError

--SELECT * from Provider where providerid = 103

SELECT @FileID = ISNULL(ConfigValue,'0') 
	FROM Configuration 
	WHERE ConFigName = 'eComCUID'

SELECT @CURouting = ISNULL(ConfigValue,'999999999') 
	FROM Configuration 
	WHERE ConFigName = 'CURoutingNumber'

SELECT @FileType = ISNULL(ConfigValue, '') 
	FROM Configuration 
	WHERE ConfigName = 'ConfirmationFileExtension'

SET @FileName = @CURouting + '_*.' + @Extension

CREATE TABLE #PCFiles(C1 varchar(256)) INSERT INTO #PCFiles 
		SELECT * FROM dbo.udfGetDirectoryFiles (@PCDirPath, @FileName)

DELETE #PCFiles WHERE (C1 IS NULL)
IF @@ERROR <> 0
	GOTO spError

SELECT @ListFile FROM #PCFiles
IF @ListFile = 'File Not Found'
	GOTO spNoFile

-- Get directories from processor table
SET @Message = 'Invalid rowcount in Configuration table'
SELECT @AdminAddress = ISNULL(ConfigValue ,'hcliou@wescom.org')
	FROM Configuration 
	WHERE ConfigName = 'EmailSysOpAddress'

IF @@ROWCOUNT <> 1 
	GOTO spError

SELECT @MemberSrvAddress = ISNULL(ConfigValue ,'hcliou@wescom.org')
	FROM Configuration 
	WHERE ConfigName = 'EmailMemberServicesAddress'

IF @@ROWCOUNT <> 1 
	GOTO spError

SELECT @ReportAddress = ISNULL(ConfigValue ,'hcliou@wescom.org')
	FROM Configuration 
	WHERE ConfigName = 'EmailReportsAddress'

IF @@ROWCOUNT <> 1 
	GOTO spError

-- Check to see if archive directory exists, if not, create it
SET @Message = 'Archive Directory is invalid'
SET @ArchiveDirPath = @PCDirPath+@ArchiveDirPath+ '\'
IF dbo.udfPathExists( @ArchiveDirPath ) = 0
BEGIN		-- If path does not exists, try to create it with the directory name in the configuration table
	IF dbo.udfCreateDirectory (@ArchiveDirPath) = 0 
		GOTO spError
END

-- Create the request format file
SET @Message = 'Could not create request format file'
SET @PCFormat = @PCDirPath + '~~WRGBPS_PC_IN.FMT'
IF dbo.udfCreatePCFF( @PCFormat ) = 0
	GOTO spError

DECLARE cPCFiles CURSOR FOR SELECT C1 FROM #PCFiles
OPEN cPCFiles

FETCH NEXT FROM cPCFiles INTO @PCFileName
WHILE @@FETCH_STATUS = 0
BEGIN

	SET @PCFile = @PCDirPath + @PCFileName

	-- Make sure the file exists in case of File Not Found is returned from dir command
	IF dbo.udfPathExists ( @PCFile ) = 0
		GOTO spContinue -- Then it does not exist, otherwise you get a zero

	-- Create a temp table
	IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CONFIRMATION]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CONFIRMATION]
	CREATE TABLE [dbo].[TEMP_CONFIRMATION]
	(
		[ID] [int] IDENTITY (1, 1) NOT NULL ,
		[Data] [varchar] (1000) NOT NULL 
	) ON [PRIMARY]
	
	TRUNCATE TABLE TEMP_CONFIRMATION

-- Load the PC file into the Transit_Payment_Requests table
	SET @Message = 'Error inserting rows into TEMP_CONFIRMATION table from payment confirmation file: ' + @PCFile + ',  format file = ' + @PCFormat
	SET @SQL = 'BULK INSERT TEMP_CONFIRMATION FROM ''' + @PCFile + ''' WITH (FORMATFILE = ''' + @PCFormat + ''' , ROWTERMINATOR = ''\r\n'' ,TABLOCK)'
	EXEC (@SQL)
	IF @@ERROR <> 0
		GOTO spError

--	SET @tAmount = 0
--	SET @eAmount = 0
--	SET @cCount = 0
--	SET @rCount = 0
	SET @eCount = 0
	SET @tCount = 0
--	SET @cAmount = 0
--	SET @rAmount = 0
	SET @fCount = 0
	SET @Notify = 0

	SET @Message = 'Importing the ORCC confirmation file: '+ @PCFile
	EXEC spLog  @Message,@SPName

	EXEC BPG_UpdateGeneratorStatus 0, 9, @Message

	DECLARE pc CURSOR FOR
		SELECT Data
		FROM TEMP_CONFIRMATION
		ORDER BY [ID]
	OPEN pc
	FETCH NEXT FROM pc
	INTO @Temp
	WHILE @@FETCH_STATUS = 0
	BEGIN

	SET @FileType = LEFT(@Temp, 3)
	IF (@FileType = '100' and SUBSTRING(@Temp, 5,4) = 'RX02') -- This is the header record, validate that it is for our CU
	BEGIN
--		SET @FileID = SUBSTRING(@Temp, 5,4)
		SET @CUID = ISNULL( SUBSTRING(@Temp, 25, 10),0 )  --  need to validate CUID
		IF @CUID != @FileID
		BEGIN
			SET @Message = 'File Identifier does not match CU ID in Confirmation Header'
			EXEC spLog @Message, @SPName
			EXEC @rc = spSendMail @StatusCode, @RecipientAddress, '', 1045
			RETURN -1
		END

--		SET @RunID = CONVERT(varchar(10), RTRIM(SUBSTRING(@Temp, 5, 8)))
	END

	IF @FileType = '200'  --  Member modification record
	BEGIN
		SET @Message = ''
		SET @MemberID = SUBSTRING( @Temp, 16, 19 )
		SET @Status = SUBSTRING( @Temp, 36, 1 )
		IF @Status = 'F'
		BEGIN
			SET @Notify = 1
			SET @fcount = @fcount + 1
			SET @StatusCode= 1045
			SET @ErrorNumber = RTRIM(SUBSTRING( @Temp, 38, 15 ))
--print @errornumber
			IF @ErrorNumber != ''	
			BEGIN
				SELECT @Message = prvStatusCode + ' : ' + ISNULL(Description,'')
					FROM ProviderCodes
				WHERE prvStatusCode = @ErrorNumber
				EXEC @rc = BPG_InsertErrorRecap NULL, '', '', @MemberID, @StatusCode, @Message
			END
		END
		
	END
		--SET @amount = Convert(money, SUBSTRING(@Temp,13,15))
	IF @FileType = '300'  -- Payee Modification response record	
	BEGIN
		SET @MemberID = SUBSTRING( @Temp, 16, 19 )
		SET @Status = SUBSTRING( @Temp, 56, 1 )
		SET @PayeeListID = RTRIM(SUBSTRING( @Temp, 36, 12 ))  --  need to add a 0 since the file drops it
		IF len(@PayeeListID) = 11	
			SET @PayeeListID = '0' + @PayeeListID
		SET @Message = SUBSTRING( @Temp, 58, 15 )
		IF @Status = 'F'
		BEGIN
			SET @Notify = 1
			SET @fcount = @fcount + 1
			SET @StatusCode= 1045
			SET @ErrorNumber = SUBSTRING( @Temp, 58, 8)
			IF @ErrorNumber != ''	
			BEGIN
				SELECT @Message = prvStatusCode + ' : ' + Description 
					FROM ProviderCodes
				WHERE prvStatusCode = @ErrorNumber
				EXEC @rc = BPG_InsertErrorRecap NULL, @PayeeListID, '', @MemberID, @StatusCode, @Message, @FileType
			END
		END
		IF EXISTS ( SELECT PayeeListID FROM User_Payee WHERE PayeeListID = @PayeeListID )
		BEGIN
			SELECT @Method = 
				CASE @Status
					WHEN 'L' THEN 'E'
					WHEN 'K' THEN 'C'
					WHEN 'C' THEN 'C'
					ELSE 'C'
				END
			EXEC @rc = BPG_UpdatePayee @Method, @PayeeListID
		END
	END	

	IF @FileType = '900'  --  Payment response record
	BEGIN
		SET @PaymentID = RTRIM(SUBSTRING(@Temp, 16, 10))  --  We only have a 10 digit paymentid
		IF Len(@paymentID) = 9
			SET @PaymentID = '0' + @PaymentID
		IF EXISTS (SELECT PaymentID FROM Payment_History WHERE PaymentID = @PaymentID)
		BEGIN
			SET @Status = SUBSTRING(@Temp,36,1)
			IF @Status = 'F' 
			BEGIN
				SET @Notify = 1
				SET @StatusCode = 1045  -- Set to failed status, since they only have 1 status
				SET @ErrorNumber = SUBSTRING ( @Temp, 58, 15 )  -- Need to record the message
				EXEC @rc = BPG_UpdatePaymentHistory @StatusCode,@PaymentID,' ', ' '
						
				SELECT	@phamount= Amount, 
						@RunID = PH.RunID,
						@MemberID = PH.MemberID, 
						@PayeeListID = PH.PayeeListID,
						@RecipientAddress = MemberEmail
					FROM PAYMENT_HISTORY PH 
					WHERE PH.PaymentID = @PaymentID

				SELECT @Message = prvStatusCode + ' : ' + Description 
					FROM ProviderCodes
					WHERE prvStatusCode = @ErrorNumber

				EXEC @rc = BPG_InsertErrorRecap @RunID, @PayeeListID, @PaymentID, @MemberID, @StatusCode, @Message, @FileType
--				EXEC @rc = spSendMail @StatusCode, @RecipientAddress, @PaymentID, @Message
				IF @rc <> 0
					EXEC spLog NULL, @Message, @SPName

				SET @fcount = @fcount + 1
--				SET @rAmount = @rAmount + @phamount
--				SET @rCount = @rCount + 1
			END
			ELSE IF @Status = 'S'
			BEGIN
				SELECT	@PaymentMethod = PaymentMethod
					FROM PAYMENT_HISTORY PH 
					WHERE PH.PaymentID = @PaymentID
				SET @eCount = @ecount + 1
				EXEC @rc = BPG_UpdatePaymentHistory 102, @PaymentID, '', @PaymentMethod
			END
			ELSE
			BEGIN
				SELECT	@phAmount= Amount, 
						@MemberID = PH.MemberID, 
						@RecipientAddress = MemberEmail,
						@PayeeListID = PH.PayeeListID
					FROM PAYMENT_HISTORY PH 
					WHERE PH.PaymentID = @PaymentID

				SELECT @Method = 
					CASE @Status
						WHEN 'L' THEN 'E'
						WHEN 'K' THEN 'C'
						WHEN 'C' THEN 'C'
						ELSE 'C'
					END

				SET @CheckNum = ''
				IF @Method = 'C'
				BEGIN
					SET @CheckNum = RTRIM(SUBSTRING(@Temp,38,12))
					SET @PaymentMethod = 'C'
--					SET @cAmount = @cAmount + @phAmount    --  Sum amount
				END
				ELSE
				BEGIN
--					SET @eAmount = @eAmount + @phAmount
					SET @PaymentMethod = 'E'
				END
				SET @ecount = @ecount + 1
				--  Update the UserPayee payment method and the PaymentHistory payment method to reflect the current method
				BEGIN TRANSACTION
				EXEC @rc = BPG_UpdatePaymentHistory 102, @PaymentID, @CheckNum, @PaymentMethod
				IF @rc != 0
				BEGIN
					ROLLBACK TRANSACTION
					SET @Message = 'Error occurred while updating the Payment method for paymentid : '+@PaymentID
					EXEC spLog @Message, @SPName
				END
				ELSE
				BEGIN
					EXEC @rc = BPG_UpdatePayee @PaymentMethod, @PayeeListID
					IF @rc != 0
					BEGIN
						ROLLBACK TRANSACTION
						SET @Message =  'Error occurred updating the User Payee table for Payment ID : ' +  @PaymentID 
						EXEC spLog @Message, @SPName
					END
					ELSE
						COMMIT TRANSACTION
				END			
			END	
		END
		ELSE
		BEGIN
			SET @Message = 'Payment ('+@PaymentID+') was not found in Payment History'
			EXEC spLog @Message, @SPName
		END
	END

	IF @FileType = '500'  -- Cleared Check Record
	BEGIN
		SET @PaymentID = RTRIM(SUBSTRING( @Temp, 16, 10 ))
		IF Len(@PaymentID) = 9 
			SET @PaymentID = '0' + @PaymentID
		SET @CheckNum = RTRIM(SUBSTRING ( @Temp, 36, 12 ))
		SET @ClearDate = SUBSTRING( @Temp, 56, 8 )
		INSERT INTO Payment_Clear
			(PaymentID, CheckNum, ClearedDate, NotifyCount, NotifyDate, LastAction, ActionDate)
		VALUES
			(@PaymentID,@CheckNum, @ClearDate, NULL, NULL, NULL, NULL)
	END

	IF LEFT (@Temp,3) = 'E00'
--	Get all the information and pass it to the error processing routine
	BEGIN
		SET @LineNum = SUBSTRING( @Temp, 27, 10 )
		SET @ErrorType = LEFT( @Temp, 3)
		SET @RunID = SUBSTRING ( @Temp, 16, 10 ) 
		SET @ErrorNumber = RTRIM(SUBSTRING( @Temp, 38, 15 ))
		SET @fCount = @fcount + 1
		EXEC @rc = BPG_ProcessConfirmationErrors @RunID, @LineNum, @ErrorNumber	
	END

FETCH NEXT FROM pc INTO @Temp
END	

CLOSE pc
DEALLOCATE pc

If @Notify = 1 --  means at least one payee or member error, alert admin for them to check
BEGIN
	SET @Message = 'Confirmation file : ' + @PCFileName + ' contained a failed member or payee upate, use failed date: ' + Convert(varchar, @now, 110) + 
		' to look up in the Error Recap Search page'
	EXEC spLog @Message, @SPName
	EXEC spSendMail 1045, @MemberSrvAddress, '', @Message
END
--SET @subtotal = @subtotal + @amount --  Add up the successful payment amounts and the reject amounts and  compare to the file amount
--SET @Totalamt = @fileamount - (Convert(money,@amount)/100.)
SET @tCount = @eCount + @fCount
SET @tAmount = (@eAmount + @cAmount + @rAmount)/100.
--Send email with the import summary information

SET @Message = 'Payments confirmed: '+ convert(varchar,@tcount)+', payments rejected: '+convert(varchar,@fCount)
EXEC  spLog @Message,@SPName

--EXEC @rc = spSendImportConfirmationSummary @eCount, @eAmount, @cCount, @cAmount, @rCount, @rAmount, @tCount, @tAmount, @fCount
EXEC @rc = spSendMail 102,@ReportAddress, '', @Message
IF @rc != 0
BEGIN
	SET @Message = 'Could not send summary email to the administrator'
	EXEC spLog @Message, @SPName
	GOTO spContinue
END

-- Move the file to the backup directory
--SET @ArchiveFilename = @ArchiveDirPath + @ProviderName + SUBSTRING(@PCFileName,1, len(@PCFileName)-3) +  '_'+REPLACE(REPLACE(REPLACE(Convert(varchar(16),@now,120),'-',''),':',''),' ','') + '.IN'
SET @ArchiveFilename = @ArchiveDirPath + @ProviderName + '_' + @PCFileName
SET @Message = 'Error moving request file to backup directory: ' + @ArchiveFilename
IF dbo.udfMoveFile ( @PCFile, @ArchiveFileName) = 0
	GOTO spContinue

spContinue:
   	FETCH NEXT FROM cPCFiles INTO @PCFileName

END

spDone:
	CLOSE cPCFiles
	DEALLOCATE cPCFiles

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CONFIRMATION]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CONFIRMATION]

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#PCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #PCFiles

spExit:
	SET @Message = ''
	EXEC BPG_UpdateGeneratorStatus 0, 9, 'Confirmation File Completed'

 	RETURN 0

spError:
	EXEC BPG_UpdateGeneratorStatus 0, 9, @Message
	EXEC @rc = spLog  @Message, @SPName
	RETURN 2

spNoFile:
	IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[#PCFILES]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE #PCFiles
	RETURN 0

END


