-- =============================================
-- Author:		Helen Liou
-- Create date: 3/8/2007
-- Description:	Inserts records into the Error Recap table
-- =============================================
CREATE PROCEDURE [dbo].[BPG_InsertErrorRecap]
@RunID			int,
@PayeeListID	varchar(12),
@PaymentID		varchar(10),
@MemberID		varchar(22),
@StatusCode		int,
@Message		varchar(650) = '',
@ErrorType		varchar(5) = NULL
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	INSERT INTO ErrorRecap ( 
			 RunID, 
			 PayeeListID,
			 PaymentID,
			 MemberID,
			 Statuscode,
			 Message,
			 EntryDate,
			 ErrorType ) 
		VALUES 
			(@RunID,
			 ISNULL(@PayeeListID,''), 
			 ISNULL(@PaymentID,''), 
			 ISNULL(@MemberID,''), 
			 ISNULL(@StatusCode,''), 
			 ISNULL(@Message,''),
			 GETDATE(),
			 ISNULL(@ErrorType, ''))
END




