-- =============================================
-- Author:		<Author,Aili Cheng>
-- Create date: <12/08/2006>
-- Description:	<Update job step's of "BillPay Manual Run", start this job manually >
-- Modifications:
--    04/11/2007  GNordstrom  Added execution context for msdb sps.
-- =============================================
CREATE PROCEDURE [dbo].[BPG_ManualRun]
@Database varchar(30),
@RunDate DateTime
AS


SET NOCOUNT ON
--declare variable
declare @rc int,
		@Message varchar(400),
		@SPName varchar(50), 
		@command varchar(100),
		@JobName varchar(30)

select @JobName = ConfigValue from dbo.Configuration where ConfigName = 'BPManualRun'
set @SPName = 'BPG_ManualRun'
--variable initialization
set @command = 'Execute '+ @Database + '.dbo.BPG_Run '+ '''' + '''' + ', ' + '''' + convert(varchar, @RunDate,112)+ ''''
--update job command

EXECUTE AS LOGIN = 'BPGenerator'
exec @rc = msdb..sp_update_jobstep 
	  @job_name = @JobName,
	  @step_id = 1,
	  @step_name = N'Manual Run',
	  @command = @command,
	  @database_name = @Database
REVERT

IF @rc != 0
  BEGIN
	  -- Log error and end run
	  SET @Message = 'ERROR: Unable to update job step'
	  EXEC spLog @Message, @SPName
	  RETURN -1
  END
ELSE --if update job successfully, start to run the job...
  BEGIN
	  --run the job
	  EXECUTE AS LOGIN = 'BPGenerator'
	  exec @rc = msdb..sp_start_job @job_name = @JobName, @step_name = N'Manual Run'
	  REVERT
	  
	  IF @rc != 0
		BEGIN
			-- Log error and end run
			SET @Message = 'ERROR: Unable to start job'
			EXEC spLog @Message, @SPName
			RETURN -2
		END	 
  END

