-- =============================================
-- Author:		Helen Liou
-- Create date: 3/25/2007
-- Description:	Parses the Payment Layout Table and returns the necessary information
-- =============================================
CREATE PROCEDURE [dbo].[BPG_ParsePaymentLayout] 
@Message		varchar(650),
@MemberID		varchar(22) OUTPUT,
@PaymentID		varchar(10) OUTPUT,
@PayeeListID	varchar(12) OUTPUT,
@ErrorType		varchar(3) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @Temp Varchar(255)

	SET @MemberID = ''
	SET @PaymentID = ''
	SET @PayeeListID = ''
	SET @ErrorType = ''

	SET @Temp = SUBSTRING(@Message, 1,3)
	IF @Temp = '200'
		SET @MemberID = RTRIM(SUBSTRING(@Message,16,19))

	IF @Temp = '210'
		SET @MemberID = RTRIM(SUBSTRING(@Message,16,19))
	
	IF @Temp = '290'	
		SET @MemberID = RTRIM(SUBSTRING(@Message,16,19))

	IF @Temp = '300'	
		SET @PayeeListID = RTRIM(SUBSTRING(@Message,36,19))
		IF Len(@PayeeListID) = 11
			SET @PayeeListID = '0'+ @PayeeListID

	IF @Temp = '310'	
		SET @PayeeListID = RTRIM(SUBSTRING(@Message,36,19))
		IF Len(@PayeeListID) = 11
			SET @PayeeListID = '0'+ @PayeeListID

	IF @Temp = '900'	
		SET @PaymentID = RTRIM(SUBSTRING(@Message,36,19))
		IF Len(@PaymentID) = 9
			SET @PaymentID = '0'+ @PaymentID

	IF @Temp = '910'	
		SET @PaymentID = RTRIM(SUBSTRING(@Message,36,19))
		IF Len(@PaymentID) = 9
			SET @PaymentID = '0'+ @PaymentID

	SET @ErrorType = @Temp
	RETURN 0
END

