/* =====================================================================================================
 Author:	Helen Liou
 Create date:   10/14/2010
 Description:	This store procedure will post payments to the members loan account that were debited during the billpay run

  ------------------------------------------------------------------
  Modifications: 

 ===================================================================================================== */

CREATE PROCEDURE [dbo].[BPG_ProcessALoanPayment] 
	@PaymentID		varchar(10),
	@MemberID		varchar(22),
	@Account		varchar(22),
	@Amount			int,
	@UsersAccount	varchar(40),
	@RunID			int,
	@User			varchar(50)
AS

SET NOCOUNT ON;

DECLARE @rc					int,
		@OnUsGLCode			int,
		@statuscode			int,
		@errnum				int

DECLARE @SPName				Varchar(255),
		@OnUsComment		varchar(255),
		@errdesc			varchar(132),
		@Message			varchar(255),
		@Money				money

DECLARE @Now	Datetime

BEGIN TRY

	SET @SPName = Convert(varchar, OBJECT_NAME(@@PROCID))
	SET @Now = GETDATE()

--  Check if GL exists
	SELECT @OnUsGLCode = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'OnUsGLCode'
	SELECT @OnUsComment = ISNULL(ConfigValue,'BillPay OnUs Loan Posting') FROM Configuration WHERE ConfigName = 'OnUsComment'

	set @money = @amount/100.

	IF Not Exists ( SELECT PaymentID FROM OnUsLoans WHERE PaymentID = @PaymentID )
		INSERT INTO ONUSLoans (PaymentID, MemberID, AccountID, LoanID, Amount, Comment, GLCode, RunID, 
			ErrorCode, ErrorDesc, SourceApp, EntryDate, ModifiedDate, ModifiedBy )
			VALUES ( @PaymentID, @MemberID, @Account, @UsersAccount, @Money, @OnUsComment, @OnUsGLCode, @RunID, 
			null, null, 'BPGen', @Now, @Now, @User )
	ELSE
		EXEC BPG_InsertOnUsTracking @PaymentID, @User

--  Post the Payment, record and set any rejections accordingly

	EXEC @rc = BPG_GLAccountTransfer @Account, @UsersAccount, @OnUsGLCode, @Money, @OnUsComment, @errnum OUTPUT, @errdesc OUTPUT
	IF @rc != 0
	BEGIN
		UPDATE Payment_History SET StatusCode = 203, FailedDate = @now WHERE PaymentID = @PaymentID		
		EXEC @rc = BPG_InsertErrorRecap @RunID, '', @PaymentID, @MemberID, @statuscode, @Errdesc, @errnum
		SET @Message = 'Error occurred while trying to post a loan payment for : ' + @paymentid + ' the error is: ' + convert(varchar, @errnum) + 
			' ' + @Errdesc
		EXEC spLog @Message,@SPName, @RunID
		RETURN 1
	END
	ELSE
	BEGIN
		IF @errnum != 0
		BEGIN
--			print 'Error num: ' + Convert(varchar, @errnum)
--			print 'Error Desc ' + @errdesc
			SELECT @statuscode = ISNULL(S.statuscode,203) FROM Payment_History PH 
				LEFT JOIN Status_Code S ON ph.statuscode = S.statuscode
			WHERE PaymentID = @PaymentID and S.HostCode = @errnum
--  If the status code comes back as 13032 service not available, update the status code to fail onus payment
			IF @StatusCode = 1007 or @statuscode is null
				SET @StatusCode = 203

			UPDATE Payment_History SET StatusCode = @statuscode, FailedDate = @now 
			FROM Payment_History PH WHERE PaymentID = @PaymentID

			UPDATE OnUsLoans SET ErrorCode = @errnum, ErrorDesc = @Errdesc WHERE PaymentID = @PaymentID
			EXEC @rc = BPG_InsertErrorRecap @RunID, '', @PaymentID, @MemberID, @statuscode, @Errdesc, @errnum
				SET @Message = 'Error occurred while trying to post a loan payment for : ' + @paymentid + ' the error is: ' + convert(varchar, @errnum) + 
				' ' + @Errdesc
			EXEC spLog @Message,@SPName, @RunID
			Return 1
		END	
		ELSE
--  Update history with successful statuscode
			Update Payment_History SET StatusCode = 102 WHERE PaymentID = @PaymentID
	END
		
END TRY
BEGIN CATCH
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

    RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH





