SELECT @AccountIDLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'AccountSuffixLength'
SELECT @MortgageEnabled = ISNULL(ConfigValue, 'N') FROM Configuration WHERE ConfigName = 'OnUsMortgageEnabled'
SELECT @MortgageIDLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'OnUsMortgageIDLength'
SELECT @OnUsGLCode = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'OnUsGLCode'
SELECT @OnUsComment = ISNULL(ConfigValue,'BillPay OnUs Loan Posting') FROM Configuration WHERE ConfigName = 'OnUsComment'

--  Get the list of payments that need to be posted
DECLARE @tmploantable TABLE (PaymentID varchar(10), MemberID varchar(22), Amount int, UsersAccount varchar(40) )

INSERT INTO @tmploantable
	SELECT	PaymentID, 
			MemberID,
			Amount, 
			REPLACE(UsersAccountAtPayee, ' ', '' ) as UsersAccountAtPayee 
	FROM Payment_History PH
	WHERE ProviderID = 101 and Statuscode = 101 and RunID = @RunID --and UsersAccountAtPayee not like '%S%'

NextPayment:

SELECT	TOP 1
		@PaymentID = PaymentID, 
		@MemberID = MemberID,
		@Amount = Amount,
		@UsersAccount = UsersAccount
FROM @tmploantable

IF @@ROWCOUNT = 0
	GOTO Done

--  Save original account number so we can report an error and the information passed in 
SET @Account = REPLACE(REPLACE(@UsersAccount, ' ', ''),'-','')
SET @LoanIDPos = CHARINDEX('L',@Account)

IF @LoanIDPos > 0
BEGIN
--  Get Account number from UsersAccountatPayee and parse, if it doesnt match the format, generate an exception
	IF ISNUMERIC(REPLACE(@Account,'L','')) = 0  --  not a valid onus format, fail the payment and process as check
		SET @StatusCode = 203
	ELSE
		SET @StatusCode = 102

	IF @LoanIDPos = 1  --  means that there is no account number and we need one to post, so assume it is the members #
		SET @MemberAccount = @MemberID
	ELSE
		SET @MemberAccount = SUBSTRING(@Account, 1, charindex('L',@Account)-1)

	IF @StatusCode = 102
	BEGIN
		SET @AccountID = SUBSTRING(@Account, charindex('L',@Account), @AccountIDLength+1 )
		IF LEN(@AccountID)-1 < @AccountIDLength
			SET @AccountID = 'L' + dbo.padAccount(SUBSTRING(@AccountID, 2,LEN(@AccountID)-1),@AccountIDLength)
		ELSE
			SET @AccountID = @AccountID + REPLICATE('?',@AccountIDLength - (Len(@AccountID)-1))	
	END
END
ELSE 
	SET @StatusCode = 203

SET @Message = 'Payment was not posted successfully, please post manually : ' + @PaymentID
IF charindex('?', @AccountID) = 0 and @StatusCode = 102
	EXEC @rc = BPG_ProcessOnUsPayment @PaymentID, @MemberId, @MemberAccount, @Amount, @AccountID, @RunID, 'BPGen', @OnUsGLCode, @OnUsComment, @MortgageEnabled, @MortgageIDLength 
ELSE 
BEGIN
	UPDATE PAYMENT_HISTORY SET StatusCode = 203, FailedDate = @Now WHERE PaymentID = @PaymentID
	EXEC BPG_InsertErrorRecap @RunID, '', @PaymentID, @MemberID, @statuscode, @Message, @statuscode
	EXEC spLog @Message, @SPName, @RunID
END
	
DELETE FROM @tmploantable WHERE PaymentID = @PaymentID
GOTO NextPayment

Done:
	Return 0

spError:
	Return 1

END


