-- =============================================
-- Author:		Helen Liou
-- Create date: 12/20/2007
-- Description:	This stored procedure processes payment fees for all payments that were NSF.  If the
--				SpecialCourtesyPay flag is enabled in the configuration table ( =1 ), then this will not
--				be executed. 
--
-- Modifications: 4/5/2013  Helen Liou 	Comment out the update for the Month to Date NSF counter.  
--					Appears that Symitar is now updating it.
--
-- =============================================
CREATE PROCEDURE [dbo].[BPG_ProcessNSFFees] 
	@CUID			varchar(9),
	@RunID			int,
	@FeeGL			varchar(5)
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
SET NOCOUNT ON;

DECLARE @PaymentID		varchar(10),
		@MemberID		varchar(22),
		@Comment		varchar(255),
		@strComment		varchar(50),
		@Account		varchar(5),
		@errDesc		varchar(255),
		@Message		varchar(255),
		@SPName			varchar(255),
		@PayeeName		varchar(32),
		@PayeeListID	varchar(12),
		@ShareID		varchar(5),
		@FeeSvcCode		varchar(10),
		@Email			varchar(255),
		@ProcessFee		varchar(1),
		@ServiceCodeExists varchar(1)

DECLARE	@errNum			int,
		@statuscode		int,
		@rc				int,
		@ShareIDLength	int

DECLARE @feeamount		money,
		@amount			money

--  Declare a temp table for processing each nsf separately
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
SELECT @feeamount = ISNULL(ConfigValue,0) FROM Configuration WHERE ConfigName = 'NSFFeeAmount'
SELECT @strComment = ISNULL(ConfigValue, 'BP NSF $') FROM Configuration WHERE ConfigName = 'NSFFeeComment'
SELECT @ShareIDLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'AccountSuffixLength'
SELECT @FeeSvcCode = ISNULL(ConfigValue, '') FROM Configuration WHERE ConfigName = 'NSFServiceCodes'
SELECT @ServiceCodeExists = ISNULL(ConfigValue, 'N') FROM Configuration WHERE ConfigName = 'NSFCheckServiceCode'

DECLARE @tmpnsf TABLE (PaymentID varchar(10), MemberID varchar(22), Account varchar(22),
					   StatusCode int, Amount money, PayeeName varchar(32), PayeeListID varchar(12),
					   Email varchar(255) )

INSERT INTO @tmpnsf
	SELECT	ph.PaymentID, 
			ph.MemberID,
			ph.Account,
			ph.StatusCode,
			ph.Amount, 
			p.Name,
			ph.payeelistid, 
			ph.MemberEmail
	FROM Payment_History ph inner join payee p on ph.payeeid = p.payeeid 
	WHERE (StatusCode = 200 or StatusCode = 2001) and RunID = @RunID

NextPayment:
SET @errnum = 0
SET @errDesc = ''

SELECT TOP 1 @PaymentID = PaymentID, 
			@MemberID = RTRIM(MemberID),
			@Account = RTRIM(SUBSTRING(Account, CHARINDEX('-',Account)+2,LEN(Account))),
			@ShareID = RTRIM(SUBSTRING(Account, CHARINDEX('-',Account)+1,@ShareIDLength+1)),
			@StatusCode = StatusCode,
			@Amount = Amount,
			@PayeeName = RTRIM(PayeeName),
			@PayeeListID = PayeeListID,
			@Email = Email
	FROM @tmpnsf

IF @@RowCount = 0
	GOTO Done

SET @ProcessFee = 'Y'

IF NOT EXISTS ( SELECT PaymentID FROM NSFFees WHERE PaymentID = @PaymentID )
BEGIN
	IF @FeeSvcCode != ''
	BEGIN
		IF @ServiceCodeExists = 'Y'	--  Need to add a check to see if the service code exists charge fee or if it is not there, charge a fee.
		BEGIN
--  Check to see if this service code is on the account, if so, then charge the fee
			SET @Message = 'Error checking account service code to determine if a NSF fee is charged or not for MemberID : ' 
				+ @MemberID + ' : PaymentID : ' + @PaymentID
			EXEC @rc = BPG_IsValidServiceCode @MemberID, @ShareID, @FeeSvcCode, @errNum OUTPUT, @errDesc OUTPUT
			IF @errNum = 0  --  Account does not have specified service code, then charge a fee
			BEGIN
				IF @rc != 1
				BEGIN
			  		SET @ProcessFee = 'N'
					EXEC spSendMail @StatusCode, @Email, @PaymentID
				END
--			ELSE
--				EXEC spLog @Message, @SPName, @RunID
			END	
			ELSE  --  An error occurred, do not set charge a fee or update counter
			BEGIN
				SET @ProcessFee = 'N'
				SET @Message = @Message + '  Error returned: ' + Convert(varchar, @errNum) + ' : ' + RTRIM(@ErrDesc)
				EXEC spLog @Message, @SPName, @RunID
			END
		END
		ELSE  --  Check if account does have a Service Code then charge a fee
		BEGIN
			SET @Message = 'Error checking account service code to determine if a NSF fee is charged or not for MemberID : ' 
				+ @MemberID + ' : PaymentID : ' + @PaymentID
			EXEC @rc = BPG_IsValidServiceCode @MemberID, @ShareID, @FeeSvcCode, @errNum OUTPUT, @errDesc OUTPUT
			IF @errNum = 0  --  Account does not have specified service code, then charge a fee
			BEGIN
				If @rc = 1
				BEGIN
			  		SET @ProcessFee = 'N'
					EXEC spSendMail @StatusCode, @Email, @PaymentID
				END
--			ELSE
--				EXEC spLog @Message, @SPName, @RunID
			END	
			ELSE  --  An error occurred, do not set charge a fee or update counter
			BEGIN
				SET @ProcessFee = 'N'
				SET @Message = @Message + '  Error returned: ' + Convert(varchar, @errNum) + ' : ' + RTRIM(@ErrDesc)
				EXEC spLog @Message, @SPName, @RunID
			END
		END
	END
	IF @ProcessFee = 'Y'
	BEGIN
--  Call the NSF Fee CMD
		SET @Comment = @strComment + STR((@amount/100.), 10, 2)+ ' to ' + @PayeeName
		SET @Message = 'Error processing NSF Fees, Error Code = '
		EXEC @rc = BPG_EXECHostFeeCMD @MemberID, @Account, @feeAmount, @Comment, @FeeGL, @errNum OUTPUT, @errDesc OUTPUT
		IF @errNum != 0
		BEGIN	
			SET @Message = @Message + Convert(Varchar, @errNum) + ' : ' + @errDesc
			EXEC spLog @Message, @SPName, @RunID
		END
		ELSE
		BEGIN
			SET @Message = 'PaymentID: ' + @PaymentID + ' has been charged a NSF fee in the amount of $' + STR(ISNULL(@feeAmount,0.00),15,2)
			EXEC spLog @Message, @SPName, @RunID
			IF @StatusCode <= 200
				SET @StatusCode = @StatusCode + 1001
			EXEC spSendMail @StatusCode, @Email, @PaymentID
			INSERT INTO NSFFees ( PaymentID, MemberID, PayeeListID, Amount, StatusCode, RunID, FeeDate )
				VALUES ( @PaymentID, @MemberID, @PayeeListID, @Amount, @StatusCode, @RunID, GETDATE() )
-- Need to update the MTD counter
--			SET @Message = 'Error Updating NSF MTD counter for MemberID : ' + @memberID + ' '
--			EXEC @rc = BPG_UpdateNSFMonthToDate	@MemberID, @ShareID, 1, @errNum OUTPUT, @errDesc OUTPUT
--			IF @rc != 1
--			BEGIN
--				SET @Message = @Message + 'errnum = ' + Convert(varchar, @errNum) + ' : ' + @errDesc
--				EXEC spLog @Message, @SPName, @RunID
--			END
		END -- End check Host Fee CMD
	END
END -- End If Exists

DELETE FROM @tmpnsf WHERE PaymentID = @PaymentID
GOTO NextPayment

DONE:

RETURN 0

END

