/* =====================================================================================================
 Author:	Helen Liou
 Create date:   10/14/2010
 Description:	This store procedure will post payments to the members loan account that were 
		debited during the billpay run

  ------------------------------------------------------------------
  Modifications: 

  
  HLiou	7/19/2017	Returns the correct statuscode when the transfer to mortgage fails.  It was
			returning success instead of failure

 ===================================================================================================== */

CREATE PROCEDURE [dbo].[BPG_ProcessOnUsPayment] 
	@PaymentID		varchar(10),
	@MemberID		varchar(22),
	@Account		varchar(22),
	@Amount			int,
	@UsersAccount	varchar(10),
	@RunID			int,
	@User			varchar(50),
	@OnUsGLCode		int,
	@OnUsComment	varchar(255),
	@MtgEnabled		varchar(1),
	@LoanIDLength	int
AS

SET NOCOUNT ON;

DECLARE @rc					int,
		@statuscode			int = 102,
		@errnum				int = 0

DECLARE @SPName				Varchar(255),
		@errdesc			varchar(132) = '',
		@Message			varchar(255),
		@NewLoanID			varchar(22),
		@Money				money

DECLARE @Now	Datetime

BEGIN TRY

	SET @SPName = Convert(varchar, OBJECT_NAME(@@PROCID))
	SET @Now = GETDATE()
	SET @money = @amount/100.

	IF @MtgEnabled = '' 
		SET @MtgEnabled = 'N'
		
	IF Not Exists ( SELECT PaymentID FROM OnUsPayments WHERE PaymentID = @PaymentID )
		INSERT INTO ONUSPayments (PaymentID, MemberID, AccountID, LoanID, Amount, Comment, GLCode, RunID, 
			ErrorCode, ErrorDesc, SourceApp, EntryDate, ModifiedDate, ModifiedBy )
			VALUES ( @PaymentID, @MemberID, @Account, @UsersAccount, @Money, @OnUsComment, @OnUsGLCode, @RunID, 
			null, null, 'BPGen', @Now, @Now, @User )
	ELSE
		EXEC BPG_InsertOnUsTracking @PaymentID, @User

--  Post the Payment, record and set any rejections accordingly

	SET @statuscode = 102
	EXEC @rc = BPG_GLAccountTransfer @Account, @UsersAccount, @OnUsGLCode, @Money, @OnUsComment, @errnum OUTPUT, @errdesc OUTPUT
	IF @rc != 0 or @errnum != 0
	BEGIN
		SET @statuscode = 203
		IF @MtgEnabled = 'Y'
		BEGIN
			IF @errnum = 13030
			BEGIN
				SET @Account = RTRIM(@Account)
				SET @NewLoanID = @Account + substring(@UsersAccount, len(@UsersAccount)-@LoanIDLength+1, @LoanIDLength)
				EXEC @rc = BPG_GLAccountTransferMortgage @Account, @NewLoanID, @OnUsGLCode, @Money, @OnUsComment, @errnum OUTPUT, @errdesc OUTPUT
				IF @rc != 0 or @errnum != 0
				BEGIN
					SET @statuscode = 203
					SET @Message = 'Error occurred while trying to transfer to the loan for : ' + @paymentid + ' the error is: ' + convert(varchar, @errnum) + 
						' ' + @Errdesc
				END
				ELSE
					SET @statuscode = 102
			END
		END
		ELSE
		BEGIN
			SET @statuscode = 203
			SET @Message = 'Error occurred while trying to post a loan payment for : ' + @paymentid + ' the error is: ' + convert(varchar, @errnum) + 
			' ' + @Errdesc
		END
	END

	IF @statuscode = 102
		UPDATE Payment_History SET StatusCode = @statuscode FROM Payment_History PH WHERE PaymentID = @PaymentID
	ELSE
	BEGIN
		UPDATE Payment_History SET StatusCode = @statuscode, FailedDate = @now FROM Payment_History PH WHERE PaymentID = @PaymentID
		UPDATE OnUsPayments SET ErrorCode = @errnum, ErrorDesc = @Errdesc WHERE PaymentID = @PaymentID
		EXEC BPG_InsertErrorRecap @RunID, '', @PaymentID, @MemberID, @statuscode, @Errdesc, @errnum
		EXEC spLog @Message, @SPName, @RunID
		Return 1
	END
		
END TRY
BEGIN CATCH
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

    RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH




