-- ======================================================================================================================
-- Author:		Helen Liou
-- Create date: 8/23/2006
-- Description:	This sp will process all one-time payments that are due.  It needs the
--		member and account information from the host and calls the BillPay command
--		to debit the members account.  If a fee needs to be charged, then the fee
--		command could also be issued.
--
-- ======================================================================================================================
-- Modification:
-- HLiou	6/20/2007	Check hostcode 13999 can be both account batch warning as well as reg-d
-- GNordstrom	9/10/2007	Modified to allow for CC advances for bill payments.
-- HLiou	12/18/2007 	Changed the statuscode to 101 for the payments that are processing from 
--              		recurring payment table to the payments table.
-- GNordstrom   03/23/2009  	Modified the update of the statuscode to 101 to include a join to the 
--                              HostMemberAcctInfo so that the exact same payments are updated in the
--                              payment table as are extracted and processed. 
-- HLiou	5/11/2009	Checking for account status of 0, and a share status != 0 and setting the appropriate status
-- HLiou	6/22/2009	Check for account status is not 0 and share status = 0, means account not found
-- HLiou	7/6/2010	   Added check if statuscode does not exist, then set it to 1007, the default error.
-- HLiou	1/15/2013   Added configuration value to allow payments to processing in ascending or descending amount order
-- HLiou	2/21/2018   Added identity column to sort the temp table in the correct order.
--======================================================================================================================
--
CREATE PROCEDURE [dbo].[BPG_ProcessPayments] 
@CUID varchar(9),
@RunID int,
@EffectiveDate datetime = NULL
AS
BEGIN
	SET NOCOUNT ON
/*  Get all payments due for the effective date, check to see if any are duplicates, if there are move one payment to history with a 

statuscode of duplicate and process the other one.
	Send emails for payments that fail due to closed accounts, NSFs or any other processing errors.
*/

--Declare all the varchar type variables
DECLARE  @MemberFirst varchar(32),
			@MemberLast varchar(32), 
			@MemberAddress1 varchar(32),
			@MemberAddress2 varchar(32),
			@MemberAddress3 varchar(32),
			@MemberCity varchar(32),
			@MemberState varchar(32),
			@MemberPostalCode varchar(11),
			@MemberEMail varchar(255),  
			@Message varchar(255),
			@PaymentID varchar(10), 
			@PayeeListID varchar(12), 
			@PayeeID varchar(12), 
			@MemberID varchar(22),
			@SPName varchar(255),
			@Account varchar(32),
			@TrsfSource  varchar(22), 
			@PayeeName varchar(32), 
			@crlf varchar(20), 
			@dupPaymentID varchar(10),
			@Comment varchar(100),
			@ErrMsg varchar(255),
			@Suffix varchar(4),
			@Description varchar(50),
			@HostError varchar(128),
			@CloseDate	varchar(30),
			@Desc varchar(255),
			@ShareDesc varchar(255),
			@ProcessingOrder varchar(10)

DECLARE		@PAmount money,
			@Amount money

DECLARE		@CurrentDate datetime,
			@Now datetime,
			@WillProcess datetime

DECLARE		@rc int, 
			@SAmount int, 
			@FailedAmount Int, 
			@Numpayments int, 
			@DuplicateAmount int, 
			@DuplicateCount int, 
			@TotalAmount int, 	 
			@StatusCode int, 
			@Failed int, 
			@Success int,
			@CourtesyPay int,
			@AcctErr int,
			@ShareErr int,
			@MemberErrNum int,
			@SuffixLength int,
			@LargePaymentEmail int,
			@RunStatus int

--Initialize all necessary variables, otherwise Nulls may be assigned
SET @CRLF = CHAR(13)+CHAR(10)
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
SET @DuplicateCount = 0
SET @Success = 0
SET @Failed = 0
SET @DuplicateAmount = 0
SET @SAmount = 0
SET @FailedAmount = 0
SET @NumPayments = 0
SET @ErrMsg = ''
SET @Desc = ''
SET @RunStatus = 6
SET @Now = GETDATE()

EXEC BPG_InsertGeneratorStatus @RunID, @RunStatus, NULL

EXEC spLog 'Begin Processing Payments', @SPName

SELECT @CourtesyPay = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'SpecialCourtesyPay'
SELECT @SuffixLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'AccountSuffixLength'
SELECT @LargePaymentEmail = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'LargePaymentNotification'
SELECT @ProcessingOrder = ISNULL(ConfigValue, 'DESC') FROM Configuration WHERE ConfigName = 'PaymentProcessingOrder'

SET @Message = 'Effective Date was not entered or is not a valid date, date set to current date'
IF @EffectiveDate IS NULL or ISDATE(@EffectiveDate) = 0
BEGIN
	SET @CurrentDate = @Now
	EXEC spLog  @Message,@SPName
END
ELSE
	SET @CurrentDate = @EffectiveDate

SET @Message = 'Begin Processing Payments for effective date of: ' + Convert(varchar(10), @CurrentDate,101)
EXEC BPG_UpdateGeneratorStatus @RunID, @RunStatus, @Message
--  Build table of temp payments to process
DECLARE @tmpPayments TABLE ( RowID int identity(1,1) not null,
	PaymentID varchar(10),PayeeID varchar(12),PayeeName varchar(40), 
	PayeeListID varchar(12), MemberID varchar(32), Account varchar(32), TrsfSource varchar(22), Amount int, 
	StatusCode int, WillProcess datetime, Email varchar(80), FirstName varchar(32), 
	LastName varchar(32), AccountErrNum int, ShareErrorNum int, ShareErrorDesc varchar(255),
	ClosedDate VARCHAR(30) )

--  Select and update the payments status codes for all those we are doing right now
UPDATE Payment SET StatusCode = 101 
   FROM Payment P INNER JOIN HostMemberAcctInfo H ON H.MemberID = P.MemberID and H.AccountID = Account 
   WHERE WillProcessDate <= @CurrentDate and StatusCode = 100 and H.RunID = @RunID

IF @ProcessingOrder = 'ASC'
BEGIN
	INSERT INTO @tmpPayments
		SELECT
			PaymentID,
			U.PayeeID, 
			Name, 
			U.PayeeListID,
			P.MemberID, 
			Account,
			TrsfSource,
			Amount,
			StatusCode,
			WillProcessDate,
			RTRIM(ISNULL(Email,'')),
			SUBSTRING(RTRIM(ISNULL(First,'')),1,32),
			SUBSTRING(RTRIM(ISNULL(Last,'')),1,32),
			ISNULL( AcctErrorNum, 0 ), 
			ISNULL( ShareErrorNum, 0 ), 
			ISNULL( ShareErrorDesc, ''),
			ISNULL( CloseDate, '' )
		FROM Payment P
			INNER JOIN User_Payee U ON P.PayeeListID = U.PayeeListID
			INNER JOIN Payee PA ON U.PayeeId = PA.PayeeID
			INNER JOIN HostMemberAcctInfo H ON H.MemberID = P.MemberID and H.AccountID = Account
		WHERE WillProcessDate <= @CurrentDate and StatusCode = 101 and H.RunID = @RunID
			ORDER BY Amount ASC
END
ELSE
BEGIN
	INSERT INTO @tmpPayments
		SELECT
			PaymentID,
			U.PayeeID, 
			Name, 
			U.PayeeListID,
			P.MemberID, 
			Account,
			TrsfSource,
			Amount,
			StatusCode,
			WillProcessDate,
			RTRIM(ISNULL(Email,'')),
			SUBSTRING(RTRIM(ISNULL(First,'')),1,32),
			SUBSTRING(RTRIM(ISNULL(Last,'')),1,32),
			ISNULL( AcctErrorNum, 0 ), 
			ISNULL( ShareErrorNum, 0 ), 
			ISNULL( ShareErrorDesc, ''),
			ISNULL( CloseDate, '' )
		FROM Payment P
			INNER JOIN User_Payee U ON P.PayeeListID = U.PayeeListID
			INNER JOIN Payee PA ON U.PayeeId = PA.PayeeID
			INNER JOIN HostMemberAcctInfo H ON H.MemberID = P.MemberID and H.AccountID = Account
		WHERE WillProcessDate <= @CurrentDate and StatusCode = 101 and H.RunID = @RunID
			ORDER BY Amount DESC
END

select * from @tmpPayments order by RowID

SELECT @NumPayments = ISNULL(COUNT(PaymentID),0) FROM @tmpPayments
SET @Message = 'Processing : ' + Convert(varchar, @Numpayments) + ' One-Time Payments'
EXEC spLog @Message, @SPName, @RunID	
EXEC BPG_UpdateGeneratorStatus @RunID, @RunStatus, @Message

--select * from @tmppayments
--  Start processing payments one at a time
NextPayment:

SET @Desc = ''
SET @ErrMsg = ''
SET @dupPaymentID = ''

--  Check for duplicate payments
SELECT TOP 1 @PaymentID = PaymentID,
	@PayeeID = RTRIM(PayeeID), 
	@PayeeListID = RTRIM(PayeeListID),
	@PayeeName = RTRIM(PayeeName),
	@MemberID = RTRIM(MemberID), 
	@Account = RTRIM(Account),
	@TrsfSource = RTRIM(TrsfSource), 
	@Amount = Amount, 
	@StatusCode = StatusCode,
	@WillProcess = WillProcess,
	@MemberEmail = Email,
	@MemberFirst = FirstName,
	@MemberLast = LastName,
	@AcctErr = AccountErrNum,
	@ShareErr = ShareErrorNum,
	@ShareDesc = ShareErrorDesc,
	@CloseDate = ClosedDate
FROM @TmpPayments ORDER BY RowID 

--  If no rows returned, then we are done processing payments
IF @@ROWCOUNT = 0
	GOTO Done

SELECT @dupPaymentID = ISNULL(PaymentID ,'')
	FROM @TmpPayments 
	WHERE MemberID = @MemberID and PayeeListID = @PayeeListID and 
		PaymentID != @PaymentID and Amount = @Amount

--  Check for a valid email address here.  If blank, report the blank email and move on
IF @MemberEmail = '' or @MemberEmail is NULL
	SET @ErrMsg = 'Member email address is blank' + @CRLF

IF @dupPaymentID != ''
BEGIN   --  This is a duplicate payment, record, move to history and delete the duplicate
	SET @DuplicateAmount = @DuplicateAmount + @Amount
	SET @DuplicateCount = @DuplicateCount + 1
	SET @StatusCode = 1035
	SET @Message = 'Duplicates found for Member: '+@MemberID + ', PayeeListID: '+
		@PayeeListID+', Amount: $' +CONVERT(varchar(20), 
		CONVERT(decimal(14,2), ISNULL(@Amount/100., 0)))+@crlf
	SET @Message = @Message + 'Removing PaymentID : '+@dupPaymentID
	EXEC spLog @Message, @SPName, @RunID
	EXEC @rc = BPG_InsertHistory @RunID, @dupPaymentID, @StatusCode
	IF @rc != 0
	BEGIN
		SET @Message = 'Error moving duplicate payment [ '+@dupPaymentID+'] to history'
		EXEC spLog  @Message, @SPName, @RunID
--  Need to build the insert history string and log, so it would be easy to move it back for processing
--  Update the statuscode of the payment in the payment table, so that the payment does not get processed again	
--		DELETE FROM Payment WHERE PaymentID = @dupPaymentID
		UPDATE Payment SET StatusCode = 300 WHERE PaymentID = @dupPaymentID
		DELETE FROM @TmpPayments WHERE PaymentID = @dupPaymentID
	END
	ELSE
	BEGIN
--  Remove payment from the temp payment table so that is doesnt get processed again
		DELETE FROM @TmpPayments WHERE PaymentID = @dupPaymentID
		DELETE FROM Payment WHERE PaymentID = @dupPaymentID
	END

--  Check to see if email is valid, if not send to BPSupport else Send email to the member telling them of the duplicate payment
	EXEC @rc = spSendMail @StatusCode, @MemberEmail, @dupPaymentID
	
END
	
--  Check for host errors here.  If there are some, log errors.

	IF @AcctErr = 0 and @ShareErr = 0 and @CloseDate = ''
		SET @StatusCode = 100
	
	IF @ShareErr = 1 and @ShareDesc like 'Symconnect%Off%host'
	BEGIN
		SET @ErrMsg = @ErrMsg + ' Symconnect is off Host, Payment has not been processed'
		GOTO PaymentError
	END
		
	IF @AcctErr <= 0 and @ShareErr != 0
	BEGIN
		SET @StatusCode = 1007  --  Set status code to Account not found
		SELECT @Desc = Description FROM Status_Code WHERE StatusCode = @StatusCode
		SET @HostError =  'Failed, status : ' + Convert(varchar, @StatusCode) + @CRLF +  'Error: ' + @DESC
		SET @ErrMsg = @ErrMsg + @Desc
	END

	IF @AcctErr < 0 and @ShareErr = 0
	BEGIN
		SET @StatusCode = 202
		SELECT @Desc = Description FROM Status_Code WHERE StatusCode = @StatusCode
		SET @HostError =  'Failed, status : ' + Convert(varchar, @StatusCode) + @CRLF +  'Error: ' + @DESC
		SET @ErrMsg = @ErrMsg + @Desc
	END


	IF @CloseDate != ''
	BEGIN
		SET @StatusCode = 305
		SELECT @DESC = Description FROM Status_Code WHERE StatusCode = @StatusCode
		SET @HostError = 'Failed, Status : ' + Convert(varchar, @StatusCode) + @CRLF + 'Error: ' + @Desc
		SET @ErrMsg = @ErrMsg + @Desc
	END
			
--  Process the payment by calling the BP command
	IF @StatusCode = 100
	BEGIN
--  Need to figure out what the shareid is
--		SET @Suffix = SUBSTRING(@Account, charindex('S',@Account) + 1, @SuffixLength )
		SET @PAmount = @Amount/100.

		EXEC BPG_ExecHostBillPayCmd @MemberID, @Account, @TrsfSource, @Pamount, @PayeeName, @PaymentID, @StatusCode OUTPUT, @DESC OUTPUT
--  What is the statuscodes that are returned?
		IF @StatusCode != 0
		BEGIN	
			SET @Message = 'Paymentid : ' + @paymentid + ' : StatusCode from BP Command = ' 
				+ Convert(varchar, @StatusCode) + ' ' + @DESC
			EXEC spLog @Message, @SpName, @RunID
			SET @HostError = 'Error : ' + Convert(varchar, @StatusCode) + ':' + @DESC
			SET @ErrMsg = @ErrMsg + @DESC
			IF @StatusCode = 13999
			BEGIN
				IF @DESC = 'Account Batch Warning'
					SET @StatusCode = 310
				IF @DESC = 'Reg-D Limit'
					SET @StatusCode = 306
			END
			SELECT @Statuscode = ISNULL(StatusCode,1007) FROM Status_Code WHERE HostCode = @StatusCode
			IF @@RowCount = 0
				SET @StatusCode = 1007
		END
		ELSE
			SET @StatusCode = 101
	END

-- Move Payment to History
	EXEC @rc = BPG_InsertHistory @RunID, @PaymentID, @StatusCode
	IF @rc != 0
	BEGIN
		SET @StatusCode = 300
		UPDATE Payment SET StatusCode = 300 WHERE PaymentID = @PaymentID
		SET @Message = 'Error inserting payment '+ @PaymentID +' into history,  ' 
		EXEC spLog  @Message, @SPName, @RunID
	END
	ELSE
	BEGIN
		IF @StatusCode = 101  --  Successful
		BEGIN
			IF @LargePaymentEmail > 0 and @Amount >= (@LargePaymentEmail * 100)
				EXEC spSendMail 3001, @MemberEmail, @PaymentID	--  Send Large payment amount email to members
			SET @Success = @Success + 1
			SET @SAmount = @Samount + @Amount
			SET @Message= --CONVERT(varchar, GETDATE()) + 
				'Payment ID  ' + ISNULL(@PaymentID, 'No Payment ID')
				+ ' WillProcess: ' + Convert(varchar(10), @WillProcess, 110) + @CRLF 
				+ ISNULL(@MemberFirst,'FIRST') + ' ' + ISNULL(@MemberLast,'LAST') + ' (' 
				+ ISNULL(@MemberID,'') +  ') Pay: $' 
				+ LTRIM(CONVERT(varchar(20), CONVERT(money, ISNULL(@Amount/100., 0),1))) 
				+ ' ' + ISNULL(RTRIM(@PayeeName),'No Payee Name') + ' (' 
				+ @PayeeID + ') successful'
			EXEC spLog @Message, @SPName, @RunID
		END
		ELSE  -- Failed 
		BEGIN
			SET @Failed = @Failed + 1
			SET @FailedAmount = @FailedAmount + @Amount
			SET @Message= --CONVERT(varchar, @Now) + 
				'Payment ID  ' + ISNULL(@PaymentID, 'No Payment ID') + ' WillProcess: ' 
				+ Convert(varchar(10), ISNULL(@WillProcess,''), 101) + @CRLF 
				+ ISNULL(@MemberFirst,'FIRST') + ' ' + ISNULL(@MemberLast,'LAST') 
				+ ' (' + ISNULL(@MemberID,'') +  ') Pay: $' 
				+ LTRIM(CONVERT(varchar(20), CONVERT(decimal(14,2), ISNULL(@Amount/100., 0)))) + ' ' 
				+ ISNULL(RTRIM(@PayeeName),'No Payee Name') + ' (' 
				+ @PayeeID + ') ' + @HostError
			EXEC spLog @Message, @SPName, @RunID

			IF @StatusCode = 200  --  NSF, if courtesy pay flag, don't send email here
			BEGIN
				IF @CourtesyPay = 0
					EXEC spSendMail @StatusCode, @MemberEmail, @PaymentID
			END
			ELSE
			BEGIN
				EXEC spSendMail @StatusCode, @MemberEmail, @PaymentID
			END
		END	
	END
	
	IF @StatusCode not in (101,102)
		EXEC @rc = BPG_InsertErrorRecap @RunID, @PayeeListID, @PaymentID, @MemberID, @StatusCode, @ErrMsg

--  Check if Courtesy Pay flag is on, then insert record into Courtesy Pay table if we need to process courtesy pay items.
	IF @CourtesyPay = 1
	BEGIN
		INSERT INTO COURTESY_PAY VALUES ( @PaymentID, @MemberID, @MemberEmail, @StatusCode, @RunID, @CurrentDate )
		IF @@ERROR != 0
		BEGIN
			SET @Message = 'Error inserting data into the Courtesy_Pay table for paymentid : ' + @PaymentID + ' email address = 

' + @MemberEmail
			EXEC spLog @Message, @SPName
		END
	END

PaymentError:
--  Get Next Payment
	DELETE FROM @TmpPayments WHERE PaymentID = @PaymentID
	GOTO NextPayment

Done:
--  Need to write error summary here to a table so we can send the log files

EXEC BPG_UpdateGeneratorStatus @RunID, @RunStatus, NULL

-- Send PaymentSummaryEmail
EXEC @rc = spSendPaymentSummaryMessage @failed, @FailedAmount, @DuplicateCount, @DuplicateAmount,  @Success, @SAmount, @RunID, 101

RETURN 0

END

