/* =====================================================================================================
 Author:	Helen Liou
 Create date: 	10/14/2010
 Description:	This store procedure will post payments to the members loan account that were debited during the billpay run
 
  ------------------------------------------------------------------
  Modifications: 

 ===================================================================================================== */

CREATE PROCEDURE [dbo].[BPG_ProcessSharePayments] 
	@CUID	VARCHAR(9),
	@RunID	Int	
AS
BEGIN

SET NOCOUNT ON;

DECLARE @rc					int,
		@amount				int,
		@AccountIDLength	int,
		@OnUsGLCode			int,
		@StatusCode			int,
		@ShareIDStartPos	int,
		@errnum				int

DECLARE @SPName				Varchar(255),
		@PaymentID			Varchar(10),
		@UsersAccount		varchar(40),
		@MemberID			varchar(22),
		@MemberAccount		varchar(22),
		@AccountID			varchar(4),
		@OnUsPostEnabled	char(1),
		@OnUsComment		varchar(255),
		@Account			varchar(22),
		@AccountPrefix		varchar(22),
		@errdesc			varchar(132),
		@Message			varchar(255)
		

DECLARE @Now	Datetime

SET @SPName = Convert(varchar, OBJECT_NAME(@@PROCID))
SET @Now = GETDATE()
SET @statuscode = 102

--  Check if Loan Posting is turned on and GL exists
SELECT @AccountIDLength = ISNULL(ConfigValue, 2) FROM Configuration WHERE ConfigName = 'AccountSuffixLength'
SELECT @OnUsGLCode = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'OnUsGLCode'
SELECT @OnUsComment = ISNULL(ConfigValue,'BillPay OnUs Share Posting') FROM Configuration WHERE ConfigName = 'OnUsComment'

--  Get the list of payments that need to be posted
DECLARE @tmploantable TABLE (PaymentID varchar(10), MemberID varchar(22), Amount int, UsersAccount varchar(40) )

INSERT INTO @tmploantable
	SELECT	PaymentID, 
			MemberID,
			Amount, 
			REPLACE(UsersAccountAtPayee, ' ', '' ) as UsersAccountAtPayee 
	FROM Payment_History PH
	WHERE ProviderID = 101 and Statuscode = 101 and RunID = @RunID and UsersAccountatPayee like '%S%'

NextPayment:

SELECT	TOP 1
		@PaymentID = PaymentID, 
		@MemberID = MemberID,
		@Amount = Amount,
		@UsersAccount = UsersAccount
FROM @tmploantable

IF @@ROWCOUNT = 0
	GOTO Done

--  Save original account number so we can report an error and the information passed in 
SET @Account = REPLACE(REPLACE(@UsersAccount, ' ', ''),'-','')
--  Get Account number from UsersAccountatPayee and parse, if it doesnt match the format, generate an exception
SET @ShareIDStartPos = CHARINDEX('S',@Account)

IF @ShareIDStartPos > 0
BEGIN
--  Check if this is an account number
	IF ISNUMERIC(REPLACE(@Account,'S','')) = 0  --  not a valid onus format, fail the payment and process as check
		SET @StatusCode = 203
	ELSE
		SET @StatusCode = 102
	
	IF @ShareIDStartPos = 1
		SET @MemberAccount = @MemberID
	ELSE
		SET @MemberAccount = SUBSTRING(@Account, 1, charindex('S',@Account)-1)

	IF @StatusCode = 102  -- Valid Account Format of all numerics
	BEGIN    --   Check if the MemberID is there, if not assign it to the members accountid.
	
		SET @AccountID = SUBSTRING(@Account, charindex('S',@Account), @AccountIDLength+1 )
	
		IF LEN(@AccountID) -1 < @AccountIDLength
			SET @AccountID = 'S' + dbo.padAccount(SUBSTRING(@AccountID, 2,LEN(@AccountID)-1),@AccountIDLength)
		ELSE
			SET @AccountID = @AccountID + REPLICATE('?',@AccountIDLength - (Len(@AccountID)-1))	
	END
END

SET @Message = 'Payment was not posted successfully, please post manually : ' + @PaymentID
If charindex('?', @AccountID) = 0 and @StatusCode = 102 
--  Post the Payment, record and set any rejections accordingly
	EXEC @rc = BPG_ProcessOnUSPayment @PaymentID, @MemberId, @MemberAccount, @Amount, @AccountID, @RunID, 'BPGen', @OnUsGLCode, @OnUsComment, 'N',0
ELSE 
BEGIN
	UPDATE PAYMENT_HISTORY SET StatusCode = 203, FailedDate = @Now WHERE PaymentID = @PaymentID
	EXEC spLog @Message, @SPName, @RunID

END

DELETE FROM @tmploantable WHERE PaymentID = @PaymentID
GOTO NextPayment

Done:
	Return 0

spError:
	Return 1

END









