/* =================================================================================================
 Author:	Helen Liou
 Create date: 	8/23/2006
 Description:	Starts the payment generation process.  This SP is called by the 
				job that starts the generation process.  It checks to see if today is a holiday, and
				if we process on the day of the holiday or before.  First calculate all recurring
				payments that are coming due and generate one time payments for payments due and
				updates the nextprocessdate and sends email if the recurring payment model has ended.
				After the recurring payment process is complete, process payments due, get member
				and account information per member and process the billpay transaction for each 
				payment.  Finally, update the tracking record if turned on to update the BP activity
				dates in the HB tracking record (which is customizable).  Send a summary email to 
				the bp support group for them to follow up with any errors in each run.

 Parameters:	CUID		The routing id of the CU, currently not used
				EffectiveDate	This is the date that run is for, always in the past, 
						can not run payments in the future.


 ------------------------------------------------------------------
Modifications:
		12/27/2008	HLiou	Added NSF Fee processing
		10/1/2011	HLiou	Added OnUs Loan autoposting
		12/18/2013	HLiou	Added OnUs Share autoposting 
====================================================================================================== */
CREATE PROCEDURE [dbo].[BPG_Run]
@CUID varchar(9),
@EffectiveDate datetime = NULL
AS

SET NOCOUNT ON

--  Declare Integers type variables
DECLARE @Count INT, 
		@LastRunID int, 
		@rc int, 
		@RunID int, 
		@RunStatus tinyint,
		@CheckHoliday int,
		@CourtesyPay int,
		@AutoPostOnUs char(1)

--Declare Varchar type variables
DECLARE @Message varchar(255), 
		@SPName varchar(50), 
		@AdminAddress varchar(255),
		@CUName varchar(30),
		@PaymentModel varchar(15),
		@FeeGL varchar(5),
		@NSFEnabled varchar(1)

--  Declare Datetime variables
DECLARE 	@Now datetime

SET @SPName = Convert(varchar, OBJECT_NAME(@@PROCID))

SET @Now = GETDATE()
--  If the effective date is null, then assume that the run is for today
IF @EffectiveDate IS NULL
	SET @EffectiveDate = @Now

IF (Convert(varchar(8),@EffectiveDate,112) != Convert(varchar(8),@Now,112))
BEGIN
	SET @Message = 'Run is for a different day than the current day, Date entered is : ' + Convert(varchar, @EffectiveDate)
	EXEC spLog @Message, @SPName, @RunID
END

--  Check to see how to process holidays, if model is Sun - Thurs (skipholiday = 1), Mon -  Fri (skipholiday = 0)
SELECT @CheckHoliday = ConfigValue FROM Configuration WHERE ConfigName = 'SkipHoliday' --and CUID = @CUID
SELECT @CUName = ConfigValue FROM Configuration WHERE ConfigName  = 'CreditUnionName' --and CUID = @CUID
SELECT @PaymentModel = ISNULL(ConfigValue,'Process') FROM Configuration WHERE ConfigName = 'PaymentModel'
SELECT @AutoPostOnUs = ISNULL(ConfigValue,'N' ) FROM Configuration WHERE ConfigName = 'OnUsPostingEnabled'
SELECT @LastRunID = CurrentID FROM ID WHERE Type = 'RunID'
SELECT @FeeGL = ISNULL(ConfigValue,' ') FROM Configuration WHERE ConfigName = 'NSFFeeGLCode'
SELECT @NSFEnabled = ISNULL(ConfigValue,'N') FROM Configuration WHERE ConfigName = 'NSFFeeEnabled'
SELECT @CourtesyPay = ISNULL(ConfigValue, 0) FROM Configuration WHERE ConfigName = 'SpecialCourtesyPay'

--  Check Last RunID and status.  If Run is complete, then start next run, otherwise log the error and return
SELECT @RunStatus = ISNULL(State, 0) FROM RunLog WHERE ID = @LastRunID
IF @RunStatus = 0
BEGIN
	SET @Message = 'Last run has not completed, this run will not process'
	EXEC spLog @Message, @SPName
	RETURN -1
END

IF @CheckHoliday = 1
BEGIN
	IF EXISTS (SELECT [Date] FROM Holidays WHERE Convert(varchar(8),DateAdd(dd,-1,Date), 112) = Convert(Varchar(8), GETDATE(), 112))
	BEGIN
		EXEC spLog 'The generator will not run tonight as tomorrow is a holiday', @SPName
		RETURN -1
	END
END
ELSE
BEGIN
	IF EXISTS (SELECT [Date] FROM Holidays WHERE Convert(varchar(8),Date, 112) = Convert(Varchar(8), GETDATE(), 112))
	BEGIN
		EXEC spLog 'The generator will not run tonight as today is a holiday', @SPName
		RETURN -1
	END
END

-- Get directories from processor table
SET @Message = 'EmailSysOpAddress was not found in the Configuration table'
SELECT  @AdminAddress = ConfigValue 
	FROM Configuration WHERE ConfigName = 'EmailSysOpAddress' --and CUID = @CUID
IF @@ROWCOUNT <> 1 
	GOTO spError

-- *************Need to update this to support multiple CUs  ***********************
--  Get a RunID and update the Runlog table with the current run infomation
EXEC @rc = GetID 'RunID', @RunID OUTPUT
IF @rc != 0
BEGIN
	-- Log error and end run
	SET @Message = 'ERROR: Unable to generate RunID for CU :' + ISNULL(@CUID,'')
	EXEC spLog @Message, @SPName, @RunID
	RETURN 1
END
UPDATE RunLog SET State = 0 WHERE ID = @RunID

SET @Message = '============ Beginning BillPay Payment Generation Run ' +Convert(varchar, @RunID) + ' ============ '
EXEC spLog  @Message, @SPName, @RunID

--  Clean out the temp tables
--TRUNCATE TABLE HostMemberAcctInfo

-- Process recurring payments, if process model is "process", use will processdate.  If model is "Due", calculate NextDue Date Model
IF @PaymentModel = 'Due'
BEGIN
	EXEC @rc = BPG_GenerateRecurringPaymentWithDueDate @CUID, @RunID, @EffectiveDate
	IF @rc != 0
	BEGIN
		UPDATE RunLog SET State = 1 WHERE ID = @RunID
		RETURN 2
	END
END
ELSE
BEGIN
	EXEC @rc = BPG_GenerateRecurringPayment @CUID, @RunID, @EffectiveDate
	IF @rc != 0
	BEGIN
		UPDATE RunLog SET State = 1 WHERE ID = @RunID
		RETURN 2
	END
END

EXEC BPG_UpdateGeneratorStatus @RunID, 5

--  Update status generator status table to show we are processing Host commands
EXEC BPG_InsertGeneratorStatus @RunID, 1

--TRUNCATE TABLE HostMemberAcctInfo
SET @Message = '============= Obtaining Member and Account Information from Host ==============='
EXEC spLog @Message, @SPName, @RunID
--  Get Member and account information from the host    
EXEC BPG_GetMemberHostInfoForBPG @EffectiveDate, @RunID, @rc output
IF @rc > 0
BEGIN
--
	SET @Message = '=========== Member and Account Information Completed ============='
	exec spLog @Message, @SPName, @RunID

--  Update the status for run
	EXEC BPG_UpdateGeneratorStatus @RunID, 1

	UPDATE RunLog SET State = 1 WHERE ID = @RunID
	SET @Message= '============= Begin Processing One-Time Payment ============'
	EXEC spLog @Message, @SPName, @RunID
-- Process payments
	EXEC @rc = BPG_ProcessPayments @CUID, @RunID, @EffectiveDate
	IF @rc != 0
		RETURN 3

	IF @FeeGL != ' ' and @NSFEnabled = 'Y' and @CourtesyPay = 0
	BEGIN
		EXEC BPG_InsertGeneratorStatus @RunID, 15, 'Start Processing NSF Fees'
		EXEC @rc = BPG_ProcessNSFFees @CUID, @RunID, @FeeGL
		IF @rc != 0
			RETURN 7
		EXEC BPG_UpdateGeneratorStatus @RunID, 15, 'NSF Fee Processing Complete'
	END

--  Process On Us Loans
	IF @AutoPostOnUs = 'Y'
	BEGIN
		EXEC BPG_InsertGeneratorStatus @RunID, 16, 'Posting OnUs Payments Starting'
--  Attempt to post the share first, take all the non-share ones and attempt to post via a loan account
		EXEC @rc = BPG_ProcessSharePayments @CUID, @RunID
		If @rc != 0
		BEGIN
			SET @Message = 'Error occurred during On Us Share Posting, please check logs for more information'
			EXEC spLog @Message, @SPName, @RunID
		END
		EXEC @rc = BPG_ProcessLoanPayments @CUID, @RunID
		If @rc != 0
		BEGIN
			SET @Message = 'Error occurred during On Us Loan Posting, please check logs for more information'
			EXEC spLog @Message, @SPName, @RunID
		END

		EXEC BPG_UpdateGeneratorStatus @RunID, 16, 'Posting OnUs Payments Completed'

	END

	IF ( SELECT count(paymentid) --  Only create payment files if there are payments send
		FROM Payment_History WHERE RunID = @RunID ) > 0
	BEGIN
		EXEC BPG_InsertGeneratorStatus @RunID, 8, 'Creating Payment Files'
		EXEC @rc = BPG_CreateAllPaymentFiles @CUID, @RunID
		IF @rc != 0
			RETURN 5
		EXEC BPG_UpdateGeneratorStatus @RunID, 8, 'Payment Files Creation Completed'
	END

	SET @Message = '============ Completed Bill Pay run ' + Convert(varchar,@RunID) + '============ '
	EXEC spLog @Message, @SPName, @RunID
	RETURN 0
END
ELSE
BEGIN
	SET @Message = '============= Run ' + Convert(varchar, @RunID) + ' failed, could not get member or account information successfully, please check logs for more information  ==============='
	UPDATE RunLog SET State = 1 WHERE ID = @RunID
	EXEC spLog @Message, @SPName, @RunID
	EXEC spSendMail 99,@AdminAddress, '', @Message
	RETURN 6
END

spError:
	IF len(@Message) > 0
		EXEC spLog @Message, @SPName
	UPDATE RunLog SET State = 1 WHERE ID = @RunID
	RETURN 10














