CREATE PROCEDURE [dbo].[BPG_UpdatePayee] 
@PaymentMethod  	char(1), 
@PayeeListID 		varchar(12)
AS

SET NOCOUNT ON

DECLARE @Message 			varchar(255),
		@rc					int,
		@oldPaymentMethod	char(1)

SET @Message = 'Could not find user payee'
SELECT  @oldPaymentMethod = PaymentMethod  
	FROM USER_PAYEE  
	WHERE PayeeListID =  @PayeeListID

IF @@ROWCOUNT = 0
	GOTO spError

IF @oldPaymentMethod != @PaymentMethod 
	BEGIN
		SET @Message = 'Problems updating the Payment method'
		UPDATE User_Payee 
			SET	PaymentMethod = @PaymentMethod
		WHERE 
			User_Payee.PayeeListID = @PayeeListID
	END  
				
IF @@ERROR != 0
	GOTO spError

SET @Message = ' '
RETURN 0

spError:
	EXEC spLog @Message,'BPG_UpdateUserPayee'
	RETURN 1



GO
