-- =============================================
CREATE PROCEDURE [dbo].[BPG_UpdateTrackingInfo] 
@CUID varchar(9)
AS

BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	DECLARE @rc				int,
			@StatusCode		int

	DECLARE @MemberID	varchar(22),
			@Message	varchar(255),
			@SPName		varchar(255),
			@ErrMsg		varchar(500)

	DECLARE @LastUpdate	datetime
	DECLARE @EffectiveDate datetime
	
	SET @SPName = 'BPG_UpdateTrackingInfo'
	SET @EffectiveDate = GETDATE()
--	EXEC BPG_InsertGeneratorStatus @RunID, 4

--  We do not want to update the tracking record if effective date is in the future.
	SET @Message = 'No tracking records to update, Effective date is in the past: ' + Convert(varchar,@EffectiveDate)
	IF Convert(varchar, @EffectiveDate, 112) < Convert(varchar, GETDATE(), 112)
	BEGIN
		EXEC spLog @Message, @SPName
		GOTO Done
	END

	SET @Message = 'Starting to processing tracking record updates'
--  Get members who have BP activity to update in the tracking record.
	DECLARE @tmpMemberID TABLE ( MemberID varchar(22), LastUpdate datetime )
	INSERT INTO @tmpMemberID
		EXEC wc_BP_DailyActiveAccounts 

NextMember: 

	SELECT TOP 1 @MemberID = RTRIM(MemberID),
				 @LastUpdate = LastUpdate
		FROM @tmpMemberID

	IF @@RowCount = 0    --  Nothing to do so end
		GOTO Done
		
--	EXEC BPG_UpdateHostTrackingDate @MemberID, @EffectiveDate, @statuscode OUTPUT, @errmsg OUTPUT
	EXEC BPG_UpdateHostTrackingDate @MemberID, @LastUpdate, @statuscode OUTPUT, @errmsg OUTPUT
	IF @StatusCode != 0
	BEGIN
		SET @Message = 'Error updating tracking record for MemberID : ' + @MemberID + ' Error = ' + @errmsg
		EXEC spLog @Message, @SPName
	END

	DELETE FROM @tmpMemberID 
		WHERE MemberID = @MemberID
	
	GOTO NextMember

Done:
	SET @Message = 'Tracking Record Update Completed'
	EXEC spLog @Message, @SPName
	RETURN 0

END


GO
