CREATE PROCEDURE [dbo].[FindPayees]
@strSearch          varchar(40)='',	-- required to have strSearch or iMinNumMembers
@iScope             int=0,		-- or scope=GLOBAL.
@iSearchType        int=1,
@iOrderby           int=1,
@iMinNumMembers     int=0,
@bFilterAliased     bit=0,
@strLastUpdate      varchar(32)=''
/* strSearch: The search criteria. Will be set to what the user is looking for.      */
/*   What it is compared against depends on the search type.                         */
/*                                                                                   */
/* iScope: ALL=0, USER=1 or GLOBAL=2                                                 */
/*                                                                                   */
/* iSearchType: tells SP what type of search it is to be:                            */
/*   1 = payee id search (default - no like searches allowed)                        */
/*   2 = payee name search                                                           */
/*   3 = phone number search                                                         */
/*   4 = memberid search (no like searches allowed)                                  */
/*                                                                                   */
/* iOrderBy:  indicates the several different coded order by choices.                */
/*   1 = Payee ID (default)                                                          */
/*   2 = Name                                                                        */
/*   3 = Number of Members                                                           */
/*   4 = Last Update Date Time                                                       */
/*                                                                                   */
/* iMinNumMembers: Set to minimum number of members set up for this payee.           */
/*   defaults to 0 - or no minimum                                                   */
/*                                                                                   */
/* strLastUpdate: Last Update datetime passed as a string. Results will be filtered  */
/* to only include those payees updated after the specified datetime.                */
/* Note that no explicit conversion to datetime is done so the query will fail if a  */
/* datetime is specified which SQL Server does not understand. It is expected that   */
/* the client code will only pass valid datetimes.                                   */
/*                                                                                   */
/* Notes: the query results are put into temporary tables to simplify sorting and to */
/* improve performance. Some of the criteria are specified twice - both in the       */
/* initial table #p1 and in the final query. This is to speed the queries when no    */
/* strSearch is specified. Initially this query was coded in one statement but was   */
/* found to have poor performance even when index hints were given.                  */
/*                                                                                   */
/* An alternative to this method would be to build a dynamic SQL string, but this    */
/* method would lack the benefits of statement compilation. It would, however, be    */
/* much cleaner in it's coding.                                                      */
AS
create table #p1 ( pid char(12) )
if @strSearch > ""
	if @iSearchType = 1  -- payee id search
		insert into #p1 values (@strSearch)	-- we already know which payee we are looking
							-- for.
	else if @iSearchType = 2  -- payee name search
		insert into #p1 select e.payeeid as pid from payee e where e.name like @strSearch 
	else if @iSearchType = 3   -- Phone Number Search
		insert into #p1 select e.payeeid as pid from payee e where e.phone like @strSearch 
	else if @iSearchType = 4   -- Member Id Search
		insert into #p1 select e.payeeid as pid from payee e
			inner join user_payee u on e.payeeid=u.payeeid and u.MemberId = @strSearch 
	else goto badparams	-- need searchtype between 1 and 4
else if @iMinNumMembers > 0	-- if passed without a strSearch
	insert into #p1 select payeeId as pid from user_payee group by payeeid
		having count(payeelistid) > @iMinNumMembers
else if @iScope = 2	-- just GLOBAL payees if passed without a strSearch
	insert into #p1 select payeeid as pid from payee where scope = 'GLOBAL'
else if @strLastUpdate > ''
	insert into #p1 select payeeid as pid from payee
		where LastUpdatedOn > @strLastUpdate
else goto badparams	-- we must have strSearch, MinNumMembers, 
			-- a LastUpdate date or GLOBAL scope to run search.
select e.payeeid,e.name,e.PayeeAliasId,e2.name as AliasName,
	count(u.payeelistid) as NumMembers,
	e.address1,e.address2,
	e.address3, e.city, e.state, e.PostalCode,e.country,e.phone,
	e.scope,e.LastUpdatedOn,e.LastUpdatedBy 
into #p2 from payee e 
	inner join #p1 on e.payeeid=#p1.pid
	left join payee e2 on e.PayeeAliasId=e2.PayeeId
	left join user_payee u on e.payeeid=u.payeeid
where e.LastUpdatedOn > @strLastUpdate
	and ( @iScope=0 or (@iScope=1 and e.scope = 'USER') or (@iScope=2 and e.scope='GLOBAL'))
	and (@bFilterAliased=0 or e.PayeeAliasId='')
group by e.payeeid,e.name,e.PayeeAliasId,e2.name,
	e.address1,e.address2,
	e.address3, e.city, e.state, e.PostalCode,e.country,e.phone,
	e.scope,e.LastUpdatedOn,e.LastUpdatedBy 
having ( @iMinNumMembers=0 or count(u.payeelistid) >= @iMinNumMembers ) 
if (@@ERROR <> 0) GOTO on_error
	
if @iOrderby = 1 
   select * from #p2 order by payeeid
else if @iOrderby = 2
   select * from #p2 order by name, payeeid
else if @iOrderby = 3
   select * from #p2 order by NumMembers desc, name asc
else if @iOrderby = 4
   select * from #p2 order by LastUpdatedOn, name
else
   select * from #p2
if (@@ERROR <> 0) GOTO on_error
RETURN (0)
on_error:
RETURN (-1)
badparams:
print 'Insufficient or bad parameter(s) for query'
return (-100)
GO
