CREATE PROCEDURE [dbo].[GetBillPayCounts]

	@GetCountDate		smalldatetime,
	@BillPayActiveUsers	int OUTPUT,
	@BillPayPaymentCount	int OUTPUT,
	@BillPayPaymentTotal	int OUTPUT

 AS


SET NOCOUNT ON

	-- Get Active BillPayer Count

	DECLARE @ActiveInstitution	int
	DECLARE @CountInstitution	int
	DECLARE @GetCountEndDate	smalldatetime
	DECLARE @GetCountBeginDate smalldatetime

	SET	@GetCountEndDate =  @GetCountDate +1
	SET	@GetCountBeginDate =  @GetCountDate -30

	--EXEC 	@ActiveInstitution = wc_BP_ActiveUsers2 @GetCountDate, @GetCountEndDate,  @CountInstitution

	EXEC 	@ActiveInstitution = wc_BP_ActiveUsers2 @GetCountBeginDate, @GetCountDate,  @CountInstitution

	SET	@BillPayActiveUsers = @ActiveInstitution
	

	-- Get BillPay Count info

	DECLARE @CUTotAmount int
	DECLARE @CUTotCount int
	
	SELECT 	@CUTotAmount=Sum(H.Amount/100), @CUTotCount=Count(*) 
	FROM 		Payment_History H join RunLog R on H.RunID = R.ID 
	WHERE	((statuscode=1036) 
			or ( H.FailedDate is null 
			and H.CancelledDate is null)) 
			and R.DateProcessed >= @GetCountDate
			and  R.DateProcessed < @GetCountEndDate	
	
	SET @BillPayPaymentCount = @CUTotCount
	SET @BillPayPaymentTotal = @CUTotAmount


SET NOCOUNT OFF
GO
